-- ----------------------------
--  Table structure for `curio_pic_content`
-- ----------------------------
DROP TABLE IF EXISTS `curio_pic_content`;
CREATE TABLE `curio_pic_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `title_en` varchar(255) DEFAULT NULL COMMENT '名称英文',
  `title_tc` varchar(255) DEFAULT NULL COMMENT '名称繁体',
  `clazz_id` int(11) DEFAULT NULL COMMENT '分类id',
  `pic` varchar(1024) DEFAULT NULL COMMENT '图片json',
  `num` varchar(255) DEFAULT NULL COMMENT '标号',
  `prize_en` varchar(255) DEFAULT NULL COMMENT '价格英文',
  `prize_tc` varchar(255) DEFAULT NULL COMMENT '价格繁体',
  `size_en` varchar(255) DEFAULT NULL COMMENT '尺寸英文',
  `size_tc` varchar(255) DEFAULT NULL COMMENT '尺寸繁体',
  `standard_en` varchar(512) DEFAULT NULL COMMENT '规格英文',
  `standard_tc` varchar(512) DEFAULT NULL COMMENT '规格中文',
  `descript_en` longtext COMMENT '描述英文',
  `descript_tc` longtext COMMENT '描述繁体',
  `sort` int(8) DEFAULT '0' COMMENT '排序。数值越大，越靠前',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  `pdf` varchar(255) DEFAULT NULL COMMENT 'pdf',
  `spec` varchar(255) DEFAULT NULL COMMENT 'spec',
  `daihao` varchar(255) DEFAULT NULL COMMENT 'daihao',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3502 DEFAULT CHARSET=utf8 COMMENT='图录内容表';

-- ----------------------------
--  Records of `curio_pic_content`
-- ----------------------------
BEGIN;
INSERT INTO `curio_pic_content` VALUES ('1', 'DRIED TANGERINE PEEL AGED OVER 30 YEARS', '約三十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_501_1.jpg,/public/pic_content/img/a1/LOTNO_501_2.jpg', '501', '8,000 - 15,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：兩袋<br />\n重量：每袋各重約629克 <br />\n      共重約兩斤<br />\n<br />\n此拍品為陳化30多年的新會老陳皮，由於後期曬制工藝越來越成熟，此陳皮水分蒸發透徹，皮身乾脆，加之倉儲良好，品相上乘。', '100', null, null, null, null, null), ('2', 'DRIED TANGERINE PEEL AGED OVER 40 YEARS', '約四十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_502_1.jpg,/public/pic_content/img/a1/LOTNO_502_2.jpg', '502', '20,000 - 38,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：四袋<br />\n重量：每袋各重約297克、295克、297克、298克<br />\n      共重約兩斤<br />\n<br />\n超過40年的沉澱，充分的陳化，此老陳皮形狀完整，顏色深湛，油室較密集，外富紋路細坑，質素上好。', '100', null, null, null, null, null), ('3', 'DRIED TANGERINE PEEL AGED OVER 50 YEARS', '約五十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_503_1.jpg,/public/pic_content/img/a1/LOTNO_503_2.jpg', '503', '40,000 - 68,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：三袋<br />\n重量：每袋各重約628克、629克、627克<br />\n      共重約三斤<br />\n<br />\n約50年的新會老陳皮，陳化時間較久，水分蒸發較為透徹，皮身乾脆，外表呈現棕黑色澤。年代久遠，加之曬制功夫較足，此老陳皮散發著一股乾燥、陳年的陳皮香味，隔著外包裝都能聞到。', '100', null, null, null, null, null), ('4', 'DRIED TANGERINE PEEL AGED OVER 50 YEARS', '約五十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_504_1.jpg,/public/pic_content/img/a1/LOTNO_504_2.jpg,/public/pic_content/img/a1/LOTNO_504_3.jpg', '504', '150,000 - 280,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：十袋<br />\n重量：共重約十斤<br />\n<br />\n此拍品為陳化約50年的新會老陳皮，外皮顏色深邃，內裡油室密集，輕輕搖晃袋子會有細屑粉末飛出，足見其倉儲乾燥。香氣十足，有陣陣藥香味。年代久遠，分量有足十斤之多，質、量雙佳，值得納入囊中。', '100', null, null, null, null, null), ('5', 'DRIED TANGERINE PEEL AGED OVER 45 YEARS', '約四十五年陳齡 新會 大紅陳皮', '12', '/public/pic_content/img/a1/LOTNO_505_1.jpg,/public/pic_content/img/a1/LOTNO_505_2.jpg,/public/pic_content/img/a1/LOTNO_505_3.jpg', '505', '15,000 - 28,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n重量：淨重約600克(約一斤)<br />\n<br />\n此拍品為採摘於農曆小雪至小寒時節之新會四十五年陳皮(大紅皮)，出自油皮上品的茶枝柑，皮厚扁大，表皮面均勻分佈大小凹入油室，以「正三刀」開皮技藝，三瓣相連，片張分明。經自然陳曬，皮面呈棕黑紅色。<br />\n    陳皮內囊風化自然剝落，內表紋理清，呈陳舊棕黑黃色，分佈均勻有光澤。沖泡後水澤呈自然棕紅色，入口陳香味濃郁，香、甜、甘、醇，久煮不爛且無苦澀等異味，歷經歲月，陳香醇舊。', '100', null, null, null, null, null), ('6', 'DRIED TANGERINE PEEL AGED AROUND 60 YEARS', '約六十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_506_1.jpg', '506', '15,000 - 28,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：一袋<br />\n重量：約574克(約九両四)<br />\n<br />\n近60年的新會老陳皮，陳化時間足久，乾曬透徹，皮身乾脆，外皮棕黑，內皮油室密集，紋路明顯，質素上好。年代久遠，加之曬制功夫較足，倉儲良好，此老陳皮散發著一股乾燥、微苦的藥香味，輕輕一敲，還可聽到清脆響聲，乾燥的粉末也會隨之飄落，此乃上好陳皮的一大特色。', '100', null, null, null, null, null), ('7', 'DRIED TANGERINE PEEL AGED AROUND 60 YEARS', '約六十年陳齡 新會 老陳皮', '12', '/public/pic_content/img/a1/LOTNO_507_1.jpg,/public/pic_content/img/a1/LOTNO_507_2.jpg', '507', '46,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：新會<br />\n數量：三袋<br />\n重量：每袋各重約626克、632克、624克<br />\n      共重約三斤<br />\n<br />\n陳化近60年，此老陳皮顏色飽和深湛，香氣隔袋撲鼻，陣陣藥香味飄出。蘊涵數十年正火的老陳皮，泡水入饌，口感絕佳，還是保健、治病的絕佳良品，有祛痰濕、平喘咳的功效。', '100', null, null, null, null, null), ('8', 'NO.2 WILD AMERICAN GINSENG TABLET', '美國野山二號花旗泡蔘', '12', '/public/pic_content/img/a1/LOTNO_508_1.jpg,/public/pic_content/img/a1/LOTNO_508_2.jpg', '508', '50,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：美國俄亥俄州紅番區<br />\n數量：一盒<br />\n重量：淨重約600克<br />\n<br />\n此蔘產自美國俄亥俄州紅番區，該區所產的均為野生蔘，約有20年天然生長期，蔘體保存完好，條紋清晰，品相較佳。<br />\n花旗蔘有提神活血提神、清熱、降糖、強心的效果，可用來作為中醫治療及日常保健用的食品。', '100', null, null, null, null, null), ('9', 'NO.2 WILD AMERICAN GINSENG', '美國野生二號花旗蔘', '12', '/public/pic_content/img/a1/LOTNO_509_1.jpg', '509', '80,000 - 120,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：美國紐約北省<br />\n數量：兩盒<br />\n重量：每盒淨重約500克  / 每條約4克<br />\n            共淨重約1000克<br />\n<br />\n花旗蔘，味甘、性涼。補氣滋陰，清熱生津。用於治療氣虛陰虧、內熱、咳喘痰血、虛熱煩倦、消渴、口燥咽乾等問題上。並有助改善心肌功能、鎮靜、降血糖、增強免疫力、增強體質，藥用保健功效一流。', '100', null, null, null, null, null), ('10', 'NO.1 WILD AMERICAN GINSENG TABLET', '美國野山一號花旗泡蔘', '12', '/public/pic_content/img/a1/LOTNO_510_1.jpg,/public/pic_content/img/a1/LOTNO_510_2.jpg', '510', '68,000 - 100,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：美國俄亥俄州紅番區<br />\n數量：一盒<br />\n重量：淨重約600克<br />\n<br />\n此蔘產自美國俄亥俄州紅番區，該區所產的均為野生蔘，約有30年天然生長期，蔘體較肥大，條紋清晰，蔘鬚保持完整，品相較佳，而「一號」更是人蔘業界中最頂尖的指標。<br />\n花旗蔘是人參的一種。美國舊稱為花旗國，花旗蔘由此得名，又稱洋蔘、西洋蔘、野山泡蔘。原產於美國北部到加拿南部一帶，通常照產地分成花旗蔘與加拿大蔘。兩者雖然同種，但因為氣候影響，前者的蔘面橫紋比後者更明顯，療效最佳。', '100', null, null, null, null, null), ('11', 'EXTRA LARGE WILD AMERICAN GINSENG', '美國野生特大花旗蔘', '12', '/public/pic_content/img/a1/LOTNO_511_1.jpg', '511', '50,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：美國紐約北省<br />\n數量：一盒<br />\n重量：淨重約500克 / 每條約10克<br />\n<br />\n野生花旗蔘是受美國特殊稀有物種法律保護，因此進出口及買賣均受到嚴格管制。但其藥用功效和價值絕佳，所以市場需求一直居高不下。物以稀為貴，此種品相、色澤、紋路等質素均屬上乘，且還為特大號的花旗蔘，實在難能一見。', '100', null, null, null, null, null), ('12', 'NO.1 WILD AMERICAN GINSENG', '美國野生一號花旗蔘', '12', '/public/pic_content/img/a1/LOTNO_512_1.jpg,/public/pic_content/img/a1/LOTNO_512_2.jpg', '512', '88,000 - 150,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：美國紐約北省<br />\n數量：兩盒<br />\n重量：每盒淨重約500克 / 每條約5克<br />\n      共重約1000克<br />\n<br />\n野生花旗蔘藏在深山，天然根深入地裡，故蘆頭細長而多節，蔘鬚很長，有細珠粒，蔘身橫紋密而深色；野生蔘以大粒、蘆頭蔘鬚完整為貴。一號花旗蔘屬人蔘業界中級數最頂尖的指標，且此拍品形狀完整，表相良好，則更為珍貴。', '100', null, null, null, null, null), ('13', 'WILD PANAX GINSENG (TOP GRADE)', '一等生曬野生蔘', '12', '/public/pic_content/img/a1/LOTNO_513_2.jpg,/public/pic_content/img/a1/LOTNO_513_4.jpg,/public/pic_content/img/a1/LOTNO_513_3.jpg,/public/pic_content/img/a1/LOTNO_513_1.jpg', '513', '220,000 - 380,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '鑒定機構：國家蔘茸產品質量監督檢驗中心<br />\n證書編號：201721512<br />\n產地：長白山<br />\n重量：淨重約41克<br />\n<br />\n根據《本草綱目》記載，人蔘是上等的滋補藥材，功效包括補五臟、安神、生津、延年益壽、加速新陳代謝等，甚至被稱爲「救命藥」，有助增強體力、免疫力及大補元氣。<br />\n人蔘中以中國長白山及吉林野山人蔘最為著名，非人工種植，生長過程全天然、無添加。每次收成後，曾經種植人蔘的土壤都需要數年的修養期，且挖掘難度巨大，所以長白山人蔘在市場上向來千金難求。<br />\n此拍品為經過國家蔘茸產品質量監督檢驗中心依據《野山蔘鑒定及分等質量》標準認證的「一等生曬野山蔘」。其可貴之處在於嵾體肥大，露出地面的露頭有三個，且均已肉化，正是依靠這三個露頭吸收了充足的養分才可以滋養出如此肥大的蔘體。更重要的是，每條蔘鬚須都十分長且保存得較完整，可見在挖掘此蔘的時候花費的心思，蔘鬚須之長，至於在一米多的錦盒內還無法完全伸長拉直，足見此蔘非同一般。', '100', null, null, null, null, null), ('14', '1983 CKJ KOREAN RED GINSENG ROOTS (HEAVEN GRADE 15)', '正官庄 1983年產 天字 高麗蔘15支(一斤裝)', '12', '/public/pic_content/img/a1/LOTNO_514_2.jpg,/public/pic_content/img/a1/LOTNO_514_3.jpg,/public/pic_content/img/a1/LOTNO_514_1.jpg', '514', '40,000 - 70,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：韓國<br />\n數量：一罐<br />\n重量：淨重約600克<br />\n<br />\n此產品乃頂級的天字15支高麗蔘。15支裝的人蔘體粗壯，而天字號品質更屬於首一品級，為蔘中之王。1983年入罐，陳年30多年，如此年代久遠而保存良好的正官庄高麗蔘，實屬難能可見。', '100', null, null, null, null, null), ('15', '1989 CKJ KOREAN RED GINSENG ROOTS (HEAVEN GRADE 10)', '正官庄 1989年產 天字 高麗蔘10支(一斤裝)', '12', '/public/pic_content/img/a1/LOTNO_515_1.jpg,/public/pic_content/img/a1/LOTNO_515_2.jpg,/public/pic_content/img/a1/LOTNO_515_3.jpg,/public/pic_content/img/a1/LOTNO_515_4.jpg', '515', '50,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：韓國<br />\n數量：一罐<br />\n重量：淨重約600克<br />\n<br />\n正官庄天字10支高麗蔘，嵾體粗壯，每支蔘有近60克。天字號品質上乘，為蔘中之王。1989年入罐，陳年將近30年，年代久遠，市面上少之又少。', '100', null, null, null, null, null), ('16', '1999 CKJ KOREAN RED GINSENG ROOTS (HEAVEN GRADE 30)', '正官庄 1999年產 天字六年根 高麗蔘30支（半斤裝）', '12', '/public/pic_content/img/a1/LOTNO_516_1.jpg', '516', '80,000 - 120,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：韓國<br />\n數量：八罐 <br />\n重量：每罐淨重約300克<br />\n            共淨重約2400克<br />\n<br />\n此天字號六年根高麗蔘，品質上乘，產自1999年，入罐後有接近二十年的陳齡，陳期使罐內人蔘濁氣消除，正氣越純，補而不燥，有生津安神、補氣養身的功效。', '100', null, null, null, null, null), ('17', 'KORYO INSAM (HEAVEN GRADE 15)', '北韓 高麗蔘 天字 高麗蔘15支(一斤裝)', '12', '/public/pic_content/img/a1/LOTNO_517_1.jpg,/public/pic_content/img/a1/LOTNO_517_2.jpg', '517', '40,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：北韓<br />\n數量：三罐<br />\n重量：每罐各淨重約600克<br />\n            共重約1800克<br />\n<br />\n此拍品為天字號蔘，等級最高，且收藏了約30年，年代久遠，市面上較為少見。<br />\n高麗蔘在韓國經過有計劃的栽培，細心的挑選，均添加了附子、乾薑等熱性中藥炮製，所以性質偏於燥熱，適用於四肢冰冷，怕冷等寒性體質者。', '100', null, null, null, null, null), ('18', 'KORYO INSAM (HEAVEN GRADE 10)', '北韓 高麗蔘 天字 高麗蔘10支(一斤裝)', '12', '/public/pic_content/img/a1/LOTNO_518_1.jpg,/public/pic_content/img/a1/LOTNO_518_2.jpg', '518', '100,000 - 150,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：北韓<br />\n數量：兩罐<br />\n重量：每罐淨重約600克<br />\n            共淨重約1200克<br />\n<br />\n「高麗蔘」是指在朝鮮半島產的人蔘，以政治體區分為北韓蔘(北朝鮮蔘)、南韓蔘。真正的北韓蔘不多見，奇貨可居，所以市面的售價并不低。<br />\n此拍品為天字號蔘，等級最高，一罐只有十支蔘，蔘體較為粗壯，且收藏了約30年，年代久遠，市面上較為少見。', '100', null, null, null, null, null), ('19', 'TIBET NAGQU CORDYCEPS SINENSIS', '2017年 西藏那曲 野生冬蟲夏草', '12', '/public/pic_content/img/a1/LOTNO_519_1.jpg,/public/pic_content/img/a1/LOTNO_519_2.jpg', '519', '120,000 - 160,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：西藏那曲<br />\n重量：約618克(約一斤)<br />\n條數：約2000條(1條約有0.3克)<br />\n<br />\n此拍品蟲身較肥，身色金黃，內裡呈奶白色。味香氣足，極乾，色、香、味之品質皆屬上乘。2017年挖採，十分新鮮，且之後均經過金屬探測器檢測，不存在人為注水或插金屬條絲入蟲身以增加重量，為純天然野生上品。', '100', null, null, null, null, null), ('20', 'TIBET NAGQU CORDYCEPS SINENSIS', '2017年 西藏那曲 野生冬蟲夏草', '12', '/public/pic_content/img/a1/LOTNO_520_1.jpg,/public/pic_content/img/a1/LOTNO_520_2.jpg,/public/pic_content/img/a1/LOTNO_520_3.jpg', '520', '120,000 - 160,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：西藏那曲<br />\n重量：淨重約500克(約十三両二)<br />\n條數：約1100條(1條約有0.45克)<br />\n<br />\n此拍品為西藏那曲野生冬蟲夏草，2017年採摘，十分新鮮。蟲身較肥，身色深黃，有淡淡的腥香味。 <br />\n由於冬蟲夏草具有動植物兼具的神奇特徵，讓其含有多種微量元素，目前科學上公認的四大神奇元素是蟲草素、蟲草酸、蟲草多醣、超氧化岐化本酶，具有抗腫瘤，保護呼吸系統及肝腎，提高免疫力，降血糖、血壓、血脂等顯著作用。', '100', null, null, null, null, null), ('21', 'QINGHAI YUSHU CORDYCEPS SINENSIS', '2017年 青海玉樹 野生冬蟲夏草', '12', '/public/pic_content/img/a1/LOTNO_521_1.jpg,/public/pic_content/img/a1/LOTNO_521_2.jpg', '521', '50,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '<br />\n產地：青海玉樹<br />\n數量：兩袋<br />\n重量：共淨重約500克(約十三両二)<br />\n條數：約1500條(1條約0.33克)<br />\n<br />\n冬蟲夏草外形可分為「蟲」與「草」兩部份：<br />\n「蟲」體似蠶，表面深棕色，有環紋二十至三十個，足八對，中間四對最明顯。蟲體質脆易斷，帶有微腥氣，微香味； <br />\n「草」為子座，從蟲體的頭部長出，表面黑褐色，有細縱紋，質輕柔韌。<br />\n冬蟲夏草主要以蟲體豐滿肥壯，外皮色黃，內肉色白，乾身完整者為上品。此拍品2017年挖採，蟲身較乾，紋路清晰，色澤黃亮，質素較佳。', '100', null, null, null, null, null), ('22', 'CUBILOSE FROM HOI AN VIETNAM', '越南 會安 燕窩', '12', '/public/pic_content/img/a1/LOTNO_522_1.jpg', '522', '28,000 - 40,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：越南<br />\n數量：一盒<br />\n重量：淨重約600克(約一斤)<br />\n<br />\n全世界燕窩80%都來自印尼產區，其他由馬來西亞、泰國及越南出產。而越南燕窩只佔了全世界產量的1%，每年產量只有3000公斤，量少難尋。即使越南燕窩價格高於印尼產區很多，依然供不應求。<br />\n會安的環境氣候最適合燕子生長，周邊有豐富的紅樹林可供覓食，這裡的燕子大多屬體型大的金絲燕，產出的燕窩巢厚而內有網絲，落水後膨脹力極強，燕窩也最大片，一直是燕窩中的頂級品。', '100', null, null, null, null, null), ('23', 'COLLOCALIA CUBILOSE FROM THAILAND', '泰國 金絲燕窩', '12', '/public/pic_content/img/a1/LOTNO_523_1.jpg', '523', '18,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：泰國<br />\n數量：一盒<br />\n重量：約600克(約一斤)<br />\n<br />\n燕窩的主要產地分佈於東南亞地區，主要在印尼、泰國、越南、馬來西亞一帶。泰國燕窩產量較少，價格也就相對較高。此款金絲燕窩，產自泰國，外形完整，寬度大，顏色天然無漂白跡象，紋理清晰，縫隙自然，品相尚佳。<br />\n市面上能吃的燕窩，是指一種雨燕科金絲燕所建造的窩。金絲燕會分泌出呈凝膠狀細長幼絲，再融合絨羽等物質混合凝結做成巢穴，這就是&quot;燕窩&quot;。<br />\n金絲燕平均一年築三次窩，築窩費時45天，從築窩到小燕子離開燕窩，大概前後需110天。當燕子飛走後，才能進行採集工作，所以一年最多也只能採集三次燕窩，量少珍貴。', '100', null, null, null, null, null), ('24', 'COLLOCALIA CUBILOSE FROM INDONESIA', '印尼 金絲燕窩', '12', '/public/pic_content/img/a1/LOTNO_524_1.jpg', '524', '9,000 - 15,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '<br />\n產地：印尼<br />\n 數量：一盒<br />\n重量：淨重約600克(約一斤)<br />\n<br />\n此款燕窩產自印尼，形狀完整，紋理清晰，底座輕薄，盞身通透，2017年採集，較為新鮮。<br />\n燕窩以外形完整、寬度大為佳。較天然的燕窩的形狀為三角形狀，而經洗燕過程後較不成型的燕窩則有呈燕條、燕角、燕餅或燕碎等。燕窩顏色則是以天然不經漂白且無羽毛及泥沙等雜質的為佳品。<br />\n印尼為全世界產燕窩最多的地方，當地行家採集燕窩的經驗豐富，盡可能保持了燕窩的完整自然，出產的燕窩大多質量較佳。', '100', null, null, null, null, null), ('25', 'JAPANESE KANTA SEA CUCUMBER', '日本關東遼參', '12', '/public/pic_content/img/a1/LOTNO_525_1.jpg,/public/pic_content/img/a1/LOTNO_525_2.jpg', '525', '40,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：日本關東<br />\n數量：六盒<br />\n重量：每盒各重約824克 / 25-26條<br />\n            共重約4900克<br />\n<br />\n此拍品為六盒關東遼參，每盒約有25-26條參，海參具有補腎養血、潤膚養顏等功效與作用。<br />\n日本遼參一直以關東所產的為首選，價錢也最貴。關東遼參，主產於日本東北及關東地區，因氣候、水質等地理因素，所出產的遼參質素較佳。關東遼參體型不大，長3-5寸，參體渾圓，偏肥厚，肉質細糯，口感脆爽，外皮多呈黯黑色，色澤光亮鮮明，長滿密集尖銳肉刺，通常約有6排，排列整齊帶序。', '100', null, null, null, null, null), ('26', 'DRIED AMIDORI ABALONE (3pcs / 605g)', '三頭 溏心 乾網鮑', '12', '/public/pic_content/img/a1/LOTNO_526_1.jpg,/public/pic_content/img/a1/LOTNO_526_2.jpg', '526', '40,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：三隻<br />\r\n重量：每隻各重約197克、198克、210克<br />\r\n            共重約一斤<br />\r\n<br />\r\n此拍品為溏心大網鮑，收藏了約15年，而三頭鮑魚的生長期需要超過二十年，所以該三頭鮑年代實為久遠，在市面上較為罕見。加上其形狀完整對稱，鮑身飽滿，群邊密集完整，實為收藏佳品。', '100', null, null, null, null, null), ('27', 'DRIED AMIDORI ABALONE (5pcs / 605g)', '五頭 乾網鮑', '12', '/public/pic_content/img/a1/LOTNO_527_1.jpg,/public/pic_content/img/a1/LOTNO_527_2.jpg,/public/pic_content/img/a1/LOTNO_527_3.jpg', '527', '65,000 - 90,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：十六隻<br />\r\n重量：共重約1852克(約三斤)<br />\r\n<br />\r\n此16隻五頭鮑魚為藏家精挑細選出來的精品，每隻個個鮑身厚實飽滿，形狀對稱。肉厚肥美的鮑魚富含多種營養元素，益腎滋補，是四季皆宜的養生補品。', '100', null, null, null, null, null), ('28', 'DRIED JAPANESE AMIDORI ABALONE (4pcs / 605g)', '四頭 日本 大網鮑', '12', '/public/pic_content/img/a1/LOTNO_528_2.jpg,/public/pic_content/img/a1/LOTNO_528_1.jpg', '528', '40,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地： 日本<br />\r\n數量：六隻<br />\r\n重量：共重約600克(約一斤)<br />\r\n<br />\r\n此四頭網鮑產自日本，是香港海味店多年珍藏之物，保存得宜，外形完整無劃痕，品相良好。<br />\r\n日本出品的乾鮑是世界公認第一，鮑魚體型較大，鮑身肉厚而堅硬，裙邊闊而粗糙，吸盆尾部較尖，枕底呈清晰珠粒之狀，呈深棕色，香味濃郁。具有滋補養顏、平衡血壓、養肝明目、滋陰清熱等作用，並且含豐富蛋白質，尤以滋陰明目、滋補強身功效甚大，適合肝腎虛弱、視物昏暗等人士食用。', '100', null, null, null, null, null), ('29', 'DRIED AMIDORI ABALONE (NEARLY 2pcs / 605g)', '近二頭 溏心 大網鮑', '12', '/public/pic_content/img/a1/LOTNO_529_1.jpg,/public/pic_content/img/a1/LOTNO_529_2.jpg', '529', '80,000 - 120,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：兩隻<br />\r\n重量：每隻各重約249克、250克<br />\r\n            共重約 0.8斤<br />\r\n<br />\r\n此溏心大網鮑，體型巨大，接近成年人手掌大小。顏色深邃，裙邊珠粒粗壯分明，鮑枕肥厚，品相良好。近年來二頭鮑魚已十分罕見，此拍品收藏約15年，形狀仍十分完整，打開盒子還能聞到濃郁乾鮑鮮味。', '100', null, null, null, null, null), ('30', 'DRIED JAPANESE AOMORI-KEN AMIDORI ABALONE (NEARLY 3pcs / 605g)', '近三頭 日本 大網鮑', '12', '/public/pic_content/img/a1/LOTNO_530_1.jpg,/public/pic_content/img/a1/LOTNO_530_2.jpg,/public/pic_content/img/a1/LOTNO_530_3.jpg', '530', '40,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：日本青森縣<br />\r\n數量：四隻<br />\r\n重量：每隻各重約181、158、158、165 克                                    共重約一斤一両<br />\r\n<br />\r\n此拍品出產於日本青森縣，是鮑中頂級絕品，外形呈橢圓狀呈深咖啡色，鮑身肥厚，鮑邊細小，鮑枕呈珠粒狀，底邊廣闊平坦，尾部較尖，肉質大而肥厚。由世界鮑魚名師之一，天王師博花谷的秘方曬製。曬製方法是用平放於網中曬製。市面上近三頭鮑魚已經很少，如此品相良好的更是不可多見。', '100', null, null, null, null, null), ('31', 'DRIED  AMIDORI ABALONE (NEARLY 3pcs / 605g)', '近三頭 乾網鮑', '12', '/public/pic_content/img/a1/LOTNO_531_1.jpg,/public/pic_content/img/a1/LOTNO_531_2.jpg', '531', '60,000 - 100,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：六隻<br />\r\n重量：共重約1088克(約一斤十二両)<br />\r\n<br />\r\n由於三頭鮑魚的生長期需要超過20年，現在市面上較為罕見。此拍品共6隻近三頭鮑魚，均形狀完整對稱，鮑身飽滿，鮑肉厚實。外表顏色深邃，群邊密集完整，實為佳品。', '100', null, null, null, null, null), ('32', 'DRIED AMIDORI ABALONE (8pcs / 605g)', '八頭 溏心 乾網鮑', '12', '/public/pic_content/img/a1/LOTNO_532_1.jpg,/public/pic_content/img/a1/LOTNO_532_2.jpg', '532', '35,000 - 50,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：十六隻<br />\r\n重量：共重約1200克(約兩斤)<br />\r\n<br />\r\n此16隻八頭鮑魚為藏家精挑細選出來的精品，收藏已經超過15年，年代久遠。肉厚肥美的溏心網鮑蘊藏大量維他命和礦物質，益腎滋補，是四季皆宜的養生補品。', '100', null, null, null, null, null), ('33', 'DRIED AMIDORI ABALONE (10pcs / 605g)', '十頭 溏心 乾網鮑', '12', '/public/pic_content/img/a1/LOTNO_533_1.jpg,/public/pic_content/img/a1/LOTNO_533_2.jpg', '533', '60,000 - 90,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：四十隻<br />\r\n重量：共重約2400克(約四斤)<br />\r\n<br />\r\n此拍品是香港某行家收藏了約15年的存貨，陳期十足。四盒鮑魚個個鮑身厚實飽滿，形狀對稱，色澤深邃，裙邊珠粒分明，且一組共有四十隻，分量十足。', '100', null, null, null, null, null), ('34', 'EXTRAL LARGE PAKISTAN MALE POLLOCK MAW', '約十五年陳齡 巴基斯坦 鰵魚公肚', '12', '/public/pic_content/img/a1/LOTNO_534_1.jpg', '534', '15,000 - 25,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：巴基斯坦<br />\n數量：一隻<br />\n重量：約241克(約六両四)<br />\n<br />\n除中國東、南海外，印度洋城市也是花膠盛產之地，其中屬印度和巴基斯坦出產的花膠排名一二。此拍品就產自巴基斯坦。<br />\n花膠有公母之分，花膠公營養價值及口感優於花膠母，而鰵魚公魚鰾曬成的花膠屬頂級貨。公肚形如馬鞍，肚身中部較厚，身呈V字條紋,以質地結實、厚身、乾淡，呈半透明狀為上品。此拍品為鰵魚公肚，收藏近15年，陳化足久，保存得宜，質素佳。', '100', null, null, null, null, null), ('35', 'PAKISTAN POLLOCK MAW', '巴基斯坦 鰵魚肚', '12', '/public/pic_content/img/a1/LOTNO_535_1.jpg,/public/pic_content/img/a1/LOTNO_535_2.jpg', '535', '100,000 - 150,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：巴基斯坦<br />\n數量：兩隻<br />\n重量：每隻各重約360克(約九両五)、423克(約十一両二)<br />\n      共重約一斤四両六<br />\n<br />\n除中國東、南海盛產花膠外，巴基斯坦出產的花膠也享譽盛名。此拍品為兩隻巴基斯坦出產的鰵魚魚肚，個頭偏大，色澤光亮，顏色呈淡黃，重量十足。所謂“十斤魚一両膠”，可見要幾十斤的魚才有這麼大的魚肚，而且鰵魚大部分是生活在深水中，不能養殖，現今又極難捕撈這麼大的鰵魚，可以說像此拍品這樣優質的鰵魚肚越來越難得了。', '100', null, null, null, null, null), ('36', 'SOUTHERN CHINA SEA POLLOCK MAW', '南中國海 鰵魚膠', '12', '/public/pic_content/img/a1/LOTNO_536_1.jpg,/public/pic_content/img/a1/LOTNO_536_2.jpg,/public/pic_content/img/a1/LOTNO_536_3.jpg', '536', '80,000 - 140,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：南中國海<br />\n數量：八隻<br />\n重量：共重約845克(約一斤六両)<br />\n<br />\n鰵魚膠的功效備受推崇，開始供不應求，其身價年年倍升,加上越是陳年越見矜貴，收藏價值極高。鰵魚膠含豐富蛋白質、膠質、磷質及鈣質，根據清朝＜本草網目＞記載:「味甘、氣溫、入腎經。專補精陰，更能生子。」此外，鰵魚膠對糖尿病、白內障、哮喘、骨質疏松、皮膚熱毒及癌症有治療功效。', '100', null, null, null, null, null), ('37', 'MALAYSIAN FISH MAW', '馬來西亞 蜘蛛膠', '12', '/public/pic_content/img/a1/LOTNO_537_1.jpg,/public/pic_content/img/a1/LOTNO_537_2.jpg,/public/pic_content/img/a1/LOTNO_537_3.jpg,/public/pic_content/img/a1/LOTNO_537_4.jpg', '537', '100,000 - 150,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：馬來西亞<br />\n數量：兩隻<br />\n重量：每隻各重約108克(約二両九、73克(約一両九))                         共重約四両八<br />\n<br />\n蜘蛛膠（小鱗波曼石首魚），由於市面上相當的稀少，因此價格不菲。蜘蛛膠對於治療支氣管炎、咳嗽、哮喘等效果顯著，有滋陰補腎、養胃潤肺、益精健脾、補血理氣等功效，同時也是美容養顏、強身健體之佳選。<br />\n此拍品產自馬來西亞，為香港海味店珍藏之物。蜘蛛膠在香港名氣非常高，價格一直呈上升趨勢。蜘蛛膠有公母之分，香港人在選擇蜘蛛膠時特別喜歡肚子中間有條「筋」的蜘蛛膠公膠，口感頗受歡迎。', '100', null, null, null, null, null), ('38', 'FISH MAW', '上等花膠', '12', '/public/pic_content/img/a1/LOTNO_538_1.jpg,/public/pic_content/img/a1/LOTNO_538_2.jpg,/public/pic_content/img/a1/LOTNO_538_3.jpg,/public/pic_content/img/a1/LOTNO_538_4.jpg,/public/pic_content/img/a1/LOTNO_538_5.jpg', '538', '20,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：三隻<br />\n重量：每隻各重約536克(約十四両二)、419克(約十一両)、450克(約十一両九)<br />\n            共重約兩斤五両<br />\n<br />\n3隻花膠經過年月的陳化，由金黃色澤慢慢長成深褐色，可見已有一定的年歲。於燈照下，透出的光色較深，說明膠身厚實，膠原蛋白充足。此外，味道不帶魚腥之外還透出陣陣香氣，頗宜入饌。', '100', null, null, null, null, null), ('39', 'PREMINUM FISH MAW AGED OVER 50-YEARS', '約五十年陳齡 極品花膠', '12', '/public/pic_content/img/a1/LOTNO_539_2.jpg,/public/pic_content/img/a1/LOTNO_539_1.jpg', '539', '50,000 - 80,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：巴西<br />\n數量：一隻<br />\n重量：約1,077克(約一斤十二両)<br />\n<br />\n經過50年左右的陳化，此花膠表面呈現濃茶色，紋路較深，有樹皮般粗糙的紋理，這也是識別陳年花膠的一大特點。且此上土花膠，形狀完整，膠身較大較厚，原生魚體積必然需要足夠大，才能提取出這種龐大的魚鰾，故此膠乃市面稀有的佳品。', '100', null, null, null, null, null), ('40', 'BENGAL MALE POLLOCK MAW', '孟加拉 黃鰵公魚肚', '12', '/public/pic_content/img/a1/LOTNO_540_1.jpg,/public/pic_content/img/a1/LOTNO_540_2.jpg,/public/pic_content/img/a1/LOTNO_540_3.jpg', '540', '150,000 - 220,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '<br />\n產地：孟加拉<br />\n數量：兩隻<br />\n重量：每隻各重約330克(約八両七)、337克(約八両九)<br />\n      共重約一斤一両六<br />\n<br />\n此鰵魚公肚形狀完好，頭偏大，膠身呈現金黃色，帶點淡海水味道，在燈光下晶瑩通透，實為上品。<br />\n上品花膠多以鰵魚魚肚製成，產自印度洋水域，又以孟加拉及巴基斯坦的最出色。花膠含豐富蛋白質及膠質，滋陰養顏，補腎益精。花膠分公乸，花膠乸較圓身，花膠公呈馬鞍形，花膠公肉質富彈性，厚身，發頭極好。', '100', null, null, null, null, null), ('41', 'PAKISTAN  POLLOCK MAW', '巴基斯坦 鰵魚肚', '12', '/public/pic_content/img/a1/LOTNO_541_1.jpg,/public/pic_content/img/a1/LOTNO_541_2.jpg', '541', '120,000 - 160,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '<br />\n產地：巴基斯坦<br />\n數量：兩隻    <br />\n重量：每隻各重約302克(約八両)、389克(約十両三)                                   共重約一斤二両<br />\n<br />\n此拍品為鰵魚魚鰾曬成的花膠，產自巴基斯坦，為優質花膠盛產之地。其形如馬鞍，中部較厚，顏色呈深褐色，兩邊較薄，顏色呈金黃色。兩隻花膠膠身乾燥，表面帶油潤感，呈半透明狀，聞著香而不腥，且重量較足，價格適宜。', '100', null, null, null, null, null), ('42', 'EXTRAL LARGE PAKISTAN MALE POLLOCK MAW', '約十五年陳齡 巴基斯坦 鰵魚公肚', '12', '/public/pic_content/img/a1/LOTNO_542_1.jpg', '542', '18,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：巴基斯坦<br />\n數量：一隻<br />\n重量(連袋)：約286克(約七両六)<br />\n<br />\n此拍品為鰵魚公魚鰾曬成的頂級花膠，產自巴基斯坦，為優質花膠盛產之地。收藏近15年，陳化足久，保存得較完整。形如馬鞍，中部較厚。膠身乾燥，呈半透明狀，聞著香而不腥，重量較足。', '100', null, null, null, null, null), ('43', 'SOUTHERN CHINA SEA POLLOCK MAW', '南中國海 鰵魚膠', '12', '/public/pic_content/img/a1/LOTNO_543_1.jpg,/public/pic_content/img/a1/LOTNO_543_2.jpg', '543', '160,000 - 220,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：南中國海<br />\n數量：兩隻<br />\n重量：每隻各重約244克（約六両五）、217克（約五両七）<br />\n            共重約十二兩二<br />\n<br />\n此拍品為南中國海產鰵魚膠一對，膠身厚實，呈深黃褐色，燈光下通透可見清晰紋路，保存良好，乾燥完整。<br />\n鰵魚體形龐大，肚頭數大，膠質特別豐富。鰵魚膠的膠質細膩，肚身爽口，燉後湯水清，無腥味，乃花膠中之上上品，對改善皮膚組織健康和加速傷口之康復有顯著功效。', '100', null, null, null, null, null), ('44', 'FISH MAW', '上等花膠', '12', '/public/pic_content/img/a1/LOTNO_544_3.jpg,/public/pic_content/img/a1/LOTNO_544_1.jpg,/public/pic_content/img/a1/LOTNO_544_2.jpg', '544', '38,000 - 60,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '數量：三隻<br />\n重量：每隻各重約544克(約十四両四)、546克(約十四両四)、536克(約十四両二)<br />\n      共重約兩斤十一両<br />\n<br />\n3只花膠大小相近，形狀完整，呈金黃色。膠身於燈照下晶瑩通透，紋理清晰，壁膜厚實，膠質充裕。', '100', null, null, null, null, null), ('45', 'WILD INDONESIA FISH MAW', '印尼野生白花膠', '12', '/public/pic_content/img/a1/LOTNO_545_1.jpg,/public/pic_content/img/a1/LOTNO_545_2.jpg', '545', '20,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地： 印尼<br />\n數量：十八隻<br />\n重量：約793克(約一斤五両)<br />\n<br />\n此拍品產自印尼，每個約45克，均為野生白花膠。<br />\n白花膠，是取自名貴的小型魚種「黃姑魚」的魚鰾，素稱「花膠之冠」。功效、口感一流，具有固本培元、補血活血、滋補肝腎等功效，其養顏抗皺、美容的效果非常好，一直都是香港人養顏美容的聖品。白花膠在對胃炎、支氣管炎等病症上也有不錯的療效。<br />\n白花膠個頭偏小，一般一個重幾克至幾十克不等。主要產自泰國、印尼、孟加拉等地，野生白花魚膠市面上現在十分罕見。', '100', null, null, null, null, null), ('46', 'PREMIUM THAILAND DRIED SEAHORSES', '極品海馬', '12', '/public/pic_content/img/a1/LOTNO_546_2.jpg,/public/pic_content/img/a1/LOTNO_546_1.jpg', '546', '20,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：泰國<br />\n重量：約600克(約一斤)<br />\n<br />\n在《本草拾遺》中，海馬又名水馬、蝦姑、馬頭魚、龍落子魚，為海龍科動物。<br />\n中醫學認為海馬味甘、鹹，性溫。歸腎經、肝經，功效則包括補腎壯陽，散結消腫。<br />\n此拍品產自泰國，近年由於環境污染破壞問題，泰國出口海馬數量逐年減少，當地政府更於于2015年頒布命令暫停發放海馬出口許可證，所以海馬在市面上越來越少。而擁有如像此拍品這樣體型較大、形狀完整、外表保存良好的質素，更是屈指可數，乃出品人珍藏多年之物。', '100', null, null, null, null, null), ('47', 'PREMINUM AUSTRALIAN DRIED SEA DRAGONS', '超級海龍', '12', '/public/pic_content/img/a1/LOTNO_547_2.jpg,/public/pic_content/img/a1/LOTNO_547_1.jpg', '547', '18,000 - 30,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：澳洲 <br />\n重量：約600克(約一斤)<br />\n<br />\n此拍品產自澳洲，為頂級海龍，每隻約重25克，每年挑選出這種重量級數的才一百多斤，十分罕見珍貴，在市面上非常少見，多數落入藏家手中。<br />\n海龍是一個很有名的藥材，也叫水雁或是錢串子，這個藥材在生活中被廣泛的運用，因為它所含的物質成分，有著非常好的功效，其提取物對人體有抗癌、提高機體免疫力、性激素作用及抗衰老等。《現代實用中草》中對海龍的記載：“為強壯藥，有興奮作用，用於老人及衰弱者之神經衰憊。治血氣通。”', '100', null, null, null, null, null), ('48', 'R', '約六十年代 人頭馬 路易十三 干邑 白蘭地', '12', '/public/pic_content/img/a1/LOTNO_548_1.jpg', '548', '40,000 - 68,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：法國<br />\n年份：約六十年代<br />\n數量：三瓶<br />\n容量：700毫升(ABV 40%)<br />\n<br />\n此3瓶路易十三乃60年代左右出品。瓶身、瓶塞及瓶底有人手雕刻之產品編號，瓶頸封口以純金雕刻而成。均配備原廠專屬收藏硬盒、巴卡拉水晶瓶塞及產品編號說明書。', '100', null, null, null, null, null), ('49', 'RICHARD HENNESSY COGNAC', '軒尼詩 李察 干邑 白蘭地', '12', '/public/pic_content/img/a1/LOTNO_549_1.jpg,/public/pic_content/img/a1/LOTNO_549_2.jpg,/public/pic_content/img/a1/LOTNO_549_3.jpg', '549', '80,000 - 150,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：法國<br />\n年份：90年代<br />\n數量：5瓶<br />\n容量：700毫升(ABV 40%)<br />\n<br />\n軒尼詩李察干邑被譽為軒尼詩家族的靈魂，由超過一百種非凡的生命之水「eaux-de-vie」調製而成，主要原料大多數源自大香檳區的最佳葡萄園，擁有世界最長久耐嘗干邑的美譽。為了襯托軒尼詩李察的獨特不凡，軒尼詩特別與法國頂級水晶品牌Baccarat合作，選用純熟工匠需耗費40個小時以上工時，才能完成的雕花精品水晶瓶身，讓顧客從視覺上，就能感受這瓶頂級干邑白蘭地的尊貴。', '100', null, null, null, null, null), ('50', 'R', '人頭馬 路易十三 干邑 白蘭地', '12', '/public/pic_content/img/a1/LOTNO_550_1.jpg', '550', '38,000 - 50,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：法國<br />\n年份：約60-70年代(a)，約80年代(c) ，約90年代(b)<br />\n數量：三瓶<br />\n容量：700毫升(ABV 40%)<br />\n<br />\n人頭馬是法國最古老的干邑品牌之一，最具代表性的酒款當屬路易十三。1724 年，人頭馬由RÉMY MARTIN正式創建，距今歷史近 300 年。此三瓶路易十三出品于約60年代至90年代，均配備原產專屬收藏硬盒包裝和巴卡拉水晶瓶塞。', '100', null, null, null, null, null), ('51', 'SUNTORY HIBIKI 30 YEARS BLENDED JAPANESE WHISKY', '響 三十年 日本威士忌', '12', '/public/pic_content/img/a1/LOTNO_551_1.jpg,/public/pic_content/img/a1/LOTNO_551_2.jpg', '551', '28,000 - 40,000', '', null, null, null, null, 'For detailed information, please refer to Chinese site', '產地：日本<br />\r\n數量：2瓶<br />\r\n容量：700毫升(ABV 43%)<br />\r\n<br />\r\n三得利「響30」由日本最具歷史的山崎威士忌蒸餾廠，以熟成30年的麥芽調和而成，每瓶刻印出廠編號，以30切割面水晶瓶及金箔封印。此酒曾獲世界最佳調和威士忌獎。', '100', null, null, null, null, null), ('52', 'O-Serie Replica', 'O-Serie Replica', '13', '/public/pic_content/img/a2/LOTNO_601_1.jpg,/public/pic_content/img/a2/LOTNO_601_2.jpg,/public/pic_content/img/a2/LOTNO_601_3.jpg,/public/pic_content/img/a2/LOTNO_601_4.jpg,/public/pic_content/img/a2/LOTNO_601_5.jpg', '601', '5,000 - 8,000', '', null, null, null, null, 'The O-Series Replica was launched in Photokina in 2000, only 2,000 pieces were made. It comes with Anastigmat 3.5/50mm lens, front lens cap, box and instruction.', '此款O-Serie復刻版相機於2000年的Photokina上推出，限量2,000台；連Anastigmat 3.5/50mm鏡頭連前蓋及包裝說明書。', '100', null, null, null, null, null), ('53', 'I Mod.B Dial-Set Compur', 'I Mod.B Dial-Set Compur', '13', '/public/pic_content/img/a2/LOTNO_602_1.jpg,/public/pic_content/img/a2/LOTNO_602_2.jpg,/public/pic_content/img/a2/LOTNO_602_3.jpg,/public/pic_content/img/a2/LOTNO_602_4.jpg,/public/pic_content/img/a2/LOTNO_602_5.jpg,/public/pic_content/img/a2/LOTNO_602_6.jpg', '602', '30,000 - 50,000', '', null, null, null, null, 'Here is a Dial-Set Compur shutter with film counter lock, only 638 pieces were made, very rare.', '徠卡I Mod. B Compur產量大約只有1,500台，當中分有Rim-set及Dial-set兩款，而Dial-set的產量較少，只產有638台。此相機配有鏡間快門及特別的酒窩形快門按鈕，而鏡頭上獨特的“Film Counter Lock”在Rim-set所沒有的。此機年代久遠，屬收藏佳品。', '100', null, null, null, null, null), ('54', 'I Mod.B Dial-Set Compur', 'I Mod.B Dial-Set Compur', '13', '/public/pic_content/img/a2/LOTNO_603_1.jpg,/public/pic_content/img/a2/LOTNO_603_2.jpg,/public/pic_content/img/a2/LOTNO_603_3.jpg,/public/pic_content/img/a2/LOTNO_603_4.jpg,/public/pic_content/img/a2/LOTNO_603_5.jpg', '603', '30,000 - 50,000', '', null, null, null, null, 'Here is a repainted Dial-Set Compur shutter with film counter lock, only 638 pieces were made.', '此機是I Mod. B Compur中的Dial-Set，產量只有638台。它配有特別的酒窩形快門按鈕，鏡頭上有獨特的film counter lock；機身漆油為後塗。', '100', null, null, null, null, null), ('55', '250GG with Elmar 3.5/5cm', '250GG with Elmar 3.5/5cm', '13', '/public/pic_content/img/a2/LOTNO_604_1.jpg,/public/pic_content/img/a2/LOTNO_604_2.jpg,/public/pic_content/img/a2/LOTNO_604_3.jpg,/public/pic_content/img/a2/LOTNO_604_4.jpg,/public/pic_content/img/a2/LOTNO_604_5.jpg', '604', '50,000 - 80,000', '', null, null, null, null, '250GG was introduced before WWII, also known as “Reporter”. With its enlarged cassettes it accommodated 10m of film, so it could be used for up to 250 exposures. <br />\nIt comes with an Elmar 3.5/5cm lens (#941524) finished in chrome. The body surface has traces of patches.<br />', '250 GG 產於二次大戰前，機身兩側擴大的膠片室可容納長達10米的膠片; 以使連續拍攝250張照片，為其時的新聞工作者提供了極大的便利，250GG又名「Reporter」。<br />\n此機配有銀色Elmar 3.5/5cm鏡頭（#941524）。機身飾皮有修補痕跡。<br />', '100', null, null, null, null, null), ('56', '250FF Reporter with Summar 2/5cm', '250FF Reporter with Summar 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_605_1.jpg,/public/pic_content/img/a2/LOTNO_605_2.jpg,/public/pic_content/img/a2/LOTNO_605_3.jpg,/public/pic_content/img/a2/LOTNO_605_4.jpg,/public/pic_content/img/a2/LOTNO_605_5.jpg,/public/pic_content/img/a2/LOTNO_605_6.jpg,/public/pic_content/img/a2/LOTNO_605_7.jpg', '605', '80,000 - 150,000', '', null, null, null, null, '250FF was launched before WWII, approx. 246 units made and this one from the last batch 150000 to 150125. Its speed to 1/500 second and carried the early engraving, “Ernst Leitz Wetzlar D.R.P”, on top plate. It comes with a collapsible and non-rotating Summar 2/5cm lens (#198330), ESFOO case and front lens cap.', '250FF為二次大戰前之產物，根據記錄只生產了246台。最後一批250FF的機身號碼為150000至150125，此機正是最後一批生產的250FF。其快門速度為1/500，機頂見早期版本的刻字「Ernst Leitz Wetzlar D.R.P」；配有1934年生產，可伸縮及非旋轉式的Summar 2/5cm鏡頭（#198330），連同ESFOO皮套及鏡頭前蓋。', '100', null, null, null, null, null), ('57', 'Leica 72 Betriebsk', 'Leica 72 Betriebsk', '13', '/public/pic_content/img/a2/LOTNO_606_1.jpg,/public/pic_content/img/a2/LOTNO_606_2.jpg,/public/pic_content/img/a2/LOTNO_606_3.jpg,/public/pic_content/img/a2/LOTNO_606_4.jpg,/public/pic_content/img/a2/LOTNO_606_5.jpg,/public/pic_content/img/a2/LOTNO_606_6.jpg,/public/pic_content/img/a2/LOTNO_606_7.jpg', '606', '50,000 - 80,000', '', null, null, null, null, 'This is a Leica 72 for internal use, with “Betriebsk Nr.303” engraved on top plate. It comes with a Summicron 2/5cm lens (#995252) and front lens cap.<br />\nLeica 72 is a 18x24mm format camera produced in Canada and Germany between 1954-1957. It carries a vertical masking on viewfinder window, and the film counter counts to 75. Please refer to James Lager Vol.I p.78-81 for further information.<br />', '這是徠卡72的內部測試機，機頂位置刻有「Betriebsk Nr.303」字樣。此機配有Summicron 2/5cm鏡頭（#995252）及前蓋。<br />\n徠卡72是採用18x24mm格式的相機，於1954至1957年分別在加拿大及德國生產。此機菲林計算器讀數可至75，取景窗口為直檔，其生產年期短，產量不多。有關資料可參閱James Lager Vol.I p.78-81。<br />', '100', null, null, null, null, null), ('58', 'IIIf Betriebsk with Elmar 3.5/5cm &quot;Leitz Eigentum&quot;', 'IIIf Betriebsk with Elmar 3.5/5cm &quot;Leitz Eigentum&quot;', '13', '/public/pic_content/img/a2/LOTNO_607_1.jpg,/public/pic_content/img/a2/LOTNO_607_2.jpg,/public/pic_content/img/a2/LOTNO_607_3.jpg,/public/pic_content/img/a2/LOTNO_607_4.jpg,/public/pic_content/img/a2/LOTNO_607_5.jpg,/public/pic_content/img/a2/LOTNO_607_6.jpg', '607', '20,000 - 30,000', '', null, null, null, null, 'Here is an IIIf for internal use with engraving . “Betriebsk. Nr.156” on the top, not often to be seen. It comes with an Elmar 3.5/5cm lens (#1068878) with engraving “Leitz Eigentum”and a front lens cap.', '此機機頂刻有「Betriebsk. Nr.156」字樣，是徠卡工廠內部測試機，市場上並不常見。此機配有Elmar 3.5/5cm鏡頭 (#1068878)及鏡頭前蓋，鏡頭刻有「Leitz Eigentum」字樣，意思即徠卡資產。', '100', null, null, null, null, null), ('59', 'IIIf with Elmar 2.8/5cm', 'IIIf with Elmar 2.8/5cm', '13', '/public/pic_content/img/a2/LOTNO_608_1.jpg,/public/pic_content/img/a2/LOTNO_608_2.jpg,/public/pic_content/img/a2/LOTNO_608_3.jpg,/public/pic_content/img/a2/LOTNO_608_4.jpg,/public/pic_content/img/a2/LOTNO_608_5.jpg,/public/pic_content/img/a2/LOTNO_608_6.jpg', '608', '5,000 - 8,000', '', null, null, null, null, 'This camera is the second version of IIIf (Batch number 615001-685000) with red synchronization scale numbers 0-20. It comes with Elmar 2.8/ 5cm (#1620023) and front lens cap.', '此機為IIIf的第二版（生產編號為615001-685000），閃燈同步的數字為紅色，連Elmar 2.8/5cm鏡頭（#1620023）及鏡頭前蓋。', '100', null, null, null, null, null), ('60', 'If Black Paint', 'If Black Paint', '13', '/public/pic_content/img/a2/LOTNO_609_1.jpg,/public/pic_content/img/a2/LOTNO_609_2.jpg,/public/pic_content/img/a2/LOTNO_609_3.jpg,/public/pic_content/img/a2/LOTNO_609_4.jpg,/public/pic_content/img/a2/LOTNO_609_5.jpg,/public/pic_content/img/a2/LOTNO_609_6.jpg', '609', '15,000 - 28,000', '', null, null, null, null, 'Here is a repainted If set with Summaron 3.5/3.5cm lens (#1062608), leicavit and front lens cap.', '這台是If黑漆相機，配有同是黑漆的Summaron 3.5/3.5cm鏡頭（#1062608）、過片器及鏡頭前蓋；整套機鏡均為後塗。', '100', null, null, null, null, null), ('61', 'Ig with Summitar 2/5cm', 'Ig with Summitar 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_610_1.jpg,/public/pic_content/img/a2/LOTNO_610_2.jpg,/public/pic_content/img/a2/LOTNO_610_3.jpg,/public/pic_content/img/a2/LOTNO_610_4.jpg,/public/pic_content/img/a2/LOTNO_610_5.jpg,/public/pic_content/img/a2/LOTNO_610_6.jpg', '610', '5,000 - 8,000', '', null, null, null, null, 'This Ig camera comes with a Summitar 2/5cm lens (#981008) with front lens cap.', '此機連同Summitar 2/5cm鏡頭（#981008）及鏡頭前蓋。', '100', null, null, null, null, null), ('62', 'Compur Summicron 2/5cm with IIIf', 'Compur Summicron 2/5cm with IIIf', '13', '/public/pic_content/img/a2/LOTNO_611_1.jpg,/public/pic_content/img/a2/LOTNO_611_2.jpg,/public/pic_content/img/a2/LOTNO_611_3.jpg,/public/pic_content/img/a2/LOTNO_611_4.jpg,/public/pic_content/img/a2/LOTNO_611_5.jpg,/public/pic_content/img/a2/LOTNO_611_6.jpg,/public/pic_content/img/a2/LOTNO_611_7.jpg', '611', '80,000 - 150,000', '', null, null, null, null, 'Leitz designer Erwin Neurath combined a between-the-lens central shutter with the fitting of a Summicron 5cm lens to enable a higher electronic flash sync speed of at 1/100 and 1/200 sec. A special release arm mounted to the camera first closed the central shutter, then opened the focal plane shutter of the Leica IIIf (set to &quot;B&quot;) and then released the exposure with the central shutter. Only 150 lenses with such assembly were produced between 1954 to 1955.<br />\nIt comes with IIIf body (#762275), release arm and front lens cap.<br />', 'Summicron-M 2/5cm鏡間快門鏡頭在市場上十分少見。為提升閃燈同步速度，徠卡設計師Erwin Neurath在此款鏡頭中加入閃光燈同步接口，並增設中央快門及特別的釋放杆，使拍攝時的閃燈同步可提升至1/100及1/200秒。這種特製的鏡間快門鏡頭在1954至1955年間只生產了150支，市場少見。<br />\n此鏡配有IIIf相機 (#762275)、釋放杆及鏡頭前蓋。<br />', '100', null, null, null, null, null), ('63', 'IIIg Betriebsk with Elmar 2.8/5cm Chrome', 'IIIg Betriebsk with Elmar 2.8/5cm Chrome', '13', '/public/pic_content/img/a2/LOTNO_612_1.jpg,/public/pic_content/img/a2/LOTNO_612_2.jpg,/public/pic_content/img/a2/LOTNO_612_3.jpg,/public/pic_content/img/a2/LOTNO_612_4.jpg', '612', '45,000 - 60,000', '', null, null, null, null, 'This is a rare and fully original ex- factory-internal camera in beautiful original condition. Total production of factory internal Leica IIIg cameras was less than 40 units. The vulcanite complete with no chip missing. The shutter crate engraved “Betriebsk Nr. 529”. It comes with an Elmar 2.8/5cm lens (#1626085). The camera has been authenticated by Lars Netopil (Wetzlar).', '這台是罕有的徠卡原裝內部測試機，機身品相狀況良好。IIIg內部測試機的總產量不多於40台，而且此機保存完好，飾皮完整。此機機頂刻有「Betriebsk Nr.529」字樣，配上Elmar 2.8/5cm鏡頭（#1626085）。套機已經過Lars Netopil (Wetzlar)鑑定。', '100', null, null, null, null, null), ('64', 'Leitz 35mm Prototype', 'Leitz 35mm Prototype', '13', '/public/pic_content/img/a2/LOTNO_613_1.jpg,/public/pic_content/img/a2/LOTNO_613_2.jpg,/public/pic_content/img/a2/LOTNO_613_3.jpg,/public/pic_content/img/a2/LOTNO_613_4.jpg,/public/pic_content/img/a2/LOTNO_613_5.jpg', '613', '180,000 - 280,000', '', null, null, null, null, 'Here is a rare prototype of 35mm lens in screw mount. Its aperture ring and focusing lever were made in chrome and front rim with brass. Its shape, size, the design of focusing ring and aperture ring are different from the official batch. It comes with rear lens cap.', '這支極罕見的鏡頭估計是35mm鏡頭的原型版，為螺絲卡口，光圈環以調焦杆以鉻金屬造，前端則以黃銅為料。其形狀、大小、調焦環與光圈環的設計均與量產版本有所不同，市場少見。連同鏡頭後蓋。', '100', null, null, null, null, null), ('65', 'Compur Summicron 2/5cm Prototype', 'Compur Summicron 2/5cm Prototype', '13', '/public/pic_content/img/a2/LOTNO_614_1.jpg,/public/pic_content/img/a2/LOTNO_614_2.jpg,/public/pic_content/img/a2/LOTNO_614_3.jpg,/public/pic_content/img/a2/LOTNO_614_4.jpg,/public/pic_content/img/a2/LOTNO_614_5.jpg,/public/pic_content/img/a2/LOTNO_614_6.png', '614', '280,000 - 400,000', '', null, null, null, null, 'Here is the most unusual prototype of the Compur-Summicron 2/5cm lens in aluminium mount. All outer parts of this lens are still of a different design than the 150 pieces from the official production. Even the lens comes complete with a prototype release-arm, which is also different in design that those commonly known. Lens serial number is 1095052, while official production of the Compur Summicron only started from 1152XXX. The similar prototype lens, serial number 1095051, which was published by Ottmar Michaely in Leica Historica e.V. VIDOM magazine Nr. 95 (2008), p.36, achieved a price of HKD550,000 at L&amp;H 2015 Spring Auction.<br />\nThis lens comes with release arm, both lens caps and a letter of authenticity from Mr. Ottmar Michaely. <br />', '根據資料，正式生產的Compur Summicron 2/5cm鏡頭產量只有150支，沒有正式的貨號，鏡頭號是以 “1152” 開首的七位數；但此鏡的鏡頭編號則是以 “109” 為首的七位數，比正式生產的年份更早。<br />\n除了鏡頭號碼外，此鏡的設計與往後150支Compur Summicron 2/5cm亦有所不同，包括：鏡頭卡口為鋁製、鏡筒上螺紋的分佈位置、鏡身刻字大小及字形、光圈環上垂直條紋的斜度及調焦環的設計。搭配於此鏡頭的釋放杆設計也與正式生產的有所不同，也屬原型版。同款的原型版Compur鏡頭（#1095051）曾被徠卡專家Ottmar Michaely刊登於徠卡雜誌《VIDOM》95 06/2008期 p.36中，並在我司2015年5月春拍中，以接近55萬港元成交；足見其珍稀之處，收藏價值極高。<br />\n此鏡連同釋放杆、前後蓋及Ottmar Michaely的鑑定書。<br />', '100', null, null, null, null, null), ('66', 'Summicron 2/5cm Chrome', 'Summicron 2/5cm Chrome', '13', '/public/pic_content/img/a2/LOTNO_615_1.jpg,/public/pic_content/img/a2/LOTNO_615_2.jpg,/public/pic_content/img/a2/LOTNO_615_3.jpg,/public/pic_content/img/a2/LOTNO_615_4.jpg', '615', '8,000 - 12,000', '', null, null, null, null, 'This is the first version of Summicron 2/5cm lens, 7 elements in 4 groups, with 3 elements of the new LaK9 glass and a thorium glass. It comes with both lens caps.', '第一代Summicron 2/5cm鏡頭，鏡片由7片4組組成，其中三片用上當時新研發的LaK9玻璃，以改善成象質素。此鏡連鏡頭前後蓋。', '100', null, null, null, null, null), ('67', 'Hektor 1.9/7.3cm Black Paint', 'Hektor 1.9/7.3cm Black Paint', '13', '/public/pic_content/img/a2/LOTNO_616_1.jpg,/public/pic_content/img/a2/LOTNO_616_2.jpg,/public/pic_content/img/a2/LOTNO_616_3.jpg,/public/pic_content/img/a2/LOTNO_616_4.jpg', '616', '15,000 - 28,000', '', null, null, null, null, 'Hektor 1.9/7.3cm was launched in the early 1930’s before WWII in small batch of production, with wider apertures for use as a portrait lens, very rare. It comes with front lens cap, keeper and L39M adapter.', '這是全黑漆的Hektor 1.9/7.3cm鏡頭，它是徠卡在二戰前首款擁有1.9大光圈的人像鏡，市場較罕見。此鏡配有膠盅、鏡頭前蓋及L39M轉接環。', '100', null, null, null, null, null), ('68', 'Hektor 1.9/7.3cm Black Paint', 'Hektor 1.9/7.3cm Black Paint', '13', '/public/pic_content/img/a2/LOTNO_617_1.jpg,/public/pic_content/img/a2/LOTNO_617_2.jpg,/public/pic_content/img/a2/LOTNO_617_3.jpg,/public/pic_content/img/a2/LOTNO_617_4.jpg,/public/pic_content/img/a2/LOTNO_617_5.jpg', '617', '15,000 - 25,000', '', null, null, null, null, 'Hektor 1.9/7.3cm was launched in the early 1930’s before WWII in small batch of production, with wider apertures for use as a portrait lens. It comes with rear lens cap and lens hood with cap.', '這是全黑漆的Hektor 1.9/7.3cm鏡頭，它是徠卡在二戰前首款擁有1.9大光圈的人像鏡，色彩豐富，產量不多。此鏡配有遮光罩、遮光罩蓋及鏡頭後蓋。', '100', null, null, null, null, null), ('69', 'Summarex 1.5/8.5cm Chrome', 'Summarex 1.5/8.5cm Chrome', '13', '/public/pic_content/img/a2/LOTNO_618_1.jpg,/public/pic_content/img/a2/LOTNO_618_2.jpg,/public/pic_content/img/a2/LOTNO_618_3.jpg,/public/pic_content/img/a2/LOTNO_618_4.jpg,/public/pic_content/img/a2/LOTNO_618_5.jpg,/public/pic_content/img/a2/LOTNO_618_6.jpg,/public/pic_content/img/a2/LOTNO_618_7.jpg,/public/pic_content/img/a2/LOTNO_618_8.jpg', '618', '15,000 - 20,000', '', null, null, null, null, 'Leitz Summarex 1:1.5/85 mm lens Nr. 1.008.235. Late version of the Summarex lens in overall pristine conditions. The optic are extremely clean, focus and aperture are smooth, there is no oil or rust on the blades. Complete with original front and rear caps, hood, and M-adaptor, in original Leitz fitted leather case. The lens comes with a boxed Leitz SGOOD 8.5cm finder in matching conditions.', '此鏡狀態極佳，玻璃通透，光圈環及對焦環轉動流暢，光圈葉上沒有污跡。此鏡配有前後蓋、遮光罩、M轉接環及原裝徠茲皮套；另外配有SGOOD 8.5cm取景器連包裝。', '100', null, null, null, null, null), ('70', 'Leitz Canada 2.8/9cm Chrome Prototype', 'Leitz Canada 2.8/9cm Chrome Prototype', '13', '/public/pic_content/img/a2/LOTNO_619_1.jpg,/public/pic_content/img/a2/LOTNO_619_2.jpg,/public/pic_content/img/a2/LOTNO_619_3.jpg,/public/pic_content/img/a2/LOTNO_619_4.jpg,/public/pic_content/img/a2/LOTNO_619_5.jpg,/public/pic_content/img/a2/LOTNO_619_6.jpg', '619', '180,000 - 280,000', '', null, null, null, null, 'ts. Unnumbered prototype with “Objektiv f=9cm 1:2:8 Nr.” in German and “Ernst Leitz Canada Ltd.” engraved on the name ring; with the word “Germany” engraved on barrel. The lens is not often to be seen in the market and two or three are believed to have been produced.. <br />\nIt comes with rear lens cap.<br />', '此鏡為極罕有的原型版螺絲口2.8/9cm銀色鏡頭，從未正式推出市場。估計是1969年間，德國廠房轉交加拿大徠茲光學工廠，由光學設計師Dr. Walter Mandler重新設計鏡片及作出調較，以優化質量。此鏡沒有任何編號，前端以德文刻上「Objektiv f=9cm 1:2:8 Nr.」及「Ernst Leitz Canada Ltd.」，但鏡筒則刻有「Germany」字樣，相信只生產過二至三支，極具收藏價值。此鏡配鏡頭後蓋。', '100', null, null, null, null, null), ('71', 'Mountain Elmar 6.3/10.5cm Black Paint', 'Mountain Elmar 6.3/10.5cm Black Paint', '13', '/public/pic_content/img/a2/LOTNO_620_1.jpg,/public/pic_content/img/a2/LOTNO_620_2.jpg,/public/pic_content/img/a2/LOTNO_620_3.jpg,/public/pic_content/img/a2/LOTNO_620_4.jpg,/public/pic_content/img/a2/LOTNO_620_5.jpg', '620', '12,000 - 18,000', '', null, null, null, null, 'Mountain Elmar is famous for its lightweight as it was designed to be light for climbers and general adventurers. The lens comes with both caps, hood and original box.', 'Mountain Elmar 6.3/10.5cm螺絲口鏡頭體積輕巧，專為爬山攝影人士設計。鏡頭配有前後蓋、遮光罩及罕有的原廠鏡頭盒。', '100', null, null, null, null, null), ('72', 'Hektor 4.5/13.5cm Black Paint (Misprint Version)', 'Hektor 4.5/13.5cm Black Paint (Misprint Version)', '13', '/public/pic_content/img/a2/LOTNO_621_1.jpg,/public/pic_content/img/a2/LOTNO_621_2.jpg,/public/pic_content/img/a2/LOTNO_621_3.jpg,/public/pic_content/img/a2/LOTNO_621_4.jpg,/public/pic_content/img/a2/LOTNO_621_5.jpg', '621', '15,000 - 20,000', '', null, null, null, null, 'This Hektor 4.5/13.5cm set is the early version with coating, in matte black paint finished. What is more important, it is engraved as 3.5cm, instead of the normal 13.5cm. It comes with front and rear lens caps.', '此鏡頭最特別有趣之處是由於當時生產過程出錯，導致鏡上的刻度由原來13.5cm變為3.5cm。而此對鏡頭均是極早期的鍍膜版本，鏡身為啞黑漆，配有前後蓋。', '100', null, null, null, null, null), ('73', 'Ross Xtralux 2/5cm Rigid Chrome', 'Ross Xtralux 2/5cm Rigid Chrome', '13', '/public/pic_content/img/a2/LOTNO_622_1.jpg,/public/pic_content/img/a2/LOTNO_622_2.jpg,/public/pic_content/img/a2/LOTNO_622_3.jpg,/public/pic_content/img/a2/LOTNO_622_4.jpg,/public/pic_content/img/a2/LOTNO_622_5.jpg', '622', '280,000 - 400,000', '', null, null, null, null, 'Here is an extremely rare Ross London lens from 1948. These were a special order from Leica user, shown on advertisement on 1949. What is more important, all these lenses were made of best quality glasses, and probably less than 20 pieces were produced. This lens is in L39 mount, with both lens caps. Rear cap engraved with Ross Logo', '此拍品為非常罕見的Ross London鏡頭，產於1948年，此款鏡頭曾刊登於1949年的廣告。此鏡為當時徠卡迷特別訂製，採用最上乘的玻璃為料，成品不多於20支，市面極少見。此鏡為L39卡口，連鏡頭前後蓋，後蓋底刻有Ross商標。', '100', null, null, null, null, null), ('74', 'Dallmeyer Septac Anastigmat 1.5/2”', 'Dallmeyer Septac Anastigmat 1.5/2”', '13', '/public/pic_content/img/a2/LOTNO_623_1.jpg,/public/pic_content/img/a2/LOTNO_623_2.jpg,/public/pic_content/img/a2/LOTNO_623_3.jpg,/public/pic_content/img/a2/LOTNO_623_4.jpg,/public/pic_content/img/a2/LOTNO_623_5.jpg,/public/pic_content/img/a2/LOTNO_623_6.jpg', '623', '150,000 - 220,000', '', null, null, null, null, 'Here is a Dallmeyer lens original made for Wittness camera, this lens can used for Leica screw mount or M cameras. It comes with both lens caps.', '此拍品由英國著名廠商Dallmeyer製造，最初是為Wittness相機所造，可用於徠卡螺絲口相機或M卡口相機，以迷人的散景著稱。此鏡配有前後蓋。', '100', null, null, null, null, null), ('75', 'M3 Chrome with Summicron-M 2/5cm', 'M3 Chrome with Summicron-M 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_624_1.jpg,/public/pic_content/img/a2/LOTNO_624_2.jpg,/public/pic_content/img/a2/LOTNO_624_3.jpg,/public/pic_content/img/a2/LOTNO_624_4.jpg,/public/pic_content/img/a2/LOTNO_624_5.jpg,/public/pic_content/img/a2/LOTNO_624_6.jpg', '624', '120,000 - 180,000', '', null, null, null, null, 'This is early M3 chrome in double stroke operation, with half-moon stopper on the hot shoe, two circular areas on the cast housing and triangular strap lugs. The camera comes with Summicron-M 2/5cm lens (#1154651) and front lens cap; also box, instruction manual and meter (#04573).', '這台是極早期的銀色M3，有多個早期M3的特徵，包括: 兩次過片、機頂熱靴位置呈半月型、機背蓋內的菲林槽有兩個圓點及三角形背帶扣等。此機連同Summicron-M 2/5cm鏡頭（#1154651）及鏡頭前蓋，帶包裝、說明書及測光錶 (#04573)。', '100', null, null, null, null, null), ('76', 'M3 Black Paint with Summicron-M 2/5cm (Raw Brass Mount)', 'M3 Black Paint with Summicron-M 2/5cm (Raw Brass Mount)', '13', '/public/pic_content/img/a2/LOTNO_625_1.jpg,/public/pic_content/img/a2/LOTNO_625_2.jpg,/public/pic_content/img/a2/LOTNO_625_3.jpg,/public/pic_content/img/a2/LOTNO_625_4.jpg,/public/pic_content/img/a2/LOTNO_625_5.jpg,/public/pic_content/img/a2/LOTNO_625_6.jpg,/public/pic_content/img/a2/LOTNO_625_7.jpg', '625', '220,000 - 380,000', '', null, null, null, null, 'This black painted early M3 was produced in 1960 and belongs to the second batch of production number #993501 to 993750, and those with serial number before #1000000 is now very unusual to be seen. This camera comes with a early black paint Summicron-M 2/50mm lens (#1587262) with raw brass mount, front lens cap and leather case.', '本拍品為極早期的黑漆M3，產於1960年，屬於第二批次（#993501至993750），在現存序號#1000000之前的黑漆M3已經非常罕見。機身操作流暢，配黑漆Summicron-M 2/50mm鏡頭 (#1587262)、鏡頭蓋及皮套，極具收藏價值。', '100', null, null, null, null, null), ('77', 'M3 Black Paint', 'M3 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_626_1.jpg,/public/pic_content/img/a2/LOTNO_626_2.jpg,/public/pic_content/img/a2/LOTNO_626_3.jpg,/public/pic_content/img/a2/LOTNO_626_4.jpg,/public/pic_content/img/a2/LOTNO_626_5.jpg', '626', '180,000 - 280,000', '', null, null, null, null, 'This M3 in original matte black paint was produced in 1960 and belongs to the second batch production no.993501-993750. M3 in matte black paint with serial number before #1000000 is exceptionally rare nowadays. Matte black paint was only used in the first and second batch production of M3, early button rewind M2 and MP original Black Paint. This was then replaced by shiny black paint in the later M3 production. It comes with a body cap.', '這台M3是原裝啞黑漆，產於1960年，屬M3黑漆第二批次（#993501 - #993750）的產品，用在此機上的啞黑漆只在首兩批M3，M2按鈕及MP黑漆上使用，後期的M3已改用亮面黑漆。序號在1000000之前的啞黑漆M3已極為罕見，在拍賣會上，此款機也是收藏家追捧焦點之一。這台M3配有機身蓋。', '100', null, null, null, null, null), ('78', 'M3 Black Paint with Summicron-M 2/5cm', 'M3 Black Paint with Summicron-M 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_627_1.jpg,/public/pic_content/img/a2/LOTNO_627_2.jpg,/public/pic_content/img/a2/LOTNO_627_3.jpg,/public/pic_content/img/a2/LOTNO_627_4.jpg,/public/pic_content/img/a2/LOTNO_627_5.jpg,/public/pic_content/img/a2/LOTNO_627_6.jpg,/public/pic_content/img/a2/LOTNO_627_7.jpg', '627', '30,000 - 50,000', '', null, null, null, null, 'Here is a repainted M3 in double stroke, with a repainted Summicron-M 2/5cm lens (#1474740) and front lens cap.', '早期的M3相機，有兩次過片功能特性，機身及鏡頭黑漆均為後塗；連同Summicron-M 2/5cm鏡頭（#1474740）及前蓋。', '100', null, null, null, null, null), ('79', 'M3 Chrome', 'M3 Chrome', '13', '/public/pic_content/img/a2/LOTNO_628_1.jpg,/public/pic_content/img/a2/LOTNO_628_2.jpg,/public/pic_content/img/a2/LOTNO_628_3.jpg,/public/pic_content/img/a2/LOTNO_628_4.jpg,/public/pic_content/img/a2/LOTNO_628_5.jpg', '628', '18,000 - 28,000', '', null, null, null, null, 'Here is a single stroke operation M3 in chrome finished, with interesting serial number containing special sequence. It comes with body cap.', '此M3的機身號碼排列方式整齊有趣，機身屬單次過片，連同機身蓋。', '100', null, null, null, null, null), ('80', 'M3 Black Paint', 'M3 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_629_1.jpg,/public/pic_content/img/a2/LOTNO_629_2.jpg,/public/pic_content/img/a2/LOTNO_629_3.jpg,/public/pic_content/img/a2/LOTNO_629_4.jpg,/public/pic_content/img/a2/LOTNO_629_5.jpg,/public/pic_content/img/a2/LOTNO_629_6.jpg', '629', '80,000 - 120,000', '', null, null, null, null, 'Here is an original M3 in black paint finished, with self-timer and rewind lever. It comes with body cap.', '此機是原裝黑漆M3，帶自拍及撥杆回片。操作功能正常；連機身蓋。', '100', null, null, null, null, null), ('81', 'M2 Black Paint', 'M2 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_630_1.jpg,/public/pic_content/img/a2/LOTNO_630_2.jpg,/public/pic_content/img/a2/LOTNO_630_3.jpg,/public/pic_content/img/a2/LOTNO_630_4.jpg,/public/pic_content/img/a2/LOTNO_630_5.jpg', '630', '80,000 - 120,000', '', null, null, null, null, 'This M2 belongs to the first batch production, numbered between 948601 and 949100, which were introduced in James Lager Vol.I p.202. And here is a M2 with button rewind, and no self-timer, in original black paint finished; less than 200 pieces were made. It comes with body cap.', '這是第一批生產的按鈕回片、不帶自拍黑漆版M2，產於1958年。首批M2黑漆的機身編號在948601至949100之間（這個版本的M2在James Lager Vol.I. p.202中也有介紹過）。而有按鈕回片及不帶自拍的黑漆M2 更加罕見，產量少於200台。此機為原裝黑漆，配有機身蓋。', '100', null, null, null, null, null), ('82', 'M2 Black Paint', 'M2 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_631_1.jpg,/public/pic_content/img/a2/LOTNO_631_2.jpg,/public/pic_content/img/a2/LOTNO_631_3.jpg,/public/pic_content/img/a2/LOTNO_631_4.jpg,/public/pic_content/img/a2/LOTNO_631_5.jpg,/public/pic_content/img/a2/LOTNO_631_6.jpg', '631', '60,000 - 100,000', '', null, null, null, null, 'Here is an original M2 in black paint finished with self-timer, less than 300 units were produced, very rare. It comes with body cap.', '此為原裝M2黑漆機身，產於1960年，連自拍，產量少於300台，實屬罕有。此機機頂部分露銅，連機身蓋。', '100', null, null, null, null, null), ('83', 'M2 Black Paint', 'M2 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_632_1.jpg,/public/pic_content/img/a2/LOTNO_632_2.jpg,/public/pic_content/img/a2/LOTNO_632_3.jpg,/public/pic_content/img/a2/LOTNO_632_4.jpg,/public/pic_content/img/a2/LOTNO_632_5.jpg,/public/pic_content/img/a2/LOTNO_632_6.jpg,/public/pic_content/img/a2/LOTNO_632_7.jpg', '632', '35,000 - 55,000', '', null, null, null, null, 'Original M2 Black Paint with self-timer and rewind lever. It comes with body cap and leather case.', '原裝黑漆M2機身，產自1962年。帶自拍及回片，機械性能良好，連機身蓋及皮套。', '100', null, null, null, null, null), ('84', 'M2 Black Paint', 'M2 Black Paint', '13', '/public/pic_content/img/a2/LOTNO_633_1.jpg,/public/pic_content/img/a2/LOTNO_633_2.jpg,/public/pic_content/img/a2/LOTNO_633_3.jpg,/public/pic_content/img/a2/LOTNO_633_4.jpg,/public/pic_content/img/a2/LOTNO_633_5.jpg,/public/pic_content/img/a2/LOTNO_633_6.jpg', '633', '48,000 - 68,000', '', null, null, null, null, 'Here is an early original M2 black paint, comes with body cap.', '原裝早期M2黑漆機身，操作功能正常；連機身蓋。', '100', null, null, null, null, null), ('85', 'M4 Black Paint with Summicron-M 2/35mm', 'M4 Black Paint with Summicron-M 2/35mm', '13', '/public/pic_content/img/a2/LOTNO_634_1.jpg,/public/pic_content/img/a2/LOTNO_634_2.jpg,/public/pic_content/img/a2/LOTNO_634_3.jpg,/public/pic_content/img/a2/LOTNO_634_4.jpg,/public/pic_content/img/a2/LOTNO_634_5.jpg,/public/pic_content/img/a2/LOTNO_634_6.jpg', '634', '50,000 - 80,000', '', null, null, null, null, 'This M4 Black Paint was produced in 1963, comes with a Summicron-M 2/35mm Chrome lens (#2010236) and front lens cap. Both camera and lens engraved with the word “MDW 062461508”.', '黑漆版的M4產於1963年，機頂及附帶的銀色Summicron-M 2/35mm八枚玉鏡頭（#2010236）均刻有「MDW 062461508」字樣；連鏡頭前蓋。', '100', null, null, null, null, null), ('86', 'M4 Midland “50 Jahre”', 'M4 Midland “50 Jahre”', '13', '/public/pic_content/img/a2/LOTNO_635_1.jpg,/public/pic_content/img/a2/LOTNO_635_2.jpg,/public/pic_content/img/a2/LOTNO_635_3.jpg,/public/pic_content/img/a2/LOTNO_635_4.jpg,/public/pic_content/img/a2/LOTNO_635_5.jpg,/public/pic_content/img/a2/LOTNO_635_6.jpg', '635', '18,000 - 25,000', '', null, null, null, null, 'Here is a 50th anniversary edition manufactured in Canada, with “50 JAHRE” engraved and surrounded with maple leaves. The word “Midland Canada” engraved on top plate. It carries a special number 066-E, and comes with a body cap.', '這台M4是徠卡五十週年特別版，產於加拿大，機頂正面有「50 JAHRE」及楓葉的標示，機頂上刻有「Midland, Canada」字樣，機背有特別編號「066-E」。相機連同機身蓋。', '100', null, null, null, null, null), ('87', 'M4-P “Leica 1913 - 1983” Chrome', 'M4-P “Leica 1913 - 1983” Chrome', '13', '/public/pic_content/img/a2/LOTNO_636_1.jpg,/public/pic_content/img/a2/LOTNO_636_2.jpg,/public/pic_content/img/a2/LOTNO_636_3.jpg,/public/pic_content/img/a2/LOTNO_636_4.jpg,/public/pic_content/img/a2/LOTNO_636_5.jpg', '636', '8,000 - 12,000', '', null, null, null, null, 'To commemorate the 70th years of the first 135mm camera produced in 1913, M4-P “70 Years” was launched. “Leica 1913 – 1983” engraved at the back of the camera and the winder. The commemorative number of this camera is I058. It comes with packing, certificate, body cap and strap.', '此套機是為慶祝第一台135mm相機面世70週年而生產，紀念號為I058，相機機背刻有「Leica 1913 – 1983」字樣；機身配有原包裝證書、相機蓋及相機帶。', '100', null, null, null, null, null), ('88', 'M5 with Summicron-C 2/40mm', 'M5 with Summicron-C 2/40mm', '13', '/public/pic_content/img/a2/LOTNO_637_1.jpg,/public/pic_content/img/a2/LOTNO_637_2.jpg,/public/pic_content/img/a2/LOTNO_637_3.jpg,/public/pic_content/img/a2/LOTNO_637_4.jpg,/public/pic_content/img/a2/LOTNO_637_5.jpg', '637', '10,000 - 20,000', '', null, null, null, null, 'A black M5 comes with a Summicron-C 2/40mm lens (#2566158), leather case, hood, filter and front lens cap.', '黑色的M5機身配上Summicron-C 2/40mm鏡頭（#2566158），連同皮套、遮光罩、濾鏡及鏡頭前蓋。', '100', null, null, null, null, null), ('89', 'M7 0.72 Black', 'M7 0.72 Black', '13', '/public/pic_content/img/a2/LOTNO_638_1.jpg,/public/pic_content/img/a2/LOTNO_638_2.jpg,/public/pic_content/img/a2/LOTNO_638_3.jpg,/public/pic_content/img/a2/LOTNO_638_4.jpg,/public/pic_content/img/a2/LOTNO_638_5.jpg', '638', '18,000 - 28,000', '', null, null, null, null, 'This camera carries no number engraving, very special. It comes with body cap.', '此機沒有機身號，十分特別。此機連機身蓋。', '100', null, null, null, null, null), ('90', 'MP 0.58 Chrome', 'MP 0.58 Chrome', '13', '/public/pic_content/img/a2/LOTNO_639_1.jpg,/public/pic_content/img/a2/LOTNO_639_2.jpg,/public/pic_content/img/a2/LOTNO_639_3.jpg,/public/pic_content/img/a2/LOTNO_639_4.jpg,/public/pic_content/img/a2/LOTNO_639_5.jpg,/public/pic_content/img/a2/LOTNO_639_6.jpg', '639', '15,000 - 28,000', '', null, null, null, null, 'This is a MP chrome in viewfinder magnifications of 0.58x, with full packing, papers, strap and body cap.', 'MP銀色機身配以0.58線框，包裝證書連機身蓋齊全，附相機帶。', '100', null, null, null, null, null), ('91', 'MP 0.85 Chrome with Top Plate', 'MP 0.85 Chrome with Top Plate', '13', '/public/pic_content/img/a2/LOTNO_640_1.jpg,/public/pic_content/img/a2/LOTNO_640_2.jpg,/public/pic_content/img/a2/LOTNO_640_3.jpg,/public/pic_content/img/a2/LOTNO_640_4.jpg,/public/pic_content/img/a2/LOTNO_640_5.jpg', '640', '15,000 - 28,000', '', null, null, null, null, 'It is a classic MP in a la carte version. Unlike the original design, its top plate has been replaced to one without engraving by Leica. It comes with body cap and the original top plate.', '經典的徠卡MP機身，物主費盡心機，將原有相機送回原廠，換上沒有刻字的機頂蓋；故此機最特別之處是附有一個相同號碼的原機頂蓋，連機身蓋。', '100', null, null, null, null, null), ('92', 'Leica M (Typ 240) Chrome', 'Leica M (Typ 240) Chrome', '13', '/public/pic_content/img/a2/LOTNO_641_1.jpg,/public/pic_content/img/a2/LOTNO_641_2.jpg,/public/pic_content/img/a2/LOTNO_641_3.jpg,/public/pic_content/img/a2/LOTNO_641_4.jpg,/public/pic_content/img/a2/LOTNO_641_5.jpg', '641', '30,000 - 48,000', '', null, null, null, null, 'Here is a full frame Leica M (Typ 240) in 2.4 megapixels. This camera was unnumbered, only a special number N050/100 on hot shoe, not often to be seen. It comes with a body cap.', '於2012年推出的Leica M（Typ 240）相機擁有全畫幅及高達2400像素。此機只在熱靴位置刻有「N050/100」字樣，沒有機身編號，市場少有。此機配有機身蓋。', '100', null, null, null, null, null), ('93', 'Tri-Elmar-M 4/28-35-50mm ASPH Black', 'Tri-Elmar-M 4/28-35-50mm ASPH Black', '13', '/public/pic_content/img/a2/LOTNO_642_1.jpg,/public/pic_content/img/a2/LOTNO_642_2.jpg,/public/pic_content/img/a2/LOTNO_642_3.jpg,/public/pic_content/img/a2/LOTNO_642_4.jpg,/public/pic_content/img/a2/LOTNO_642_5.jpg', '642', '18,000 - 28,000', '', null, null, null, null, 'This is one of the remarkable lenses in the M-series. It is a zoom lens with tri focal length positions, E49 with 6 Bit, completes with rear lens cap and hood (#12450).', '此鏡為徠卡M系列中的其中一款代表鏡頭，可以三段變焦，成象媲美定焦鏡。此鏡頭口徑為E49，帶6 Bit；連後蓋及遮光罩（#12450）。', '100', null, null, null, null, null), ('94', 'Elmarit-M 2.8/28mm Black', 'Elmarit-M 2.8/28mm Black', '13', '/public/pic_content/img/a2/LOTNO_643_1.jpg,/public/pic_content/img/a2/LOTNO_643_2.jpg,/public/pic_content/img/a2/LOTNO_643_3.jpg,/public/pic_content/img/a2/LOTNO_643_4.jpg,/public/pic_content/img/a2/LOTNO_643_5.jpg', '643', '18,000 - 28,000', '', null, null, null, null, 'Here is the first version of Elmarit-M 2.8/28mm lens made in Canada, with keeper and front lens cap.', '這是加拿大製造的第一代Elmarit-M 2.8/28mm鏡頭，配有膠盅及鏡頭前蓋。', '100', null, null, null, null, null), ('95', 'Elmarit-M 2.8/21mm E49', 'Elmarit-M 2.8/21mm E49', '13', '/public/pic_content/img/a2/LOTNO_644_1.jpg,/public/pic_content/img/a2/LOTNO_644_2.jpg,/public/pic_content/img/a2/LOTNO_644_3.jpg,/public/pic_content/img/a2/LOTNO_644_4.jpg,/public/pic_content/img/a2/LOTNO_644_5.jpg,/public/pic_content/img/a2/LOTNO_644_6.jpg,/public/pic_content/img/a2/LOTNO_644_7.jpg', '644', '68,000 - 100,000', '', null, null, null, null, 'Leitz Canada rare original Elmarit-M 2.8/21mm lens. It is estimated that only a few such lenses exist as most were withdrawn by Leitz once the defect was discovered; and the remaining lenses were modified by Leitz. The 21mm Elmarit were introduced at Photokina 1980, together with the Leica M4-P. The very first design had an E49 front filter thread mount, which made the lens looking very similar to the 28mm Elmarit of the time. However, the E49 lens design showed cut-off at maximum aperture when a filter was attached. Leitz immediately withdraw the E49 design and changed to E60. Most of the originally produced E49 lenses were converted to E60. It can be estimated that only a few dozens of the original E49 design lenses have survived. For further information on the lens, please refer to James Lager Vol.II p.150. It comes with rear lens cap and an ultra-rare lens hood.', '這是罕有的原裝加拿大徠茲Elmarit-M 2.8/21mm鏡頭。在1980年的Photokina徠卡發表了此款Elmarit-M 21mm鏡頭及M4-P。此鏡口徑為E49，外形與當時的28mm Elmarit相近﹔但很快徠卡就發現當此鏡加上濾鏡時有周邊暗角的問題，故立刻回收產品並把鏡頭的口徑改為E60。大部份原裝E49已被更換成E60，相信只有極少數還保留原裝設計。另外，此鏡有鋸齒紋調焦按鈕。有關此鏡資料，可參閱James Lager Vol.II p.150。配鏡頭後蓋及稀有原裝遮光罩（#12537）。', '100', null, null, null, null, null), ('96', 'Elmarit-M 2.8/28mm Black Prototype', 'Elmarit-M 2.8/28mm Black Prototype', '13', '/public/pic_content/img/a2/LOTNO_645_1.jpg,/public/pic_content/img/a2/LOTNO_645_2.jpg,/public/pic_content/img/a2/LOTNO_645_3.jpg,/public/pic_content/img/a2/LOTNO_645_4.jpg,/public/pic_content/img/a2/LOTNO_645_5.jpg,/public/pic_content/img/a2/LOTNO_645_6.jpg,/public/pic_content/img/a2/LOTNO_645_7.jpg', '645', '180,000 - 280,000', '', null, null, null, null, 'Here is a Leitz Canada Elmarit-M 2.8/28mm lens under optical formula 375 (hence the prefix 375 to the lens number), which is a special optical construction and no production lens of such designs was ever produced commercially. It is estimated that three such lenses were produced. Lens No.375-002 was sold in this sale room a year ago. This lens has been authenticated by Paul Henry Van Hasbroeck. It comes with rear lens cap.', '此款Elmarit-M 2.8/28mm產於加拿大，光學設計編號為375，此鏡是該編號下的第三支。此鏡產量極少，擁有此外觀特徵的鏡頭從未出現於量產之列中，甚為罕見。同款另一支（編號375-002）曾於我司2016年春拍中，以40多萬港元拍出。此鏡已經過Paul Henry Van Hasbroeck鑑定。<br />\n配有鏡頭後蓋。<br />', '100', null, null, null, null, null), ('97', 'Elmarit-M 2.8/28mm Prototype', 'Elmarit-M 2.8/28mm Prototype', '13', '/public/pic_content/img/a2/LOTNO_646_1.jpg,/public/pic_content/img/a2/LOTNO_646_2.jpg,/public/pic_content/img/a2/LOTNO_646_3.jpg,/public/pic_content/img/a2/LOTNO_646_4.jpg,/public/pic_content/img/a2/LOTNO_646_5.jpg,/public/pic_content/img/a2/LOTNO_646_6.jpg', '646', '200,000 - 300,000', '', null, null, null, null, 'Here is a prototype lens with prototype serial number in different design to production models. Such as E39 instead of E49 or E46, depth of field, focusing ring and aperture ring look slightly different, the front part of the lens is shorter and the overall shape is rectangle rather than hourglass , has tiger claw focus tab. It comes with both lens caps.', '此鏡鏡頭編號獨特，不是在正常生產序列之中，而且設計外型與別不同。首先鏡頭口徑為E39，不是正常版本的E49或E46；而光圈環、調焦環及景深尺的設計，亦與其他版本有所不同；雖然外型上與第三代鏡頭較為相似，但此鏡前端比正常版本要短，而且鏡身整體呈筒狀，加上虎爪的調焦杆，綜合起來均與過往版本有所不同。<br />\n此鏡配前後蓋。<br />', '100', null, null, null, null, null), ('98', 'Summilux-M 1.4/35mm Steel Rim Chrome', 'Summilux-M 1.4/35mm Steel Rim Chrome', '13', '/public/pic_content/img/a2/LOTNO_647_1.jpg,/public/pic_content/img/a2/LOTNO_647_2.jpg,/public/pic_content/img/a2/LOTNO_647_3.jpg,/public/pic_content/img/a2/LOTNO_647_4.jpg,/public/pic_content/img/a2/LOTNO_647_5.jpg', '647', '30,000 - 50,000', '', null, null, null, null, 'It comes with goggles and both caps.', '加拿大製的銀色Summilux-M 1.4/35mm鋼咀眼鏡版，連眼鏡及鏡頭前後蓋。', '100', null, null, null, null, null), ('99', 'Summilux-M 1.4/35mm ASPH Titanium', 'Summilux-M 1.4/35mm ASPH Titanium', '13', '/public/pic_content/img/a2/LOTNO_648_1.jpg,/public/pic_content/img/a2/LOTNO_648_2.jpg,/public/pic_content/img/a2/LOTNO_648_3.jpg,/public/pic_content/img/a2/LOTNO_648_4.jpg,/public/pic_content/img/a2/LOTNO_648_5.jpg,/public/pic_content/img/a2/LOTNO_648_6.jpg', '648', '20,000 - 38,000', '', null, null, null, null, 'Here is a later titanium version in which only a small batch was produced. It comes with rear lens cap and hood (12589) with cap.', '此款鈦版鏡頭產量不多，配有鏡頭後蓋及遮光罩（12589）連蓋。', '100', null, null, null, null, null), ('100', 'Summicron-M 2/35mm Chrome (8 Elements)', 'Summicron-M 2/35mm Chrome (8 Elements)', '13', '/public/pic_content/img/a2/LOTNO_649_1.jpg,/public/pic_content/img/a2/LOTNO_649_2.jpg,/public/pic_content/img/a2/LOTNO_649_3.jpg,/public/pic_content/img/a2/LOTNO_649_4.jpg,/public/pic_content/img/a2/LOTNO_649_5.jpg', '649', '22,000 - 38,000', '', null, null, null, null, 'Here is an 8 elements lens without serial number engraved, made in Germany. It comes with rear lens cap.', '這是徠卡非常經典及著名德國製的八枚玉鏡頭，但此鏡鏡頭前端及其他位置都沒有刻上鏡頭編號，較為少見。此鏡連鏡頭後蓋。', '100', null, null, null, null, null), ('101', 'Summicron-M 2/35mm Black (8 Elements)', 'Summicron-M 2/35mm Black (8 Elements)', '13', '/public/pic_content/img/a2/LOTNO_650_1.jpg,/public/pic_content/img/a2/LOTNO_650_2.jpg,/public/pic_content/img/a2/LOTNO_650_3.jpg,/public/pic_content/img/a2/LOTNO_650_4.jpg,/public/pic_content/img/a2/LOTNO_650_5.jpg', '650', '35,000 - 50,000', '', null, null, null, null, 'Here is an early version of Summicron-M 2/35mm 8 elements lens made in Canada. It comes with both caps.', '較早期的加拿大製Summicron-M 2/35mm黑色八枚玉鏡頭，調焦環為紅字；配有鏡頭前後蓋。', '100', null, null, null, null, null), ('102', 'Summaron-M 2.8/35mm Chrome', 'Summaron-M 2.8/35mm Chrome', '13', '/public/pic_content/img/a2/LOTNO_651_1.jpg,/public/pic_content/img/a2/LOTNO_651_2.jpg,/public/pic_content/img/a2/LOTNO_651_3.jpg,/public/pic_content/img/a2/LOTNO_651_4.jpg,/public/pic_content/img/a2/LOTNO_651_5.jpg,/public/pic_content/img/a2/LOTNO_651_6.jpg', '651', '5,000 - 8,000', '', null, null, null, null, 'Here is a Summaron-M 2.8/35mm lens in M mount, comes with both lens caps.', '這是M卡口的小八枚鏡頭，配有鏡頭前後蓋。', '100', null, null, null, null, null), ('103', 'Summicron-M 2/5cm Chrome', 'Summicron-M 2/5cm Chrome', '13', '/public/pic_content/img/a2/LOTNO_652_1.jpg,/public/pic_content/img/a2/LOTNO_652_2.jpg,/public/pic_content/img/a2/LOTNO_652_3.jpg,/public/pic_content/img/a2/LOTNO_652_4.jpg,/public/pic_content/img/a2/LOTNO_652_5.jpg', '652', '18,000 - 28,000', '', null, null, null, null, 'Here is a second version of Summicron-M 2/5cm in chrome, with four Lak9 lens elements and a black bottom focusing ring. It comes with both lens caps.', '這是第二代的Summicron-M 2/5cm鏡頭，以四片鑭玻璃精製而成，成象質素大為提升。最特別之處是此鏡最底的焦距環為黑色，有別於其銀色鏡身。此鏡配有前後蓋。', '100', null, null, null, null, null), ('104', 'Summilux-M 1.4/50mm Black Paint', 'Summilux-M 1.4/50mm Black Paint', '13', '/public/pic_content/img/a2/LOTNO_653_1.jpg,/public/pic_content/img/a2/LOTNO_653_2.jpg,/public/pic_content/img/a2/LOTNO_653_3.jpg,/public/pic_content/img/a2/LOTNO_653_4.jpg,/public/pic_content/img/a2/LOTNO_653_5.jpg,/public/pic_content/img/a2/LOTNO_653_6.jpg', '653', '80,000 - 120,000', '', null, null, null, null, 'Here is a black painted Summilux-M 1.4/50mm lens with keeper.', '此黑漆鏡頭附膠盅。', '100', null, null, null, null, null);
INSERT INTO `curio_pic_content` VALUES ('105', 'Noctilux-M 1.2/50mm', 'Noctilux-M 1.2/50mm', '13', '/public/pic_content/img/a2/LOTNO_654_1.jpg,/public/pic_content/img/a2/LOTNO_654_2.jpg,/public/pic_content/img/a2/LOTNO_654_3.jpg,/public/pic_content/img/a2/LOTNO_654_4.jpg,/public/pic_content/img/a2/LOTNO_654_5.jpg', '654', '80,000 - 120,000', '', null, null, null, null, 'Here is one of the most popular lenses from Leica, approximately 1500 units were manufactured. It comes with both lens caps and hood (#12503).', '此為徠卡名鏡Noctilux-M 1.2/50mm，當年產量約1500支，編號由2176701開始。此款鏡頭有兩片為手磨非球面玻璃，製作工序繁複，成象質素極為出色。此鏡配有前後蓋及遮光罩（#12503）。', '100', null, null, null, null, null), ('106', 'Summilux-M 1.4/50mm Black Paint', 'Summilux-M 1.4/50mm Black Paint', '13', '/public/pic_content/img/a2/LOTNO_655_1.jpg,/public/pic_content/img/a2/LOTNO_655_2.jpg,/public/pic_content/img/a2/LOTNO_655_3.jpg,/public/pic_content/img/a2/LOTNO_655_4.jpg,/public/pic_content/img/a2/LOTNO_655_5.jpg,/public/pic_content/img/a2/LOTNO_655_6.jpg,/public/pic_content/img/a2/LOTNO_655_7.jpg,/public/pic_content/img/a2/LOTNO_655_8.jpg', '655', '150,000 - 200,000', '', null, null, null, null, 'This is the first version of Summilux-M 1.4/50mm lens in black paint finished, with raw brass mount, extremely rare. It comes with .keeper, hood #12521G and hood cap.', '此為極度罕見的第一代Summilux-M 1.4/50mm黑漆鏡頭，鏡頭底部的接口以黃銅製造，十分稀有。此鏡連同膠盅、遮光罩（12521G）及遮光罩蓋。', '100', null, null, null, null, null), ('107', 'CL-2/50mm Black Prototype', 'CL-2/50mm Black Prototype', '13', '/public/pic_content/img/a2/LOTNO_656_1.jpg,/public/pic_content/img/a2/LOTNO_656_2.jpg,/public/pic_content/img/a2/LOTNO_656_3.jpg,/public/pic_content/img/a2/LOTNO_656_4.jpg,/public/pic_content/img/a2/LOTNO_656_5.jpg', '656', '100,000 - 160,000', '', null, null, null, null, 'Here is a prototype lens of CL, its serial number doesn’t follow the official production, and it carries an optical formula number C241. Two types of lens were produced under CL series in 1970’s, which are 40mm and 90mm. This 2/50mm lens for CL is extremely rare. It comes with both lens caps. Similar lens published in James Lager Book Vol.II p.229.', '這是徠卡生產的CL系列原型鏡頭，其鏡頭序號不在正常之列，鏡頭前端亦刻有特別的光學編號C241。徠卡只為CL系列推出過兩款鏡頭，分別是40mm及90mm版本，當中Elmarit 40mm鏡頭的總產量亦只有大約500支，所以這支焦距50mm的CL系列鏡頭可謂絕無僅有，收藏價值極高。在James Lager Vol.II p.229中可找同相類鏡頭。此鏡配有前後蓋。', '100', null, null, null, null, null), ('108', 'Summicron-M 2/50mm Gold Plated', 'Summicron-M 2/50mm Gold Plated', '13', '/public/pic_content/img/a2/LOTNO_657_1.jpg,/public/pic_content/img/a2/LOTNO_657_2.jpg,/public/pic_content/img/a2/LOTNO_657_3.jpg,/public/pic_content/img/a2/LOTNO_657_4.jpg,/public/pic_content/img/a2/LOTNO_657_5.jpg', '657', '20,000 - 38,000', '', null, null, null, null, 'The fourth version of Summicron-M 2/50mm lens in original gold plated, not often seen in the market. It comes with built-in hood and both lens caps.', '這是經典的第四代Summicron-M 2/50mm原廠鍍金鏡頭。鏡頭配有內置遮光罩及前後蓋。', '100', null, null, null, null, null), ('109', 'Summicron-M 2/90mm Black', 'Summicron-M 2/90mm Black', '13', '/public/pic_content/img/a2/LOTNO_658_1.jpg,/public/pic_content/img/a2/LOTNO_658_2.jpg,/public/pic_content/img/a2/LOTNO_658_3.jpg,/public/pic_content/img/a2/LOTNO_658_4.jpg,/public/pic_content/img/a2/LOTNO_658_5.jpg', '658', '5,000 - 8,000', '', null, null, null, null, 'This is the first version Summicron-M 2/90mm lens made in Canada, with built-in hood, both lens caps and box.', '這是第一代的原廠Summicron-M 2/90mm鏡頭，產自加拿大，內置遮光罩。此鏡連同鏡頭前後蓋及包裝盒。', '100', null, null, null, null, null), ('110', 'Elmar-C 4/90mm Black Prototype', 'Elmar-C 4/90mm Black Prototype', '13', '/public/pic_content/img/a2/LOTNO_659_1.jpg,/public/pic_content/img/a2/LOTNO_659_2.jpg,/public/pic_content/img/a2/LOTNO_659_3.jpg,/public/pic_content/img/a2/LOTNO_659_4.jpg,/public/pic_content/img/a2/LOTNO_659_5.jpg,/public/pic_content/img/a2/LOTNO_659_6.jpg', '659', '60,000 - 100,000', '', null, null, null, null, 'Here is a rare prototype lens with a special serial number. It carries some special features as well, such as the engraving style of aperture scale, and a wider barrel below the aperture ring. Similar lens No. 0001215 published in James Lager Book Vol.II P. 235. It comes with both lens caps.', '此原型版鏡頭編號非在正常序列號之內，其外觀設計亦與量產版本有所不同；例如：光圈環刻度標示、光圈環下一圈的鏡筒比正常版本闊等，都可顯示此鏡為原型版；同款鏡頭（序號0001215）曾刊登於James Lager Vol.II p.235中。此鏡配有鏡頭前後蓋。', '100', null, null, null, null, null), ('111', 'Telyt-M 8/400mm Prototype', 'Telyt-M 8/400mm Prototype', '13', '/public/pic_content/img/a2/LOTNO_660_1.jpg,/public/pic_content/img/a2/LOTNO_660_2.jpg,/public/pic_content/img/a2/LOTNO_660_3.jpg', '660', '18,000 - 25,000', '', null, null, null, null, 'This is Leitz Telyt (M) 1:8/400 mm prototype lens Nr. 0.000.688. Rare and fully original experimental lens for use on Visoflex II, prepared for testing at the 1964 Tokyo Olympic Games. Clean glasses. Total production of the Telyt 8/400 lenses was less than 30. The project led into the later 6.8/400 version for serial production. The lens has been authenticated by Lars Netopil (Wetzlar). It comes with both lens caps.', '此鏡是當年徠卡為1964年東京奧運而研發，用於Visoflex II上的Telyt-M 8/400mm原型版鏡頭，鏡頭編號不在正常生產序列之中，總產量不多於30支，後期的量產版本已改為6.8/400mm，原裝罕有。鏡頭配有前後蓋，己經過Lars Netopil (Wetzlar)鑑定。', '100', null, null, null, null, null), ('112', 'IIIc Heer with Summitar 2/5cm', 'IIIc Heer with Summitar 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_661_1.jpg,/public/pic_content/img/a2/LOTNO_661_2.jpg,/public/pic_content/img/a2/LOTNO_661_3.jpg,/public/pic_content/img/a2/LOTNO_661_4.jpg,/public/pic_content/img/a2/LOTNO_661_5.jpg', '661', '25,000 - 35,000', '', null, null, null, null, 'Here is a IIIc for military use during WWII. The word “Heer” which means army engraved on the top plate and on the barrel of Summitar 2/5cm lens (#525143). It completes with front lens cap.', '此為二次世界大戰軍用機，機頂上及Summitar 2/5cm鏡頭（#525143）均刻有「Heer」字樣；附鏡頭前蓋。', '100', null, null, null, null, null), ('113', 'IIIc Grey with Summitar 2/5cm', 'IIIc Grey with Summitar 2/5cm', '13', '/public/pic_content/img/a2/LOTNO_662_1.jpg,/public/pic_content/img/a2/LOTNO_662_2.jpg,/public/pic_content/img/a2/LOTNO_662_3.jpg,/public/pic_content/img/a2/LOTNO_662_4.jpg,/public/pic_content/img/a2/LOTNO_662_5.jpg,/public/pic_content/img/a2/LOTNO_662_6.jpg', '662', '30,000 - 50,000', '', null, null, null, null, 'Here is a grey painted IIIc camera, the Summitar 2/5cm lens (#552231) included here was delivered to Germany Air Force on 19th November 1940, with shipping code 19481. The word “Luftwaffen – Eigentum” engraved on barrel. Please refer to James Lager Vol.I P.59 for further illustration. <br />\nThe set comes with a front lens cap.<br />', '這台是原裝灰漆IIIc配同色系皮革，是二戰期間的國防軍用版本。特別之處是套機配有刻上「Luftwaffen – Eigentum」字樣的Summitar 2/5cm鏡頭（#552231）乃1940年11月19日付運予德國柏林空軍使用，付運編號19481。有關資料可參閱James Lager Vol.I P.59。<br />\n套機連同鏡頭前蓋。<br />', '100', null, null, null, null, null), ('114', 'IIIf Black Paint &quot;Swedish Army&quot;', 'IIIf Black Paint &quot;Swedish Army&quot;', '13', '/public/pic_content/img/a2/LOTNO_663_1.jpg,/public/pic_content/img/a2/LOTNO_663_2.jpg,/public/pic_content/img/a2/LOTNO_663_3.jpg,/public/pic_content/img/a2/LOTNO_663_4.jpg,/public/pic_content/img/a2/LOTNO_663_5.jpg,/public/pic_content/img/a2/LOTNO_663_6.jpg', '663', '250,000 - 380,000', '', null, null, null, null, 'This IIIf was specially made for the Swedish national army in 1956. It can withstand very extreme weather conditions in Sweden and only 100 units were produced, with serial number from 822901 to 823000. The camera and lens are original; complete with matching black paint Elmar 3.5/5cm (#1427036) and a leather case which stamped with three crowns, very rare.', '1950年IIIf正式面世，全球其中一支歷史最悠久的常備軍 「瑞典國軍」 (Swedish Army)，在1956年特別向德國徠卡下訂單，訂造100台IIIf予軍隊使用。能夠在嚴冬環境使用的IIIf黑漆版， 生產批號由822901- 823000。此機身及鏡頭均為原裝，連Elmar 3.5/5cm (#1427036)及刻有三皇冠的皮套，市面少有。', '100', null, null, null, null, null), ('115', 'M3 Olive “Bundeseigentum”', 'M3 Olive “Bundeseigentum”', '13', '/public/pic_content/img/a2/LOTNO_664_1.jpg,/public/pic_content/img/a2/LOTNO_664_2.jpg,/public/pic_content/img/a2/LOTNO_664_3.jpg,/public/pic_content/img/a2/LOTNO_664_4.jpg', '664', '120,000 - 150,000', '', null, null, null, null, 'Here is an early rare M3 in original olive paint, made for German Army. This particular camera has been published in Paul-Henry Van Hasbroeck, “Leica In Colour” p.85. According to Leitz factory records of the camera assembly department, number from 927671 to 927734 are belonged to M3 Olive as well. “Bundeseigentum 12-121-5418” engraved on the back of top plate, carrying an eagle sticker of German Army. It comes with body cap, with signs of use.', '這台原裝軍用M3 Bundeseigentum相機（機身號927673）曾被刊登於Paul-Henry Van Hasbroeck的《Leica In Colour》p.85頁中。根據記錄，M3 Olive產量不多於300台，在徠茲工廠相機組裝部的記錄，機身號由927671至927734也屬於M3 Olive。此機機蓋背面刻有「Bundeseigentum 12-121-5418」字樣，及貼有代表德軍的鷹標貼紙；配有機身蓋。', '100', null, null, null, null, null), ('116', 'M3 Olive “Bundeseigentum&quot; with Hektor-M 4.5/13.5cm', 'M3 Olive “Bundeseigentum&quot; with Hektor-M 4.5/13.5cm', '13', '/public/pic_content/img/a2/LOTNO_665_1.jpg,/public/pic_content/img/a2/LOTNO_665_2.jpg,/public/pic_content/img/a2/LOTNO_665_3.jpg,/public/pic_content/img/a2/LOTNO_665_4.jpg,/public/pic_content/img/a2/LOTNO_665_5.jpg', '665', '65,000 - 80,000', '', null, null, null, null, 'Rare and fully original camera from a small group of olive finished Leica M3 cameras, prepared for German Army (Bundeswehr). Total production of olive M3 cameras was less than 300 units, between 1957 and 1968. The vulcanite complete without chip missing. There is a Leitz Wetzlar “L” repair seal in the bayonet flange. The shutter crate engraved “927741”. The camera comes complete with matching Hektor-M 4.5/13.5cm (#1509122) in Leica M mount. The camera has been authenticated by Lars Netopil (Wetzlar). The set completes with body cap, both lens caps and leather case for lens.', '這台軍用M3相機根據記錄是在1957至1968年間生產，總產量不多於300台。此機見原裝「L」封條，機背刻有「Bundeseigentum 12-121-5418」字樣，連同相配的Hektor-M 4.5/13.5cm鏡頭（#1509122），鏡身有「Bundeseigentum 12-120-1625」的刻字。套機已經過Lars Netopil (Wetzlar)鑑定；連同機身蓋、鏡頭前後蓋、鏡頭配有皮套。', '100', null, null, null, null, null), ('117', 'M1 Olive “Bundeseigentum” with Hektor-M 4.5/135m', 'M1 Olive “Bundeseigentum” with Hektor-M 4.5/135m', '13', '/public/pic_content/img/a2/LOTNO_666_1.jpg,/public/pic_content/img/a2/LOTNO_666_2.jpg,/public/pic_content/img/a2/LOTNO_666_3.jpg,/public/pic_content/img/a2/LOTNO_666_4.jpg,/public/pic_content/img/a2/LOTNO_666_5.jpg', '666', '50,000 - 80,000', '', null, null, null, null, 'Only 208 units M1 Olive finishing in three batches were produced for Germany Army and this was from the first production batch. It carries the top plate like MP, “Bundeseigentum 12-129-7739” and “5+13.5” was engraved . It carries an eagle sticker of German Army. It comes with a Hektor 4.5/135mm lens (#1718738) with engraving “Bundeseigentum 12-120-1625”.', '西德軍用相機，屬於1960年生產的第一批次。機身上刻有「Bundeseigentum 12-129-7739」字樣，機身正面取景框外型設計與 M3 一樣凸出，機頂刻有「5+13.5」字樣，機底有代表德軍的鷹標貼紙。配搭銀色Hektor 4.5/135mm鏡頭（#1718738），鏡頭上刻有「Bundeseigentum 12-120-1265」字樣', '100', null, null, null, null, null), ('118', 'M1 Olive “Bundeseigentum” with Elmar-M 2.8/50mm', 'M1 Olive “Bundeseigentum” with Elmar-M 2.8/50mm', '13', '/public/pic_content/img/a2/LOTNO_667_1.jpg,/public/pic_content/img/a2/LOTNO_667_2.jpg,/public/pic_content/img/a2/LOTNO_667_3.jpg,/public/pic_content/img/a2/LOTNO_667_4.jpg,/public/pic_content/img/a2/LOTNO_667_5.jpg', '667', '60,000 - 100,000', '', null, null, null, null, 'Only 208 units M1 Olive finishing in three batches were produced for Germany Army. Number 980451-980500; 1035926-1036000 and 1098101-1098183, this camera belongs to the third production batch, with “Bundeseigentum” engraved. An Elmar 2.8/50mm lens (#1723544) is included, also a front lens cap.', 'M1 Olive是徠卡為德軍生產的相機，總產量只有208台，共有三個批次，此機來自1964年第三批次所生產的83台（編號1098101-1098183）。此機背上刻有「Bundeseigentum」字樣，配有Elmar 2.8/50mm鏡頭（#1723544）及鏡頭前蓋。', '100', null, null, null, null, null), ('119', 'Summaron 2.8/35mm Chrome 3-Crowns “Swedish Army”', 'Summaron 2.8/35mm Chrome 3-Crowns “Swedish Army”', '13', '/public/pic_content/img/a2/LOTNO_668_1.jpg,/public/pic_content/img/a2/LOTNO_668_2.jpg,/public/pic_content/img/a2/LOTNO_668_3.jpg,/public/pic_content/img/a2/LOTNO_668_4.jpg,/public/pic_content/img/a2/LOTNO_668_5.jpg', '668', '20,000 - 28,000', '', null, null, null, null, 'Here is an original Summaron 2.8/35mm lens for IIIg black, engraved with three crowns, for the Swedish army. It comes with keeper and front lens cap, not often to be seen.', '此特別版本的Summaron 2.8/35mm由瑞典皇家軍隊向徠卡訂製，鏡身刻有三皇冠，為黑漆IIIg配備的版本。連同鏡頭前蓋及膠盅，市面少有。', '100', null, null, null, null, null), ('120', 'Tele-Elmar-M 4/135mm Bundeswehr', 'Tele-Elmar-M 4/135mm Bundeswehr', '13', '/public/pic_content/img/a2/LOTNO_669_1.jpg,/public/pic_content/img/a2/LOTNO_669_2.jpg,/public/pic_content/img/a2/LOTNO_669_3.jpg,/public/pic_content/img/a2/LOTNO_669_4.jpg,/public/pic_content/img/a2/LOTNO_669_5.jpg', '669', '10,000 - 16,000', '', null, null, null, null, 'Here is an early model of Tele-Elmar-M 4/135 mm lens, with wide and knurled focusing ring. Sticker on barrel showed the words “Bundeswehr” and “ErpSt 52”, means it is for military use. It comes with keeper, filter and front lens cap.', '此鏡為早期版本的Tele-Elmar-M 4/135mm黑色鏡頭，其調焦環為粗身的波浪紋；最特別之處為鏡頭背後的貼紙上有「Bundeswehr」及「ErpSt 52」字樣，顯示為國防軍用的版本。鏡頭配有膠盅、濾鏡及鏡頭前蓋。', '100', null, null, null, null, null), ('121', 'M7 50 Jahre M-System Titanium Set', 'M7 50 Jahre M-System Titanium Set', '13', '/public/pic_content/img/a2/LOTNO_670_1.jpg,/public/pic_content/img/a2/LOTNO_670_2.jpg,/public/pic_content/img/a2/LOTNO_670_3.jpg,/public/pic_content/img/a2/LOTNO_670_4.jpg,/public/pic_content/img/a2/LOTNO_670_5.jpg', '670', '680,000 - 1,000,000', '', null, null, null, null, 'In order to commemorate the 50 years since the LEICA M system was first introduced with the LEICA M3, Leica had introduced a very nice M7 Titanium set with 3 lenses in 2004. Only 50 sets were made, serial number from 3000000 to 3000050 and Leica M creation years as commemorative number from 1954 to 2004 . Unlike previous products were only covered with a titanium outer finish, this set is made of solid titanium in all metal parts. Titanium is corrosion resistant, hard and relatively light weight which makes the set more valuable. The camera top plate has engraving “1954-2004 50 Jahre Leica M System” and commemorative number “1989”.Three lenses included are also made of titanium , which are Summicron-M 2/28mm ASPH (1989/28), Summilux-M 1.4/50mm ASPH (1989/50) as well as an APO-Summicron-M 2/90mm ASPH (1989/90). Each carries serial number matching with the camera . The set comes with box, instruction, certificate, strap, Rimowa custom made aluminum case in titanium color, titanium hood for Summicron-M 2/28mm ASPH, front and rear caps for three lenses, also a camera body cap.', '為慶祝M系列面世50年，徠卡於2004年特意推出M7鈦金屬版三鏡頭套裝，產量只有50套，機身編號由3000000至3000050止；紀念號由1954至2004，與徠卡M相機的年份相配。有別過往的鈦金屬版只有機身外部以鈦金屬鍍膜覆蓋，此限量版套裝機身內外的所有金屬部份均採用全鈦金屬鑄造；雖然鈦金屬的鑄磨時間較長及鑄磨工具損耗較大，但取其輕身，堅硬及抗腐蝕的優點，更突顯此限量版份量十足而且倍感珍貴。機身頂部刻有“1954-2004 50 JAHRE M-SYSTEM” 及紀念號 “1989”，。套機連同三支鏡頭，每支同樣以全鈦金屬鑄造，並配同與機身紀念號相配的鏡頭編號。三支鏡頭分別是：Summicron-M 2/28mm ASPH（1989/28）、Summilux-M 1.4/50mm ASPH（1989/50）及APO-Summicron-M 2/90mm ASPH（1989/90）。套機亦連同包裝證書、由Rimowa特別配製的鈦金屬色專用存放箱、Summicron-M 2/28mm ASPH的鈦金屬遮光罩、三支鏡頭的前後蓋及相機機身蓋。', '100', null, null, null, null, null), ('122', 'M6 Platinum Sultan of Brunei Silver Jubilee Edition 1992', 'M6 Platinum Sultan of Brunei Silver Jubilee Edition 1992', '13', '/public/pic_content/img/a2/LOTNO_671_1.jpg,/public/pic_content/img/a2/LOTNO_671_2.jpg,/public/pic_content/img/a2/LOTNO_671_3.jpg,/public/pic_content/img/a2/LOTNO_671_4.jpg,/public/pic_content/img/a2/LOTNO_671_5.jpg,/public/pic_content/img/a2/LOTNO_671_6.jpg', '671', '120,000 - 180,000', '', null, null, null, null, 'To celebrate the 25th anniversary of accession the throne of Sultan of Brunei, the royal family of Brunei ordered the special limited platinum edition in total 250 sets from Leica in 1992, which was never released to market for sale. The special number of this set is HB-025, with symbol of Brunei on top plate and “Sambutan Jubli Perak” engraved at the back and “Sambutan Jubli Perak 5 OCT. 92” engraved on name ring of the lens. The camera is decorated with grey lizard skin. It comes with a Summilux-M 1.4/50mm lens (#3726413), body cap, both caps, strap, certificate, outer box and wooden presentation case.', '為慶祝汶萊國王登基25周年，汶萊皇室特別向德國徠卡訂製了M6鉑金紀念機：此款紀念機並不對外發行，只供汶萊國王饋贈特別貴賓用，限量250套。此機紀念號為HB-025，機頂刻有兩個汶萊皇室徽章，分別為黑色及紅色；機背刻有「Sambutan Jubli Perak」；而鏡頭上則刻有「Sambutan Jubli Perak 5 OCT. 92」字樣。鍍上鉑金的機身飾以灰色蜥蝪皮，設計盡顯尊貴。此機連同Summilux-M 1.4/50mm鍍鉑金鏡頭（#3726413），連機身蓋、鏡頭前後蓋、相機帶、證書、外盒及木盒。', '100', null, null, null, null, null), ('123', 'M6 Gold Sultan of Brunei Silver Jubilee Edition 1992', 'M6 Gold Sultan of Brunei Silver Jubilee Edition 1992', '13', '/public/pic_content/img/a2/LOTNO_672_1.jpg,/public/pic_content/img/a2/LOTNO_672_2.jpg,/public/pic_content/img/a2/LOTNO_672_3.jpg,/public/pic_content/img/a2/LOTNO_672_4.jpg,/public/pic_content/img/a2/LOTNO_672_5.jpg,/public/pic_content/img/a2/LOTNO_672_6.jpg,/public/pic_content/img/a2/LOTNO_672_7.jpg', '672', '120,000 - 180,000', '', null, null, null, null, 'To celebrate the 25th Anniversary of accession to the throne of Sultan of Brunei on 1st October 1992, the royal family ordered 350 sets of gold plated Leica M6 Sambutan Jubli Perak. The national totem of Sultan engraved on the top plate and “Sambutan Jubli Perak” engraved at the back. The camera is decorated with black leather embossed ornamentation. The camera comes with Summilux-M 1.4/50mm lens in gold plated (#3655761), engraved &quot;Sambutan Jubli Perak 5 Oct 92&quot;. The commemorative number is HB-303. This set comes with box and papers, wooden box, strap, body cap, rear lens cap and hood (#12586) with cap.', '1992年10月1日，汶萊蘇丹登基25週年，汶萊皇室為此向徠卡訂製了350台M6套機作為紀念。此套機是以M6為基礎，鍍上24K黃金，機頂上刻有汶萊的國徽，機背後亦有「Sambutan Jubli Perak」字樣。機身配有黑色壓花紋飾皮，盡顯高貴。此套機連一支鍍金的Summilux-M 1.4/50mm鏡頭（#3655761），鏡頭前刻有「Sambutan Jubli Perak 5 Oct 92」的字樣。套機的特別號碼為HB-303。此套機連包裝證書、木盒、相機帶、機身蓋、鏡頭後蓋及遮光罩（#12586）連蓋。', '100', null, null, null, null, null), ('124', 'R6.2 Gold “July 1, 1997 Hong Kong”', 'R6.2 Gold “July 1, 1997 Hong Kong”', '13', '/public/pic_content/img/a2/LOTNO_673_1.jpg,/public/pic_content/img/a2/LOTNO_673_2.jpg,/public/pic_content/img/a2/LOTNO_673_3.jpg,/public/pic_content/img/a2/LOTNO_673_4.jpg,/public/pic_content/img/a2/LOTNO_673_5.jpg,/public/pic_content/img/a2/LOTNO_673_6.jpg,/public/pic_content/img/a2/LOTNO_673_7.jpg', '673', '80,000 - 120,000', '', null, null, null, null, 'In 1997, Leica released 300 set of R6.2 limited edition plated in 18 caret gold for Hong Kong returning to China. The commemorate number is 118/300. “July 1, 1997 HONG KONG” and Hong Kong skyline were engraved on top plate. This set comes with Summicron-R 2/50mm lens (#3765186) and both lens caps. Also with box and papers, wooden box, body cap and strap', '徠卡為香港回歸中國推出罕見的R6.2 18K鍍金套機，量產300套。此套機有吉祥的紀念號碼118/300，機頂刻有 「July 1, 1997 HONG KONG」 和香港著名地標，相機配有Summicron-R 2/50mm（#3765186）鏡頭。此套機連同包裝證書、木盒、機身蓋、相機帶及鏡頭前後蓋。', '100', null, null, null, null, null), ('125', 'M6 Gold Dragon', 'M6 Gold Dragon', '13', '/public/pic_content/img/a2/LOTNO_674_1.jpg,/public/pic_content/img/a2/LOTNO_674_2.jpg,/public/pic_content/img/a2/LOTNO_674_3.jpg,/public/pic_content/img/a2/LOTNO_674_4.jpg,/public/pic_content/img/a2/LOTNO_674_5.jpg', '674', '40,000 - 60,000', '', null, null, null, null, 'M6 Gold Dragon Edition comes with a matching Summicron-M 2/50mm (#3700213), only 300 units were made. The camera body is decorated with red snake leather and has a dragon engraved on the top plate and the wooden box. The commemorate number is 213/300, with full packing, strap and front lens cap.', '此M6金龍機連Summicron-M 2/50mm 鏡頭（#3700213），限量300台，紀念號碼為213/300。機身配有紅色蛇皮，機頂及木盒均刻有傳統中國龍圖案。包裝證書齊全，連相機帶及鏡頭前蓋。', '100', null, null, null, null, null), ('126', 'Schneider PC Super-Angulon-R 2.8/28mm', 'Schneider PC Super-Angulon-R 2.8/28mm', '13', '/public/pic_content/img/a2/LOTNO_675_1.jpg,/public/pic_content/img/a2/LOTNO_675_2.jpg,/public/pic_content/img/a2/LOTNO_675_3.jpg,/public/pic_content/img/a2/LOTNO_675_4.jpg', '675', '15,000 - 25,000', '', null, null, null, null, 'This lens comes with both lens caps, in Leica R mount.', '此款鏡頭是徠卡採用施耐德最著名的移軸鏡頭，成象質素佳，而且便於操作。此鏡是徠卡R卡口，配有鏡頭前後蓋。', '100', null, null, null, null, null), ('127', 'Elmarit-R 2.8/24mm Prototype', 'Elmarit-R 2.8/24mm Prototype', '13', '/public/pic_content/img/a2/LOTNO_676_1.jpg,/public/pic_content/img/a2/LOTNO_676_2.jpg,/public/pic_content/img/a2/LOTNO_676_3.jpg,/public/pic_content/img/a2/LOTNO_676_4.jpg,/public/pic_content/img/a2/LOTNO_676_5.jpg', '676', '20,000 - 35,000', '', null, null, null, null, 'Here is a prototype lens of Elmarit-R 2.8/24mm; its serial number 0001344 is not in the commercial batch, very rare. It comes with front and rear lens caps.', '此鏡是Elmarit-R 2.8/24mm的原型版，鏡頭序號0001344並非在正常生產序列之中，市面罕見。此鏡連同鏡頭前後蓋。', '100', null, null, null, null, null), ('128', 'R Lens Set in Chrome', 'R Lens Set in Chrome', '13', '/public/pic_content/img/a2/LOTNO_677_1.jpg,/public/pic_content/img/a2/LOTNO_677_2.jpg,/public/pic_content/img/a2/LOTNO_677_3.jpg,/public/pic_content/img/a2/LOTNO_677_4.jpg', '677', '28,000 - 40,000', '', null, null, null, null, 'Here are two R series lenses in chrome finished; both were made in Germany in 1960’s, only small number produced. For the Elmarit-R 2.8/35mm lens (#1972094) comes with rear lens cap and hood (#12564) with hood cap; and the Summicron-R 2/50mm lens (#1999074) comes with rear lens cap, hood (#12564) with hood cap and filter adapter (#14160U).', '此對銀色版本的R系列鏡頭，均在1960年代由德國製造產，產量不多。當中Elmarit-R 2.8/35mm鏡頭(#1972094)連鏡頭後蓋及遮光罩（#12564）連蓋﹔而Summicron-R 2/50mm鏡頭(#1999074)則帶鏡頭後蓋、遮光罩（#12564）連蓋及濾鏡轉接環(#14160U)。', '100', null, null, null, null, null), ('129', 'Summilux-R 1.4/50mm Black', 'Summilux-R 1.4/50mm Black', '13', '/public/pic_content/img/a2/LOTNO_678_1.jpg,/public/pic_content/img/a2/LOTNO_678_2.jpg,/public/pic_content/img/a2/LOTNO_678_3.jpg,/public/pic_content/img/a2/LOTNO_678_4.jpg,/public/pic_content/img/a2/LOTNO_678_5.jpg,/public/pic_content/img/a2/LOTNO_678_6.jpg', '678', '18,000 - 28,000', '', null, null, null, null, 'This lens was made in Germany in E60 with ROM and built-in hood . It comes with packing, instructions, leather case and both caps.', '此鏡為德國製的Summilux-R 1.4/50mm最新款鏡頭，內置遮光罩。此鏡頭口徑為E60，帶ROM。連包裝盒、說明書、皮套及鏡頭前後蓋。', '100', null, null, null, null, null), ('130', 'APO-Macro-Elmarit-R 2.8/100mm', 'APO-Macro-Elmarit-R 2.8/100mm', '13', '/public/pic_content/img/a2/LOTNO_679_1.jpg,/public/pic_content/img/a2/LOTNO_679_2.jpg,/public/pic_content/img/a2/LOTNO_679_3.jpg,/public/pic_content/img/a2/LOTNO_679_4.jpg,/public/pic_content/img/a2/LOTNO_679_5.jpg', '679', '8,000 - 15,000', '', null, null, null, null, 'APO-Macro-Elmarit-R 2.8/100mm lens was launched in 1987; but its serial number indicating that this lens was made in 1986, also with special number “EE24” engraved. It comes with both lens caps and built-in hood.', '此款鏡頭於1987年的Photokina上推出，但根據鏡頭號碼顯示，此鏡的生產年份是1986年，比同款鏡頭的發佈年份更早；而且鏡身上刻有特別編號「EE24」，有可能屬於小批量的試行版鏡頭。此鏡配有前後蓋及內置遮光罩。', '100', null, null, null, null, null), ('131', 'Elmarit-R 2.8/180mm Prototype', 'Elmarit-R 2.8/180mm Prototype', '13', '/public/pic_content/img/a2/LOTNO_680_1.jpg,/public/pic_content/img/a2/LOTNO_680_2.jpg,/public/pic_content/img/a2/LOTNO_680_3.jpg,/public/pic_content/img/a2/LOTNO_680_4.jpg,/public/pic_content/img/a2/LOTNO_680_5.jpg', '680', '10,000 - 18,000', '', null, null, null, null, 'Here is a prototype lens of the second version of Elmarit-R 2.8/180mm. Its serial number doesn&#039;t belong to the official batch and it has  a shorter focusing ring, very special. It comes with built-in hood and both lens caps.', '徠卡Elmarit-R 2.8/180mm第二代的原型鏡，序號並非在正常序列之中，而且鏡筒調焦環比正常版短。鏡頭連內置遮光罩及鏡頭前後蓋。', '100', null, null, null, null, null), ('132', 'Elmar-R 4/180mm Prototype Black', 'Elmar-R 4/180mm Prototype Black', '13', '/public/pic_content/img/a2/LOTNO_681_1.jpg,/public/pic_content/img/a2/LOTNO_681_2.jpg,/public/pic_content/img/a2/LOTNO_681_3.jpg,/public/pic_content/img/a2/LOTNO_681_4.jpg,/public/pic_content/img/a2/LOTNO_681_5.jpg', '681', '18,000 - 28,000', '', null, null, null, null, 'Here is an Elmar-R 4/180mm lens in black, with special serial number, probably a prototype lens or pre-series. It comes with built-in lens hood and rear cap.', '此Elmar-R 4/180mm黑色鏡頭，其生產序號不在正常之列，估計為正式量產前的原型版或試行版。配備內置遮光罩及鏡頭後蓋。', '100', null, null, null, null, null), ('133', 'Leicavit MP Black Paint', 'Leicavit MP Black Paint', '13', '/public/pic_content/img/a2/LOTNO_682_1.jpg,/public/pic_content/img/a2/LOTNO_682_2.jpg,/public/pic_content/img/a2/LOTNO_682_3.jpg', '682', '80,000 - 120,000', '', null, null, null, null, 'This is an original Leicavit for early MP and M2 in black paint finished, which is an extremely rare piece of original accessory for MP, comes with the original Leitz box.', '非常罕見的原裝MP黑漆過片器SYOOM-M，連同當時徠茲包裝盒。', '100', null, null, null, null, null), ('134', 'Leica Special Edition Pocket Watch 1913-1993', 'Leica Special Edition Pocket Watch 1913-1993', '13', '/public/pic_content/img/a2/LOTNO_683_1.jpg,/public/pic_content/img/a2/LOTNO_683_2.jpg,/public/pic_content/img/a2/LOTNO_683_3.jpg', '683', '3,000 - 5,000', '', null, null, null, null, 'To celebrate Leica&#039;s 80th Anniversary (1913-1993), a limited Leica Special Edition Pocket Watch was made. It comes with original box and guarantee card.', '此精緻非常的鍍金陀錶是於1993年為慶祝徠卡80週年（1913-1993）所鑄造，限量發售。連原包裝盒並配有鍍金錶鏈及保養卡。', '100', null, null, null, null, null), ('135', '21/24/28mm Viewfinder Chrome', '21/24/28mm Viewfinder Chrome', '13', '/public/pic_content/img/a2/LOTNO_684_1.jpg,/public/pic_content/img/a2/LOTNO_684_2.jpg', '684', '2,000 - 3,000', '', null, null, null, null, 'Here is a zoom finder in chrome finished with 21/24/28mm. It comes with a box and leather case.', '這個銀色的取景器可以三段變焦，提供21/24/28mm焦距的選擇，使用方便。配有包裝盒及皮筒。', '100', null, null, null, null, null), ('136', '28mm Viewfinder Chrome', '28mm Viewfinder Chrome', '13', '/public/pic_content/img/a2/LOTNO_685_1.jpg,/public/pic_content/img/a2/LOTNO_685_2.jpg,/public/pic_content/img/a2/LOTNO_685_3.jpg', '685', '1,000 - 2,000', '', null, null, null, null, 'Here is an Leica 28mm viewfinder in code 12007, SLOOZ, in chrome finished.', '這個是徠卡28mm銀色取景器，產品編號12007，又名SLOOZ。', '100', null, null, null, null, null), ('137', 'Leica Camera Hard Case MBROO', 'Leica Camera Hard Case MBROO', '13', '/public/pic_content/img/a2/LOTNO_686_1.jpg,/public/pic_content/img/a2/LOTNO_686_2.jpg', '686', '3,000 - 5,000', '', null, null, null, null, 'Leica camera hard case MBROO manufactured in 1955, made of metal with rubber seal which makes it impact-proof and water-resistant, designed for screw mount camera.', '徠卡於1955年生產相機套MBROO，以堅硬的金屬製成，配上經典的黑色水波紋，防撞抗水，適用於螺絲口相機。', '100', null, null, null, null, null), ('138', '3 Pieces Of 8582 Pu-er Tea Cake, 1980’s', '八十年代 8582厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_201_1.jpg,/public/pic_content/img/a3/LOTNO_201_2.jpg', '201', '60,000 - 100,000', '', null, null, null, null, '', '數量：共三片<br />\r\n總重量：約1,059g<br />\r\n年份：80年代中期<br />\r\n<br />\r\n80年代中期的厚紙8582，包裝較完整，倉儲良好，茶餅外形圓厚端正，撤面條索肥壯，鬆緊適中，茶質極佳，是中期茶中的精品。此茶包裝為「長撇雲」版本；採用五至八級粗壯新老茶菁拼配緊壓，當中三、四級為面；五至八級為裡茶尤以七、八級為主，是收藏市場的搶手貨。<br />\r\n有關資料可參閱《深邃的七子世界》第243至245頁。', '100', null, null, null, null, null), ('139', 'A Tong Of 8582 Pu-er Tea Cake In Thick Wrapping, 1980s', '八十年代 8582厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_202_1.jpg,/public/pic_content/img/a3/LOTNO_202_2.jpg,/public/pic_content/img/a3/LOTNO_202_3.jpg', '202', '300,000 - 400,000', '', null, null, null, null, '', '數量：一筒七片<br />\r\n總重量：約2,474g<br />\r\n年份：80年代<br />\r\n<br />\r\n此茶為80年代中期的8582，厚紙包裝，倉存良好，條件上佳，茶質出眾，加上餅形圓厚端正，側面條索肥壯，鬆緊適中，可說是80年代所有生茶中最有印級茶風味的茶，可稱為中期茶中的精品。此茶採用五至八級粗壯新老茶菁拼配緊壓，當中三、四級為面；五至八級為裡，尤以七、八級為主，乃收藏市場之搶手貨。', '100', null, null, null, null, null), ('140', '2 Pieces Of 8582 Pu-er Tea Cake From The First Batch, 1980’s', '八十年代 首批大葉青餅', '14', '/public/pic_content/img/a3/LOTNO_203_1.jpg,/public/pic_content/img/a3/LOTNO_203_2.jpg,/public/pic_content/img/a3/LOTNO_203_3.jpg,/public/pic_content/img/a3/LOTNO_203_4.jpg,/public/pic_content/img/a3/LOTNO_203_5.jpg', '203', '100,000 - 150,000', '', null, null, null, null, '', '數量：兩片<br />\r\n總重量：約667g<br />\r\n年份：80年代初<br />\r\n<br />\r\n本拍品為第一批大葉青餅8582，以厚紙包裝，見內票，茶質極佳，是收藏市場的搶手貨。這款茶的特色為表面茶用三、四級茶菁，裡面用七、八級茶，因用的茶料粗枝大葉，又被叫大葉青餅，餅身也較大較厚。經三十多年的陳化，其特殊的口感令人讚嘆。', '100', null, null, null, null, null), ('141', '8 Pieces Of Little Green Mark Pu-er Tea Cake, 1970-1980&#039;s', '七十至八十年代 73普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_204_1.jpg,/public/pic_content/img/a3/LOTNO_204_2.jpg', '204', '', '350,000', null, null, null, null, '', '數量：共八片<br />\n總重量：約2,709g<br />\n年份：70年代末至80年代初<br />\n<br />\n一組八片的大口中73青餅(又名小綠印)，一直為藏家所珍藏，包裝完好附內票。<br />\n73青餅為早年的7542普洱茶餅，被譽為八十年代勐海青餅的「三大名牌」。經近四十年的陳化，滿口甜醇餘韻豐滿，被普洱茶收藏界視為珍品；由於需求者眾，故近年價格也愈見高昂。<br />\n有關資料可參閱《深邃的七子世界》第197至203頁。', '100', null, null, null, null, null), ('142', '5 Pieces Of 7572 Pu-er Tea Cake From The First Batch, early 1980’s', '八十年代 首批7572普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_205_1.jpg,/public/pic_content/img/a3/LOTNO_205_2.jpg', '205', '', '460,000', null, null, null, null, '', '數量：5片<br />\n總重量：約1,680g<br />\n年份：80年代初<br />\n<br />\n一組五片的首批7572製於八十年代前後，是小口中版本，為尖出內飛及長撇雲，包裝紙完整，見內票；加上倉儲良好，極為難得罕見。它是由香港利安茶行向雲南省茶司訂購，勐海茶廠製作，是絕無僅有的一批7572普洱青餅，彌足珍貴；其後的7572皆為熟茶。', '100', null, null, null, null, null), ('143', '2 Tongs Of 88 Pu-er Tea Cake, 1980’s', '88青普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_206_1.jpg,/public/pic_content/img/a3/LOTNO_206_2.jpg,/public/pic_content/img/a3/LOTNO_206_3.jpg', '206', '', '550,000', null, null, null, null, '', '數量：兩筒共十四片<br />\n總重量：約4,918g<br />\n年份：約80年代末<br />\n<br />\n此組聞名中外的88青接近乾倉倉儲，竹箬包裝完整，品相上佳；生產在1988至1992年間，以經典生茶7542配方製作，用中壯葉為主體，嫩芽鋪面，間以少量粗老葉製成。作為七十年代雲南七子餅茶出產的一款標杆茶，升值潛力與日俱增，一直深受藏家追捧。<br />', '100', null, null, null, null, null), ('144', '4 Pieces Of Yellow Mark Pu-er Tea Cake, 1970’s', '七十年代 大黃印圓茶', '14', '/public/pic_content/img/a3/LOTNO_207_1.jpg,/public/pic_content/img/a3/LOTNO_207_2.jpg,/public/pic_content/img/a3/LOTNO_207_3.jpg,/public/pic_content/img/a3/LOTNO_207_4.jpg', '207', '', '180,000', null, null, null, null, '', '數量：共四片<br />\n總重量：約1,368g<br />\n年份：70年代初<br />\n<br />\n一組四片的黃字黃印均附內票，倉儲極乾，湯色金黃，十分難得。<br />\n黃印圓茶是由勐海茶廠以易武正山為料所生產，被稱之為「現代拼配茶菁的普洱茶品始祖」。經過四十多年陳化，茶餅毫頭已轉化為金黃色，回甘良好，沖泡後茶氣強烈，令人心動。<br />\n有關資料可參閱《深邃的七子世界》第121頁。', '100', null, null, null, null, null), ('145', 'A Tong And A Piece Of Blue Mark Pu-er Tea Cake, 1970’s', '七十年代 大藍印圓茶', '14', '/public/pic_content/img/a3/LOTNO_208_1.jpg,/public/pic_content/img/a3/LOTNO_208_2.jpg,/public/pic_content/img/a3/LOTNO_208_3.jpg', '208', '300,000 - 450,000', '', null, null, null, null, '', '數量：一筒七片加一片<br />\n總重量：約2,789g<br />\n年份：70年代中期<br />\n<br />\n此拍品七子大藍印，倉儲良好，品相上佳。整組拍品餅面比其他七子餅較闊，使用的茶葉亦較粗，如果撥開餅身甚至可以在葉底中找到完整的大葉，這在其它的七子餅中難得一見。此茶是勐海茶廠最後一次按古董茶的原料做的茶品，就品飲及收藏而言，相信是別具雅趣的選擇。<br />\n有關資料可參閱《深邃的七子世界》第181頁。', '100', null, null, null, null, null), ('146', 'A Tong Of 7452 Pu-er Tea Cake, 1980&#039;s', '七十年代  7452普洱圓茶', '14', '/public/pic_content/img/a3/LOTNO_209_1.jpg,/public/pic_content/img/a3/LOTNO_209_2.jpg', '209', '', '140,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,455g<br />\n年份：70年代<br />\n工序：熟茶<br />\n<br />\n本拍品堪稱普洱「熟茶王」，為勐海茶廠於七十年代的出品，採用西雙版納勐海茶區中上等茶菁製作工序而成，是普洱茶市場的指標產品，也是勐海茶廠最受歡迎的產品，至今有超過四十年以上陳期，屬普洱茶收藏市場的明星茶。', '100', null, null, null, null, null), ('147', '5 Pieces Of 7542 Pu-er Tea Cake, 1980’s', '八十年代 7542厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_210_1.jpg,/public/pic_content/img/a3/LOTNO_210_2.jpg', '210', '120,000 - 180,000', '', null, null, null, null, '', '數量：五片<br />\n總重量：約1,512g<br />\n年份：80年代<br />\n<br />\n此藏品為八十年代初的7542普洱青餅，曾被開封；它是中國茶葉領導品牌大益茶業出產量最高的青餅，以肥壯茶菁為裡，幼嫩芽葉撒面，研配得當，面茶色澤烏潤顯芽毫，心茶肥壯。該茶品香氣純正持久，有荷花香，滋味濃厚回甘好，湯色黃亮，葉底勻齊；相當討喜。', '100', null, null, null, null, null), ('148', '14 Pieces Of 8653 Pu-er Tea Cake, 1990&#039;s', '九十年代 下關8653中茶青餅(將軍餅)', '14', '/public/pic_content/img/a3/LOTNO_211_1.jpg,/public/pic_content/img/a3/LOTNO_211_2.jpg,/public/pic_content/img/a3/LOTNO_211_3.jpg', '211', '', '170,000', null, null, null, null, '', '數量：共十四片<br />\n總重量：約4,693g<br />\n倉儲：乾倉<br />\n年份：90年代初<br />\n<br />\n一組14片無紙包裝的8653下關茶，又稱中茶繁體8653將軍餅，其茶餅乾倉，餅面見布痕，而且餅形完整份量十足，為中期陳茶的佳品。此茶以5級茶菁為主要拼配原料，由下關茶廠用雲南大葉種曬青毛茶生產壓制的普洱茶。', '100', null, null, null, null, null), ('149', 'Two Tongs Of 8653 Pu-er Tea Cake In Traditional Chinese Charaters, 1980’s', '八十年代 下關8653繁體字中茶青餅', '14', '/public/pic_content/img/a3/LOTNO_212_1.jpg,/public/pic_content/img/a3/LOTNO_212_2.jpg', '212', '', '300,000', null, null, null, null, '', '數量：兩筒共十四片<br />\n總重量：約4,816g<br />\n倉儲：較乾倉<br />\n年份：80年代<br />\n<br />\n一組兩筒共十四片的8653茶餅產於80年代中，為繁體版本，大票上寫「8653-8806」。其倉儲較乾的程度在8653中少見；茶餅連竹箬包裝。<br />\n此批茶餅拼配以臨滄南部茶菁居多，與中茶簡體字口感相若。有關資料可參閱《深邃的七子世界》第378頁。', '100', null, null, null, null, null), ('150', '8 Pieces Of 8653 Pu-er Tea Cake In Traditional Chinese Charaters, 1980&#039;s', '八十年代 首批下關8653中茶繁體字青餅', '14', '/public/pic_content/img/a3/LOTNO_213_1.jpg,/public/pic_content/img/a3/LOTNO_213_2.jpg,/public/pic_content/img/a3/LOTNO_213_3.jpg', '213', '', '140,000', null, null, null, null, '', '數量：八片<br />\n總重量：約2,730g<br />\n年份：80年代中期<br />\n<br />\n此拍品是首批8653繁體字版，餅型較寬，以5級茶菁為主要拼配原料。普洱老茶在歲月的沉澱之中越陳越香，其層次豐富的香氣、醇厚順滑的口感和顯著的保健功效，使其風靡收藏界和高端品飲市場。<br />\n8653被譽為下關茶廠最馳名鐵餅，因其壓制緊實，後期陳化緩慢卻優質而獲得不少資深茶友的喜愛和追捧。<br />\n有關資料可參閱《深邃的七子世界》第373頁。', '100', null, null, null, null, null), ('151', 'A Piece Of Chung Cha Brand Pu-er Tea Cake with Simplified Chinese Character (Nineteen Words), 1970’s', '七十年代 下關簡體字中茶青餅(19字版)', '14', '/public/pic_content/img/a3/LOTNO_214_1.jpg,/public/pic_content/img/a3/LOTNO_214_2.jpg,/public/pic_content/img/a3/LOTNO_214_3.jpg,/public/pic_content/img/a3/LOTNO_214_4.jpg', '214', '', '70,000', null, null, null, null, '', '數量：一片<br />\n總重量：約312g<br />\n年份：70年代<br />\n <br />\n本藏品為中茶牌最早期壓製的鐵餅，特別之處是本藏品包裝底部的「中國土產畜產進出口公司雲南省茶葉分公司」為十九個字的簡體版本，產量相對較少。<br />\n茶餅背後有凹窩；茶韻中的梅樟香韻強烈，茶湯飽滿，生津度高。<br />\n有關資料可參閱《深邃的七子世界》第372頁。', '100', null, null, null, null, null), ('152', 'A Piece Of Discus Pu-er Tea Cake, 1950’s - 1960’s', '五十至六十年代 普洱鐵餅', '14', '/public/pic_content/img/a3/LOTNO_215_1.jpg,/public/pic_content/img/a3/LOTNO_215_2.jpg,/public/pic_content/img/a3/LOTNO_215_3.jpg', '215', '40,000 - 70,000', '', null, null, null, null, '', '數量：一片<br />\n總重量：約321g<br />\n年份：50至60年代<br />\n工序：生茶<br />\n<br />\n此拍品餅型扁平，壓的非常緊實，後有乳釘，餅形狀態良好，以自然倉儲存。<br />\n由於茶葉壓制成鐵餅後，內部的條索空間緊密，與空氣的接觸面積非常小，茶質能更好的保存下來。<br />\n<br />', '100', null, null, null, null, null), ('153', 'A Piece Of Snow Mark Pu-er Tea Cake, early 1980&#039;s', '八十年代初 雪印青餅', '14', '/public/pic_content/img/a3/LOTNO_216_1.jpg,/public/pic_content/img/a3/LOTNO_216_2.jpg', '216', '', '65,000', null, null, null, null, '', '數量：一片<br />\n總重量：320g<br />\n倉儲：乾倉<br />\n年份：80年代初<br />\n<br />\n此茶是勐海茶廠出產的七子餅7532，它以「小口中」厚棉紙包裝，「茶」字為粉綠色手工蓋印，乾倉儲存，品相良好，內飛細字尖出。此茶是其出品中最為細嫩的茶品，餅面細芽多且芽白如雪，故有名「雪印」，產量不多。茶材以3至6級茶菁原料混拼，面裡一致。<br />\n貴為藏量不多而品相極佳的普洱茶收藏界精品，其香氣口感幽雅，表現細膩，留韻持久；帶油樟香的滋味甘滑醇厚，入口即滑入丹田，陳化優秀，極受市場歡迎。<br />\n<br />', '100', null, null, null, null, null), ('154', 'A Tong Of Snow Mark Pu-er Tea Cake, early 1980&#039;s', '八十年代初 雪印青餅', '14', '/public/pic_content/img/a3/LOTNO_217_1.jpg,/public/pic_content/img/a3/LOTNO_217_2.jpg,/public/pic_content/img/a3/LOTNO_217_3.JPG,/public/pic_content/img/a3/LOTNO_217_4.JPG', '217', '380,000 - 550,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,340g<br />\n年份：80年代初<br />\n<br />\n此茶連同原廠牛皮紙的「紙仝」包裝，它以「小口中」厚棉紙包裝，「茶」字為粉綠色手工蓋印，內飛細字尖出，小內票，紙筒包。雪印是勐海茶廠出產包裝承襲了七子黃印的七子餅茶，以三至六級茶菁原料混拼，是其出品中最為細嫩的茶品，面裡一致，產量不多，深具收藏價值。餅面細芽多且芽白如雪，故有名「雪印」，也是50年代印級茶品後繼者。貴為藏量不多而品相極佳的普洱茶收藏界精品，其香氣口感幽雅，表現細膩，留韻持久；帶油樟香的滋味甘滑醇厚，入口即滑入丹田，陳化優秀，極受市場歡迎。<br />\n<br />', '100', null, null, null, null, null), ('155', '2 Pieces Of Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 藍印青餅', '14', '/public/pic_content/img/a3/LOTNO_218_1.jpg,/public/pic_content/img/a3/LOTNO_218_2.JPG', '218', '', '630,000', null, null, null, null, '', '數量：兩片<br />\n重量：約350g及342g<br />\n年份：50年代<br />\n<br />\n一組兩片比較早期的五十年代藍印青餅重量十足，其中一片的品相更可堪稱完美。超過半世紀之多的陳期，令此茶喉韻強烈持久，陳香與樟香相互交織，茶韻風格別樹一幟。表面可見「綠字八中一茶」商標設計。此茶餅採用勐海茶青為原料，餅面烏亮潤澤，條索粗壯緊實，為收藏佳品。<br />', '100', null, null, null, null, null), ('156', 'A Tong Of Yellow Mark Pu-er Tea Cake, 1960’s', '約六十年代末 認真配方黃印青餅', '14', '/public/pic_content/img/a3/LOTNO_219_1.jpg,/public/pic_content/img/a3/LOTNO_219_2.jpg,/public/pic_content/img/a3/LOTNO_219_3.jpg', '219', '', '800,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,260g<br />\n年份：60年代末至70年代初<br />\n<br />\n此拍品為原整牛皮紙包裝黃印七子餅，保存完整，內飛有「認真配方」四字。其陳樟香充滿了陳茶之氣韻，三十多年陳期的自然陳化老普洱茶，高價和迷人的地方就在於此。<br />\n黃印七子餅產於五十年代末至七十年代中期，是雲南七子餅的一個里程碑，同時也是現代拼配普洱茶工藝的始祖。「綠字黃印」比「黃字黃印」生產年份更早，當中分為此拍品之「認真配方」﹑「適度發酵」及「加重萌芽」三種，以「認真配方」茶質最為優良且年份最早；被視為小黃印中的佼佼者。<br />\n<br />', '100', null, null, null, null, null), ('157', '7 Pieces Of Yellow Mark Pu-er Tea Cake, late 1960’s', '六十年代 八中黃印青餅', '14', '/public/pic_content/img/a3/LOTNO_220_1.jpg,/public/pic_content/img/a3/LOTNO_220_2.jpg,/public/pic_content/img/a3/LOTNO_220_3.jpg,/public/pic_content/img/a3/LOTNO_220_4.jpg', '220', '', '880,000', null, null, null, null, '', '數量：一筒七片 <br />\n總重量：2,390g<br />\n年份；60年代末<br />\n<br />\n此組「八中黃印」採用原裝牛皮紙包裝紙筒，其中一片包裝雖有小缺口，但整體包裝品相良好。<br />\n八中黃印乃早期印級茶過渡七子餅茶交替期的產物，其特別之處於使用純八中款式內飛，下半部份沒有勐海公司字樣，屬於與無紙紅印及藍印鐵餅同款的內飛，亦沒有後期七子茶之內票。<br />\n有關「七子八中黃印」資料可參閱《深邃的七子世界》第120頁。', '100', null, null, null, null, null), ('158', '3 Pieces Of Different Grade Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 藍印青餅大三元 (甲級藍印﹑乙級藍印及大字藍印)', '14', '/public/pic_content/img/a3/LOTNO_221_1.jpg,/public/pic_content/img/a3/LOTNO_221_2.jpg,/public/pic_content/img/a3/LOTNO_221_3.jpg,/public/pic_content/img/a3/LOTNO_221_4.jpg,/public/pic_content/img/a3/LOTNO_221_5.jpg,/public/pic_content/img/a3/LOTNO_221_6.jpg', '221', '', '1,300,000', null, null, null, null, '', '數量：共三片<br />\n重量：甲級藍印約343g、乙級藍印約339g、大字藍印約328g<br />\n總重量：約1,010g<br />\n年份：50年代<br />\n<br />\n此一套三片的藏品是五十年代與紅印並駕齊名的印級皇者：甲級藍印﹑乙級藍印及藍印青餅。<br />\n三片茶餅的重量十足，平均重達336克，倉存狀態良好，「甲級」及「乙級」字樣清晰可見，狀態極佳，茶葉金黃。<br />\n藍印普洱茶是勐海茶廠五十年代生產普洱茶餅，屬經典的印級茶，餅茶質優秀，經過半世紀的陳化，散發出無與倫比的陳年老韻，深受茶界好評。', '100', null, null, null, null, null), ('159', 'A Tong Of  &#039;Gam San Lau&#039;  Red Mark Pu-er Tea Cake,1950s', '五十年代 金山樓舊藏 無紙紅印', '14', '/public/pic_content/img/a3/LOTNO_222_1.jpg,/public/pic_content/img/a3/LOTNO_222_2.jpg,/public/pic_content/img/a3/LOTNO_222_3.jpg,/public/pic_content/img/a3/LOTNO_222_4.jpg,/public/pic_content/img/a3/LOTNO_222_5.jpg', '222', '', '2,050,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,235g<br />\n年份：50年代<br />\n<br />\n此藏品出自香港著名大酒樓「金山樓」，至今陳化逾六十年，散發著迷人陳韻，倉儲優良的無紙紅印品質絕對不輸甲級紅印。茶餅圓渾、條索粗壯鮮明，餅面較紅，並採用軟竹筒作包裝。筒上題「金山樓舊藏」、「無紙紅印」字樣，包裝完整，見「八中內飛」半藏於餅身內，品相較完好。<br />\n香港著名大酒樓「金山樓」於八十年代結業，其茶倉存放陳年普洱超過數十年，其牆面、地下都瀰漫著陳舊的普洱茶茶氣，形成一個理想存放普洱茶的陳化環境。作為愛茶之人，無不嚮往這酒樓的倉庫所儲藏的老茶。<br />\n紅印是戰火過後第一年春所採的茶，芽頭小而內含物豐富，原料採用勐海茶區野放茶菁壯葉，茶菁條索粗壯飽滿，代表著勐海茶廠技術人員最謹慎的操作水平和製茶態度。<br />', '100', null, null, null, null, null), ('160', '13 Pieces of Pu-er Tea Cake, from 1950s', '五十年代 普洱印級一條龍', '14', '/public/pic_content/img/a3/LOTNO_223_1.jpg,/public/pic_content/img/a3/LOTNO_223_2.jpg,/public/pic_content/img/a3/LOTNO_223_3.jpg,/public/pic_content/img/a3/LOTNO_223_4.jpg,/public/pic_content/img/a3/LOTNO_223_5.jpg,/public/pic_content/img/a3/LOTNO_223_6.jpg,/public/pic_content/img/a3/LOTNO_223_7.jpg,/public/pic_content/img/a3/LOTNO_223_8.jpg,/public/pic_content/img/a3/LOTNO_223_9.jpg,/public/pic_content/img/a3/LOTNO_223_10.jpg,/public/pic_content/img/a3/LOTNO_223_11.jpg,/public/pic_content/img/a3/LOTNO_223_12.jpg,/public/pic_content/img/a3/LOTNO_223_13.jpg,/public/pic_content/img/a3/LOTNO_223_14.jpg', '223', '', '2,800,000', null, null, null, null, '', '數量：共十三片<br />\r\n早期紅印：重約318g<br />\r\n後期紅印：重約329g<br />\r\n紅印鐵餅：重約298g<br />\r\n無紙紅印：重約324g<br />\r\n甲級藍印：重約331g<br />\r\n乙級藍印：重約337g<br />\r\n福祿貢茶：重約343g<br />\r\n思普貢茶：重約317g<br />\r\n圓茶鐵餅：重約331g<br />\r\n小字綠印：重約343g<br />\r\n大字綠印：重約317g<br />\r\n無紙紅印：重約317g<br />\r\n黃印圓茶：重約333g<br />\r\n年份：1950至70年代<br />\r\n<br />\r\n本拍品為五十年代起至文革前的十三餅印級普洱茶，分別以早期紅印、後期紅印、紅印鐵餅、無紙紅印、甲級藍印等舉足輕重的印級普洱圓茶為首，直至七十年代的黃印圓茶止。<br />\r\n整組拍品當年由鄧時海先生選出，大部份皆與資料圖中照片的茶餅為同一餅；十三片拍品的倉儲條件皆較良好，故茶品口感十足，茶氣強勁並帶回甘。<br />\r\n在文革以前，普洱茶都以古法工序製茶，並以蕎木茶菁為料，屬單一茶菁的生茶茶品，品質甚佳。鑑於年代久遠，品相上佳的印級茶在市場上已經愈見稀少，價格亦每每攀升。<br />\r\n整組拍品完整收集為「印字級一條龍」，其茶品質與歷史價值，使它成為了普洱茶收藏的曠世珍寶。把普洱茶的演變進程同時收納囊中，了解茶界傳奇的發展，更顯彌足珍貴，為普洱茶愛好者帶來妙不可言的心神體會。', '100', null, null, null, null, null), ('161', 'A Piece Of Red Mark Pu-er Tea Cake, 1950&#039;s', '五十年代 早期大字紅印青餅', '14', '/public/pic_content/img/a3/LOTNO_224_1.jpg,/public/pic_content/img/a3/LOTNO_224_2.jpg,/public/pic_content/img/a3/LOTNO_224_3.jpg,/public/pic_content/img/a3/LOTNO_224_4.jpg', '224', '', '580,000', null, null, null, null, '', '數量：一片<br />\n總重量：約360g<br />\n年份：50年代<br />\n<br />\n此拍品為五十年代生產的大字紅印青餅，歷經六十多年，仍保存良好，倉儲乾淨，品相完整，其包裝紙幾乎完整無缺，重量亦接近出廠時的狀態。拍品屬於早期粗體版本，因其陳化品質卓越，傳世稀少，被普洱藏家譽為為印級茶之首。此片紅印質量在市面上可尋的紅印之中，可說是佼佼者。<br />\n<br />\n紅印原料採用勐海茶區野放茶菁壯葉，茶氣乾淨而足。餅身茶葉烏潤有光，茶菁條索粗壯飽滿緊實，雖已陳放半個多世紀，葉底依舊具活性，生聞已感覺其韻氣醇而樟香味濃烈。沖泡後，茶湯明亮栗紅，清澈見底無渾濁。茶水醇厚馥郁，富層次，生津極致、喉韻持久，濃郁芳香而不霸道，令人舌底鳴泉，回味無窮。', '100', null, null, null, null, null), ('162', 'A Piece Of Extremely Rare’ Jingchanghao’ Pu-er Tea Cake, 1940&#039;s', '百年 敬昌號圓茶', '14', '/public/pic_content/img/a3/LOTNO_225_1.jpg,/public/pic_content/img/a3/LOTNO_225_2.jpg,/public/pic_content/img/a3/LOTNO_225_3.jpg,/public/pic_content/img/a3/LOTNO_225_4.jpg', '225', '450,000 - 650,000', '', null, null, null, null, '', '數量：一片<br />\n總重量：約328g<br />\n年份：40年代<br />\n<br />\n這片敬昌號圓茶餅面完整，餅身表現均稱豐滿，手感強烈，外觀最圓順，倉儲良好。茶葉內仍能見白底紅字的內飛，一直珍藏於少數藏家手裡。茶餅的壓制技術非常精細而規矩，採用普洱正山最上好的大葉種茶樹製成，其條索肥碩，葉子寬大，茶菁有如泡過茶油，油亮充足，帶金黃色碩大的毫頭。 <br />\n1921年時敬昌號茶莊是江城地方最大最有名的普洱茶莊之一，在所有先期私人茶莊的茶品中，首推敬昌和同慶圓茶；而敬昌號是當中製造技術最優良，其茶餅壓制技術、筒包技術、竹箬篾條材料、內飛內票的設計和印刷，以及貯存陳放方法等都是數一數二。直至現在，普洱茶界仍以此茶作為普洱茶品質的標杆，可見此茶地位之高。<br />\n有關資料可參閱《號級古董茶事典》第236至237頁。', '100', null, null, null, null, null), ('163', 'A Piece Of ‘Tong Chang Hao Huang Wen Xing’ Pu-er Tea Cake, 1930’s', '百年 同昌號․黃文興圓茶', '14', '/public/pic_content/img/a3/LOTNO_226_1.jpg,/public/pic_content/img/a3/LOTNO_226_2.jpg,/public/pic_content/img/a3/LOTNO_226_3.jpg,/public/pic_content/img/a3/LOTNO_226_4.jpg', '226', '380,000 - 600,000', '', null, null, null, null, '', '數量：一片<br />\n總重量：約340g<br />\n年份：30年代<br />\n<br />\n本藏品為30年代的同昌號黃文興普洱圓茶，陳期高達80多年之久；其倉儲良好，重量十足。於餅身中可見黃文興普洱早期版本的白底藍色圖字內飛，落款為「主人黃文興謹白」。此茶餅厚實呈深栗色，條索扁長粗碩，十分自然美觀。<br />\n同昌號，亦為易武大茶莊，創設於同治七年，曾經在清末民初停廠歇業。至1930年，同昌號茶莊由商人黃文興接手主持業務運作。茶莊易主的初期，仍然是以同昌號的商標發行茶品，只是在茶品內飛的落款，改為「主人黃文興謹白」。<br />\n有關資料可參閱《號級古典茶事典》第206頁。', '100', null, null, null, null, null), ('164', 'A Piece Of ‘Tong Xing Hao’ Xiang Xinwu Pu-er Tea Cake, 1930’s', '百年 同興號．向繩武圓茶', '14', '/public/pic_content/img/a3/LOTNO_227_1.jpg,/public/pic_content/img/a3/LOTNO_227_2.jpg', '227', '', '600,000', null, null, null, null, '', '數量：一片<br />\n總重量：約322g<br />\n年份：30年代<br />\n <br />\n百年同興號餅型特厚，條索分明粗碩，餅面呈栗色，質素之佳令人嚮往。內飛雖稍有破損，但仍能見到白底紅字之特徵，飛內資料主要列出此茶專採古七大茶山的正山茶為原料，是不可多得之佳品。<br />\n同興號為三大古董茶號之一，成立於雍正十年，歷經向質卿到向繩武兩代人，從茶原料選擇至工藝標準皆為業界標杆，享譽中外，與「同慶號」同屬易武最早期的茶莊。<br />\n有關資料可參閱《號級古董茶事典》第213頁。', '100', null, null, null, null, null), ('165', 'A Tong Of ‘Song Pin Hao’ Pu-er Tea Cake, 1960&#039;s', '六十年代 宋聘號圓茶', '14', '/public/pic_content/img/a3/LOTNO_228_1.jpg,/public/pic_content/img/a3/LOTNO_228_2.jpg,/public/pic_content/img/a3/LOTNO_228_3.jpg,/public/pic_content/img/a3/LOTNO_228_4.jpg', '228', '380,000 - 500,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,936g<br />\n年份：60年代前後<br />\n<br />\n珍藏五十多年的宋聘號圓茶，竹箬完整，隱約可見「乾利貞」字樣，包裝紙上內票完好，當中「乾利貞宋聘號」清晰可見，品相上佳。百年茶莊宋聘號的出品，陳放多年已自然陳化為藥蔘香氣韻，是收藏品飲之佳品。', '100', null, null, null, null, null), ('166', 'A Piece Of ‘Fu Hua Hao’ Song Pin Mark Pu-er Tea Cake, 1960’s', '六十年代 宋聘（福華號）宋聘嘜青餅', '14', '/public/pic_content/img/a3/LOTNO_229_1.jpg,/public/pic_content/img/a3/LOTNO_229_2.jpg,/public/pic_content/img/a3/LOTNO_229_3.jpg', '229', '', '60,000', null, null, null, null, '', '數量：一片<br />\n總重量：295g<br />\n年份：60年代<br />\n<br />\n褔華號茶莊由盧鑄勳師傅於1954年在香港創立，其時選用雲南茶箐，加上傳統普洱茶發酵工藝精製茶餅，遠銷泰國。此藏品倉存自然偏乾，見白底綠字內飛，飛上題「福華號宋聘嘜普茶商標」。<br />\n有關資料可參閱《號級古董茶事典》第259頁。', '100', null, null, null, null, null), ('167', 'A Piece Of Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 藍印鐵餅', '14', '/public/pic_content/img/a3/LOTNO_230_1.jpg,/public/pic_content/img/a3/LOTNO_230_2.jpg,/public/pic_content/img/a3/LOTNO_230_3.jpg,/public/pic_content/img/a3/LOTNO_230_4.jpg,/public/pic_content/img/a3/LOTNO_230_5.jpg', '230', '', '220,000', null, null, null, null, '', '數量：一片<br />\n總重量：約325g<br />\n年份：50年代<br />\n工序：生茶<br />\n<br />\n本拍品藍印鐵餅又簡稱「藍鐵」，上世紀五十年代經典代表茶品，包裝狀況依然良好。由於當時採用金屬模子直接把茶青放入壓制脫模而成，擺脫了以往用布袋包揉壓制茶葉的繁瑣工序，鐵餅壓地緊結，新茶膠質更易提早釋放出來，使豐富的茶膠勻齊地分佈在茶葉表層。因茶餅緊實，鐵餅後續發酵轉變會比較緩慢，茶湯的口感較傳統餅形就更加濃烈紮實，氣韻更足而充滿活性。', '100', null, null, null, null, null), ('168', '2 Pieces Of Blue Mark Pu-er Tea Cake Without Wrapping, 1950&#039;s', '五十年代 無紙藍印鐵餅', '14', '/public/pic_content/img/a3/LOTNO_231_1.jpg,/public/pic_content/img/a3/LOTNO_231_2.jpg,/public/pic_content/img/a3/LOTNO_231_3.jpg', '231', '', '280,000', null, null, null, null, '', '數量：兩片<br />\n重量：約292g及320g<br />\n年份：50年代<br />\n工序：生茶<br />\n<br />\n傳統的藍印鐵餅餅型較厚，採用易武喬木茶菁為原料，餅面金芽毫多，茶芽較為粗壯，屬「印級茶」中茶氣偏強之作，是上世紀五十年代經典代表茶品。由於餅型堅實，陳化相對需時，至今已經過六十多年的時日，陳期恰好。 <br />\n其中一片倉儲良好，餅形較完整並見內飛。', '100', null, null, null, null, null), ('169', '2 Packs Of 73 Tea Bricks With Jujube Aroma, 1960’s', '六十年代 73棗香特厚磚', '14', '/public/pic_content/img/a3/LOTNO_232_1.jpg,/public/pic_content/img/a3/LOTNO_232_2.jpg,/public/pic_content/img/a3/LOTNO_232_3.jpg,/public/pic_content/img/a3/LOTNO_232_4.jpg', '232', '', '420,000', null, null, null, null, '', '數量：兩包共八塊<br />\n總重量：約2,040g<br />\n年份：60年代中期<br />\n<br />\n此拍品為兩組共八塊的73棗香特厚磚，附包裝，內票為「八中一茶」商標，票上題「雲南普洱茶磚」。<br />\n棗香磚主要是用西雙版納勐海喬木曬青毛茶，調配出特別的爽朗棗香味與韻味，在口感上有古董茶風韻的表現。於仕宏2016春季拍賣中，五塊棗香磚曾以接近50萬港元(連佣金)成交，足見其價值非凡。', '100', null, null, null, null, null), ('170', '4 Pieces Of Pu-er Tea Bricks Produced During Cultural Revolution, 1973', '七十年代 文革厚磚', '14', '/public/pic_content/img/a3/LOTNO_233_1.jpg,/public/pic_content/img/a3/LOTNO_233_2.jpg', '233', '', '130,000', null, null, null, null, '', '數量：四塊<br />\n總重量：約916g<br />\n年份：1973年<br />\n工序：熟茶<br />\n<br />\n本拍品是第一批在文化大革命期間製作的磚茶，四塊茶磚品相俱佳，倉存良好，因其特殊的文化和歷史背景而得名，存世的數量不多。<br />\n文革磚的茶箐原料選用雲南喬木型大葉種曬青毛茶，品質優越，壓製緊結密實。<br />\n有關資料可參閱《深邃的七子世界》第408頁。', '100', null, null, null, null, null), ('171', '20 Pieces of 92 Pu-er Tea Bricks, 1990&#039;s', '92普洱方磚', '14', '/public/pic_content/img/a3/LOTNO_234_1.jpg,/public/pic_content/img/a3/LOTNO_234_2.jpg,/public/pic_content/img/a3/LOTNO_234_3.jpg,/public/pic_content/img/a3/LOTNO_234_4.jpg', '234', '', '120,000', null, null, null, null, '', '數量：共二十塊<br />\n總重量：約2,197g<br />\n年份：90年代<br />\n工序：生茶<br />\n<br />\n九二方磚於1991及1992年間共產四批，本拍品包裝背後見製作日期為1992年2月，而盒身可見有引人注目的錯體「淨重100公分」。<br />\n九二方磚由勐海茶廠所制，中茶商標，陳期已達二十五年，品質受到市場及茶客的認可。曾被「普洱茶王」鄧時海先生形容為「末代茶王」，其收藏潛力不容忽視。', '100', null, null, null, null, null), ('172', '3 Tongs Of 7542 Pu-er Tea Cake In Thick Wrapping, 1980&#039;s', '八十年代 7542厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_235_1.jpg,/public/pic_content/img/a3/LOTNO_235_2.jpg', '235', '', '420,000', null, null, null, null, '', '數量：三筒共廿一片<br />\n總重量：約6,893g<br />\n年份：80年代<br />\n<br />\n80年代的7542厚紙包裝普洱青餅是勐海茶廠最受歡迎的產品，也是普洱茶市場的指標產品。其面茶色澤烏潤顯芽毫，心茶肥壯，包裝品相良好；加上以肥壯茶菁為裡，幼嫩芽葉撒面，至今有約30年陳期，開飲已有印級陳味，為普洱茶收藏市場的明星茶。', '100', null, null, null, null, null), ('173', 'A Tong Of 8582 Pu-er Tea Cake, 1980’s', '八十年代 大葉青餅', '14', '/public/pic_content/img/a3/LOTNO_236_1.jpg,/public/pic_content/img/a3/LOTNO_236_2.jpg,/public/pic_content/img/a3/LOTNO_236_3.jpg', '236', '400,000 - 600,000', '', null, null, null, null, '', '數量：一筒七片<br />\r\n總重量：約2,345g<br />\r\n年代：80年代<br />\r\n<br />\r\n本拍品較乾倉儲存，品相出色；經過30年陳化及在優良倉存之下，已經轉化至富有印級茶的韻味。<br />\r\n大葉青餅配方為8582，約在八十年代出現，常稱8582大葉青餅。早期的8582大多為厚紙包裝，茶質極佳，是收藏市場的搶手貨。這款茶的特色為表面茶用三、四級茶菁，裡面用七、八級茶，因用的茶料粗枝大葉，又被稱大葉青餅，餅身也較大較厚。', '100', null, null, null, null, null), ('174', 'A Tong Of 8582 Pu-er Tea Cake In Thin Wrapping, 1980\'s', '八十年代 8582薄紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_237_1.jpg,/public/pic_content/img/a3/LOTNO_237_2.jpg,/public/pic_content/img/a3/LOTNO_237_3.jpg,/public/pic_content/img/a3/LOTNO_237_4.jpg', '237', '380,000 - 600,000', '', null, null, null, null, '', '數量：一筒七片<br />\r\n總重量：約2,555g<br />\r\n倉儲：純乾倉<br />\r\n年份：約80年代末<br />\r\n<br />\r\n此茶純乾倉儲存，茶餅外形圓厚端正，撤面條索肥壯，鬆緊適中；並採用五至八級粗壯新老茶菁拼配緊壓，當中三、四級為面，五至八級為裡茶尤以七、八級為主，乃收藏市場的搶手貨。', '100', null, null, null, null, null), ('175', 'A Tong and A Piece Of 7532 Pu-er Tea Cake, 1980s', '八十年代 7532 厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_238_1.jpg,/public/pic_content/img/a3/LOTNO_238_2.jpg', '238', '', '200,000', null, null, null, null, '', '數量：一筒七片加一片<br />\n總重約：約2,754g<br />\n年份：80年代<br />\n<br />\n此茶倉存良好，茶氣十足，為勐海茶廠上世紀80年代開始生產之高檔生茶，以一至六級原料拼配緊壓，一至三級鋪面、三至六級為裡茶，三四級茶菁原料為主，原料檔次較高，可稱是80年代的雪印。<br />\n有關資料可參閱《深遂的七子世界》第192頁。', '100', null, null, null, null, null), ('176', 'A Tong Of 88 Pu-er Tea Cake, 1988', '88青普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_239_1.jpg,/public/pic_content/img/a3/LOTNO_239_2.jpg', '239', '', '260,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,312g<br />\n年份：1988年<br />\n<br />\n大名鼎鼎的八八青，此茶以經典生茶7542配方製作，屬於較早期版本，其餅形偏大，以自然偏乾倉儲存，竹箬完整。<br />\n經近三十年的陳化，擁有優質茶底的八八青茶質愈見成熟。此青餅茶氣足，茶湯質感強勁厚重，野香誘人，散發一縷縷甜香味，生津回甘快，茶韻變化無窮，具有普洱茶的天然真味，即使經過多次沖泡，仍然不減其明快悠揚口感，深受藏家追捧。', '100', null, null, null, null, null), ('177', 'A Tong of 8582 Pu-er Tea Cake, 1990’s', '九十年代 勐海廠8582普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_240_1.jpg,/public/pic_content/img/a3/LOTNO_240_2.jpg', '240', '', '130,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,622g<br />\n倉儲：乾倉<br />\n年份：1997 / 1998年<br />\n<br />\n由勐海茶廠出品，以自有配方拼配的8582，一直以乾倉儲存，經過多年醇化，口感醇和甘甜。', '100', null, null, null, null, null), ('178', '2 Tongs Of Yunnan Wild Tea Cake with Red Ribbon, 1998', '紅大益 雲南野生餅茶 (紅帶)', '14', '/public/pic_content/img/a3/LOTNO_241_1.jpg,/public/pic_content/img/a3/LOTNO_241_2.jpg', '241', '', '250,000', null, null, null, null, '', '數量：兩筒共十四片<br />\n總重量：約5,272g<br />\n倉儲：純乾倉<br />\n年份：1998年<br />\n工序：生茶<br />\n<br />\n陳期已接近廿年的紅大益青餅，倉儲極乾，拍品竹箬保存完整，品相上佳。而此茶主要以芽頭為料，茶氣帶班樟味、湯色黃潤透亮、回甘十足，為中生代的明星茶品。<br />\n有關資料可參閱《深遂的七子世界》第299頁。', '100', null, null, null, null, null), ('179', '14 Pieces Of &#039;Guang Yun&#039; Pu-er Tea Cake, 1950s', '58廣雲貢餅', '14', '/public/pic_content/img/a3/LOTNO_242_1.jpg,/public/pic_content/img/a3/LOTNO_242_2.jpg,/public/pic_content/img/a3/LOTNO_242_3.jpg', '242', '', '380,000', null, null, null, null, '', '數量：共十四片<br />\n總重量：約6,389g<br />\n年份：50年代末<br />\n<br />\n本藏品為五十年代的廣雲貢餅，倉儲條件較佳。內票沿用印級茶的「方形八中」設計，但票型較細小。是廣雲系列中最特別﹑目前所剩最少的產品之一。餅形比其他年代的茶餅大，但反面的凹洞卻是最小，且茶餅邊形呈角狀。<br />\n當時中央規定雲南茶葉公司每年需固定提撥茶菁讓廣東茶葉公司做為拼配原料，所以早期之廣雲貢餅大都摻有印級茶之茶菁。<br />\n有關資料可參閱《深邃的七子世界》第430頁。', '100', null, null, null, null, null), ('180', '7 Pieces Of &#039;Guang Yun&#039;  Pu-er Tea Cake, 1960s', '六十年代 廣雲貢餅', '14', '/public/pic_content/img/a3/LOTNO_243_1.jpg,/public/pic_content/img/a3/LOTNO_243_2.jpg', '243', '', '80,000', null, null, null, null, '', '數量：七片<br />\n總重量：約2,340g<br />\n年份：60年代<br />\n<br />\n本藏品餅形完整，茶餅見八中綠色茶字小內票，在同期茶品中，性價比頗見優勢。<br />\n一組七片的廣雲貢在60年代生產，由於市場對於普洱老茶需求愈來愈大，達四十年陳期，而且製作獨特的廣雲貢餅也很受到追捧，身價節節上升。', '100', null, null, null, null, null), ('181', '7 Pieces Of Blue Mark Pu-er Tea Cake, 1970’s', '七十年代 大藍印圓茶', '14', '/public/pic_content/img/a3/LOTNO_244_1.jpg,/public/pic_content/img/a3/LOTNO_244_2.jpg', '244', '', '350,000', null, null, null, null, '', '數量：七片<br />\n總重量：約2,500g<br />\n年份：70年代<br />\n<br />\n一組七片的大藍印餅面較大，包裝極少見，為大口中的白紙版本，屬於七十年代的包裝風格。此茶茶韻帶樟香與甜香，略帶藥香，富印級味道；逾三十年陳期，是值得推薦的品飲七子茶品。<br />\n有關資料可參閱《深邃的七子世界》第180及181頁。', '100', null, null, null, null, null), ('182', '7 Pieces Of Yellow Mark Pu-er Tea Cake, 1970’s', '七十年代 大黃印圓茶', '14', '/public/pic_content/img/a3/LOTNO_245_1.jpg,/public/pic_content/img/a3/LOTNO_245_2.jpg', '245', '', '350,000', null, null, null, null, '', '數量：七片<br />\n總重量：約2,430g<br />\n年份：70年代初<br />\n<br />\n本拍品是產於70年代初的標準版七子大黃印，包裝完好，七片茶餅均有內票。黃印七子餅，生產於五十年代末至七十年代中期，是雲南七子餅的一個里程碑，同時也是現代拼配普洱茶工藝的始祖，因為茶質優良，是公認的好茶，近年更因達到四十年陳化轉變，使之成為受歡迎的收藏品。<br />\n有關資料可參閱《深邃的七子世界》第121頁。<br />\n<br />', '100', null, null, null, null, null), ('183', 'A Tong Of Snow Mark Pu-er Tea Cake, 1980s', '八十年代初 雪印青餅', '14', '/public/pic_content/img/a3/LOTNO_246_1.jpg,/public/pic_content/img/a3/LOTNO_246_2.jpg,/public/pic_content/img/a3/LOTNO_246_3.jpg,/public/pic_content/img/a3/LOTNO_246_4.jpg', '246', '', '480,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,424g<br />\n倉儲：乾倉<br />\n年份：80年代初<br />\n<br />\n此拍品為原廠牛皮紙的「紙仝」包裝，而且此茶倉儲非常乾，其乾倉程度在雪印中甚為少見，茶氣十足。<br />\n雪印青餅是勐海茶廠7532配方的出品，以「3級」幼葉為主要原料，是勐海茶廠最為細嫩的茶品，產量不多。以豐富口感及細膩而聞名，其聲名與73青餅及8582不遑多讓。', '100', null, null, null, null, null), ('184', 'A Piece Of Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 乙級藍印青餅', '14', '/public/pic_content/img/a3/LOTNO_247_1.jpg,/public/pic_content/img/a3/LOTNO_247_2.jpg,/public/pic_content/img/a3/LOTNO_247_3.jpg,/public/pic_content/img/a3/LOTNO_247_4.jpg', '247', '', '380,000', null, null, null, null, '', '數量：一片<br />\n總重量：370g<br />\n年份：50年代<br />\n<br />\n這片陳期逾半世紀的印級皇者–藍印青餅品相堪稱完美，該茶一直保儲於良好倉儲環境。其包裝紙幾近完整，「乙級」之樣清晰可見；而且重量更高達370克，比同款茶餅超重約百分之十五之上，市面難尋。<br />\n此茶餅面見金黃芽頭，陳年梅香單一且強勁，陳期愈久，茶韻風格更加別樹一幟，於收藏或品飲皆炙手可熱，值得世代流傳。', '100', null, null, null, null, null), ('185', '3 Pieces Of Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 甲級藍印青餅', '14', '/public/pic_content/img/a3/LOTNO_248_1.jpg,/public/pic_content/img/a3/LOTNO_248_2.jpg', '248', '', '1,270,000', null, null, null, null, '', '數量：三片<br />\n重量：約330g、352g及333g<br />\n倉儲：乾倉<br />\n年份：50年代<br />\n<br />\n一組三片的拍品均是甲級版本，包裝紙上之「甲級」字樣亦清晰易見，品相極佳加上乾倉儲存。當中一片重量更達352克，市場上難得一見，不論在包裝及重量方面，都是市場上的佼佼者，堪稱完美。<br />\n五十年代早期的大字甲級藍印青餅，其名與紅印並駕齊驅。陳期逾半世紀，茶韻風格更加別樹一幟，於收藏或品飲皆為炙手可熱之品。', '100', null, null, null, null, null), ('186', 'A Tong Of Red Mark Pu-er Tea Cake without wrapping papers, 1950’s', '五十年代 無紙紅印青餅', '14', '/public/pic_content/img/a3/LOTNO_249_1.jpg,/public/pic_content/img/a3/LOTNO_249_2.jpg,/public/pic_content/img/a3/LOTNO_249_3.jpg,/public/pic_content/img/a3/LOTNO_249_4.jpg', '249', '', '1,870,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,269g<br />\n年份：50年代<br />\n<br />\n一筒七片的無紙紅印以軟竹箬包裹，極乾倉儲存，完美品相。在烏亮金黃的餅面與粗壯條索之中，見八中內飛埋於其中。至今陳化逾六十年，葉底活性依舊，散發出的陳韻一樣迷人，生聞已感覺其韻氣醇而樟香味濃烈。<br />\n無紙紅印，如雷貫耳的名字，無人不識；在市場上不僅有「殿堂級」普洱茶的美譽，而且是號級茶的承接者，成為普洱彌珍。', '100', null, null, null, null, null), ('187', 'A Piece Of &#039;Gam San Lau&#039; Red Mark Pu-er Tea Cake, 1950’s', '五十年代 金山樓 無紙紅印青餅', '14', '/public/pic_content/img/a3/LOTNO_250_1.jpg,/public/pic_content/img/a3/LOTNO_250_2.jpg,/public/pic_content/img/a3/LOTNO_250_3.jpg,/public/pic_content/img/a3/LOTNO_250_4.jpg', '250', '', '200,000', null, null, null, null, '', '數量：一片<br />\n總重量：約359g<br />\n年份：50年代<br />\n<br />\n這片珍藏超過半世紀的金山樓無紙紅印重量達到359克，比現存之同期無紙紅印重量多出百分之十左右，依然接近當年標準出廠狀態，可謂傲視同儕。茶餅倉儲良好，餅形完整，散發著豐富的陳韻；茶菁條索粗壯飽滿，中茶商標內飛雖陷埋於茶餅之內，但依然清晰可見，乃收藏投資的佳品。', '100', null, null, null, null, null), ('188', 'A Piece Of &#039;Gam San Lau&#039; Red Mark Pu-er Tea Cake, 1950’s', '五十年代 金山樓 紅印青餅', '14', '/public/pic_content/img/a3/LOTNO_251_1.jpg,/public/pic_content/img/a3/LOTNO_251_2.jpg,/public/pic_content/img/a3/LOTNO_251_3.jpg', '251', '400,000 - 550,000', '', null, null, null, null, '', '數量：一片<br />\n總重量：約310g，另附3g碎茶<br />\n年份：50年代<br />\n<br />\n此茶為五十年代中期中字版的紅印青餅，倉儲乾淨，餅身茶葉烏潤有光，茶菁條索分明且粗壯飽滿，餅底貼有「金山樓」字樣的貼紙。半世紀後，整片紅印依舊呈現驚人的生命力，故此簇擁者無數。<br />\n因其外包裝「八中茶」之「茶」字為紅色而被稱作紅印圓茶餅；採用勐海產區頂級茶菁精製，加重萌芽的用量，科學嚴謹的芽葉配比、壓製、發酵方法，保證了印級古董茶餅擁有豐富細膩的口感。<br />\n本拍品另附3克碎茶。有關資料可參閱《深邃的七子世界》第54頁。', '100', null, null, null, null, null), ('189', '2 Pieces Of Red Mark Pu-er Tea Cake, 1950s', '五十年代 紅印鐵餅', '14', '/public/pic_content/img/a3/LOTNO_252_1.jpg,/public/pic_content/img/a3/LOTNO_252_2.jpg', '252', '600,000 - 900,000', '', null, null, null, null, '', '數量：共兩片<br />\n重量：約323g及331g<br />\n年份：50年代<br />\n工序：生茶<br />\n<br />\n本拍品之紅印鐵餅倉儲良好，經過六十年陳化，品相極佳。當年生產量極為稀少，現市場罕見，故如此品相在舊普洱中更顯珍貴。<br />\n紅印鐵餅的製作時間比「紅印圓茶」稍遲，質量及香氣與紅印同出一轍，因包裝八中茶字為紅色，且採用鐵餅緊壓製作而被定名為「紅印鐵餅」。', '100', null, null, null, null, null), ('190', 'A Tong Of ‘Si Pu Gong’ Pu-er Tea Cake, 1950’s', '思普貢茗圓茶', '14', '/public/pic_content/img/a3/LOTNO_253_1.jpg,/public/pic_content/img/a3/LOTNO_253_2.JPG,/public/pic_content/img/a3/LOTNO_253_2.jpg', '253', '700,000 - 1,200,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,165g<br />\n年份：約50年代<br />\n<br />\n本拍品之思普貢茗至今陳期超過半世紀，倉儲乾淨，仍保留著完整的竹箬筒包裝。內飛為白底紅字，題「思普貢茗」、「鴻和茶業公司」；此等百年老茶的產量不多，多屬私人收藏，甚少公開拍賣。<br />\n產於五十年代的思普貢茗貴屬號級茶的其中一員，採用了雲南南部思茅地區的普洱茶山為料，其餅身薄而小，顏色暗紅；老茶茶韻強勁，帶野樟香，是典型思普地區的普洱茶品。<br />\n有關資料可參閱《號級古董茶事典》第269頁。', '100', null, null, null, null, null), ('191', 'A Piece Of ‘Ding Xing Hao’ Purple Mark Pu-er Tea Cake, 1930’s', '鼎興號 紫票圓茶', '14', '/public/pic_content/img/a3/LOTNO_254_1.jpg,/public/pic_content/img/a3/LOTNO_254_2.jpg,/public/pic_content/img/a3/LOTNO_254_3.jpg', '254', '', '160,000', null, null, null, null, '', '數量：一片<br />\n總重量：約331g<br />\n年份：30年代<br />\n工序：生茶<br />\n<br />\n本藏品是鼎興圓茶的紫票版本，倉儲良好，在分明的條索下，見到白底紫色字的鼎興號內飛陷埋於餅身之內。此茶餅雖久經歲月洗禮，但重量依然十足，與當年狀態接近，是為難得。<br />\n鼎興號茶莊是上世紀三十至四十年代勐海最知名的茶莊之一，以生產高級普洱茶著稱，所生產的鼎興圓茶冠絕一時，目前其存世的茶品並不多，只散落於普洱茶收藏者手中。<br />\n有關資料可參閱《號級古典茶事典》第244至245頁。', '100', null, null, null, null, null), ('192', '2 Pieces Of Sun Yishun Liu An Tea, 1950s', '五十年代 三票孫義順老六安', '14', '/public/pic_content/img/a3/LOTNO_255_1.jpg,/public/pic_content/img/a3/LOTNO_255_2.jpg,/public/pic_content/img/a3/LOTNO_255_3.jpg,/public/pic_content/img/a3/LOTNO_255_4.jpg', '255', '', '88,000', null, null, null, null, '', '數量：兩顆<br />\n總重量：964g<br />\n年份：50年代<br />\n<br />\n本拍品為六安三票版本，三張票皆保存完整；茶品為竹簍與粽葉組合包裝，以頭芽製造，而且條索壯實勻齊，色澤黑褐油潤。<br />\n「香飄十里外、味釅一杯中」，安徽六安州產之六安茶，在清嘉慶年間的《六安州志》記載被列為貢茶：「天下產茶州縣數十，惟六安茶為官廷常進之品。」<br />\n大票資料可參閱《號級古典茶事典》第54頁。', '100', null, null, null, null, null), ('193', 'A Pack And A Piece Of  &#039;Ding Xing Hao’ Red Mark Tuocha', '鼎興號 紅標緊茶', '14', '/public/pic_content/img/a3/LOTNO_256_1.jpg,/public/pic_content/img/a3/LOTNO_256_2.jpg,/public/pic_content/img/a3/LOTNO_256_3.jpg', '256', '', '1,000,000', null, null, null, null, '', '數量：一條七粒加一粒<br />\n總重量：約1,522g<br />\n年份：30年代或以前<br />\n<br />\n此拍品中一條七粒的紅標緊茶連同筍葉包裝，保存完整，超過半世紀仍能保存如此品相，實在彌足珍貴。外加獨立一顆緊茶顏色轉化暗紅色，且見紅字內票。<br />\n鼎興號於上世紀初在勐海經營，以生產高級普洱茶見稱。建國後已經停產，產量不多，能保存至今特別罕見。<br />\n鼎興號緊茶分有紅標、藍標及紫標，內票皆以星月圖案為識，其中以本拍品的紅標品質為上佳。', '100', null, null, null, null, null), ('194', 'A Bag Of Chongcha From Wahsang Tea Co., 1950s', '五十年代 前中環華生茶莊蟲茶', '14', '/public/pic_content/img/a3/LOTNO_257_1.jpg,/public/pic_content/img/a3/LOTNO_257_2.jpg', '257', '', '14,000', null, null, null, null, '', '數量：散茶一袋<br />\n總重量：約600g<br />\n年份：50年代<br />\n<br />\n早在明朝，李時珍的《本草綱目》中就認為蟲茶是一種很好的醫藥保健飲料，據說它有清熱祛暑、解毒健胃、助消化等功效。自乾隆年間起，蟲茶就奉為貢品，聞名海內外。<br />\n此批達五十多年的蟲茶一直收藏於享負盛名的中環華生茶莊，雖然該行已結業約30年，但就養生及品飲而言，依然有質素保證。', '100', null, null, null, null, null), ('195', 'A Piece Of ‘Long Ma Tong Qing’ Pu-er Tea Cake, 1940’s', '百年 龍馬同慶號圓茶', '14', '/public/pic_content/img/a3/LOTNO_258_1.jpg,/public/pic_content/img/a3/LOTNO_258_2.jpg,/public/pic_content/img/a3/LOTNO_258_3.jpg', '258', '', '100,000', null, null, null, null, '', '數量：一片<br />\n總重量：約249g<br />\n年份：40年代<br />\n<br />\n此百年同慶號圓茶白底雙圈的紅字內飛仍見埋於餅身之中，「雲南同慶老號啟」的字樣清晰易見，從內飛風格中可辨認出龍馬同慶號特徵。由於年代久遠，茶邊緣已略見鬆散，但精緻的品相與精良製作工藝，可演繹出清代普洱茶最為輝煌的篇章，能被流傳至今的「號級茶」格外的珍貴。<br />\n同慶號茶莊的普洱茶，一直都被喻為品質最佳的茶品，普洱茶愛好者均視同慶號普洱茶為國寶級的茶品，堪稱「清末國寶級百年普洱」。其製造過程選料精當，加工技術頗為講究，採用三月陽春時分，易武山三至五等細嫩茶菁製成。所製成之茶幽雅內斂，絕冠群倫，是極為柔和性之優美茶品。<br />\n有關資料可參閱《號級古董茶事典》第230及231頁。', '100', null, null, null, null, null), ('196', 'A Tong Of Late ‘Tong Qing Hao’ Pu-er Tea Cake', '後期同慶號普洱圓茶', '14', '/public/pic_content/img/a3/LOTNO_259_1.jpg,/public/pic_content/img/a3/LOTNO_259_2.jpg,/public/pic_content/img/a3/LOTNO_259_3.jpg,/public/pic_content/img/a3/LOTNO_259_4.jpg', '259', '280,000 - 500,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約1,777g <br />\n<br />\n本藏品一筒七片，連同竹箬包裝，餅形工整圓渾，倉存良好，是為難得。茶餅上見內票，以白底紅字題：「本號開設易武大街專購正山細嫩茗芽精工揉造發行恐有假冒特加此內飛為記 同慶茶莊主人謹白」。', '100', null, null, null, null, null), ('197', 'A Piece Of ‘Fu Lu Gong’ Pu-er Tea Cake, 1960’s', '六十年代 福祿貢茶', '14', '/public/pic_content/img/a3/LOTNO_260_1.jpg,/public/pic_content/img/a3/LOTNO_260_2.jpg,/public/pic_content/img/a3/LOTNO_260_3.jpg,/public/pic_content/img/a3/LOTNO_260_4.jpg,/public/pic_content/img/a3/LOTNO_260_5.jpg', '260', '', '130,000', null, null, null, null, '', '數量：一片<br />\n總重量：約353g<br />\n年份：60年代<br />\n工序：生茶<br />\n<br />\n此片福祿貢茶倉儲良好，條索分明，有白底綠字內飛，上有「福祿貢茶」及「鴻利公司督製」字樣。<br />\n福祿貢茶是50年代產品，當時由泰國銷往香港，由於內票上有茶廠的泰國地址，故一開始被認為是泰國普洱茶，導致銷路不佳。後來辨清是來自雲南鳳山的茶菁，乃正統的雲南普洱茶，更被歸納為印級茶一員，以致價格飛升至現時每片價格約需十餘萬港元，收藏價值倍增。<br />\n有關資料可參閱《號級古董茶事典》第270頁。', '100', null, null, null, null, null), ('198', '6 Pieces Of ‘He Nei Hao’ Pu-er Tea Cake, 1930’s', '百年 河內號圓茶', '14', '/public/pic_content/img/a3/LOTNO_261_1.jpg,/public/pic_content/img/a3/LOTNO_261_2.jpg', '261', '220,000 - 380,000', '', null, null, null, null, '', '數量：六片<br />\n總重量：約1,571g<br />\n年份：30年代<br />\n<br />\n河內圓茶以北越茶菁為料，條索較粗見茶梗，餅型較小，壓製上並沒有雲南號級茶的厚重感。由於越南北部與雲南省為鄰，兩地處於相同緯度上，可說是雲南地勢的延伸，故以北越茶菁為料，茶性上兩者極為相似，難以分辨。河內圓茶的茶氣沉穩不外放，茶背後的生命力在品茗者味蕾跳舞，印級味十足，使河內號普洱茶名遠播海內外，成為最具特色的茶品之一。', '100', null, null, null, null, null), ('199', 'A Piece Of Blue Mark Pu-er Tea Cake, 1950’s', '五十年代 藍印鐵餅', '14', '/public/pic_content/img/a3/LOTNO_262_1.jpg,/public/pic_content/img/a3/LOTNO_262_2.jpg,/public/pic_content/img/a3/LOTNO_262_3.jpg,/public/pic_content/img/a3/LOTNO_262_4.jpg', '262', '', '130,000', null, null, null, null, '', '數量：一片<br />\n總重量：約305g<br />\n年份：50年代<br />\n工序：生茶<br />\n<br />\n此藏品於早年曾經拆開品飲，故重量相對較輕；餅面烏亮潤澤，條索粗壯緊實。<br />\n傳奇的藍印鐵餅餅型較厚，採用易武喬木茶菁為原料，餅面金芽毫多，茶芽較為粗壯，屬「印級茶」中茶氣偏強之作，是上世紀五十年代經典代表茶品；由於餅型堅實，故陳化時間相對需時，至今經過六十多年的時日陳化。', '100', null, null, null, null, null), ('200', 'A Tong Of 7572 Pu-er Tea Cake, 1980’s', '八十年代 7572普洱圓茶', '14', '/public/pic_content/img/a3/LOTNO_263_1.jpg,/public/pic_content/img/a3/LOTNO_263_2.jpg', '263', '220,000 - 300,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,241g<br />\n年份：80年代末<br />\n<br />\n一筒七片的7572保存原有竹箬包裝，倉存乾淨完好，包裝紙完整，見內票。採用勐海茶區青壯茶菁為心茶，金毫細茶作撒尖和蓋茶，品質甚佳。', '100', null, null, null, null, null), ('201', 'A Tong Of 8582 Pu-er Tea Cake In Thick Wrapping, 1980’s', '八十年代 8582厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_264_1.jpg,/public/pic_content/img/a3/LOTNO_264_2.jpg', '264', '300,000 - 450,000', '', null, null, null, null, '', '數量：一筒七片<br />\r\n總重量：約2,499g<br />\r\n年份：80年代<br />\r\n<br />\r\n本拍品為八十年代中期的「商檢」厚紙包裝8582，倉存較乾，茶質出色。茶餅外形圓厚端正，餅面條索肥壯，鬆緊適中。經過三十年陳化及在優良倉存之下，已經轉化至富有印級茶的韻味。', '100', null, null, null, null, null), ('202', 'A Tong Of 7542 Pu-er Tea Cake In Thick Wrapping, 1980’s', '八十年代 7542厚紙普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_265_1.jpg,/public/pic_content/img/a3/LOTNO_265_2.jpg,/public/pic_content/img/a3/LOTNO_265_3.jpg', '265', '', '200,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,400g<br />\n年份：80年代<br />\n<br />\n此藏品為80年代的厚紙包裝7542普洱青餅，完整竹箬，倉儲狀況良好，相當討喜。7542是普洱茶市場的指標產品，也是勐海茶廠最受歡迎的產品，至今有超過30年以上陳期。<br />\n它以肥壯茶菁為裡，幼嫩芽葉撒面，研配得當，面茶色澤烏潤顯芽毫，心茶肥壯。該茶品香氣純正持久，有荷花香，滋味濃厚回甘好，湯色黃亮，葉底勻齊，被稱為普洱茶收藏市場的明星茶。', '100', null, null, null, null, null), ('203', 'A Tong Of 88 Pu-er Tea Cake, 1980&#039;s', '88青普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_266_1.jpg,/public/pic_content/img/a3/LOTNO_266_2.jpg', '266', '200,000 - 300,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,370g<br />\n年份：約80年代末<br />\n<br />\n此品相出色的88青以7542為配方，配上竹箬包裹，餅形完整，約為八十年代末出品。<br />\n陳期恰巧及茶菁乾淨優質的88青，來源於勐海純正野生大葉種，是七十年代雲南七子餅茶出產至今無法超越的一款標杆茶。早在1992年此茶被引進香港時，成本價只為港幣7.8元一片；及至去年市場價格一路猛進達至港幣8萬元一片，升值潛力與日俱增。', '100', null, null, null, null, null), ('204', '3 Tongs Of 7542 Pu-er Tea Cake, 1992', '92普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_267_1.jpg,/public/pic_content/img/a3/LOTNO_267_2.jpg', '267', '', '280,000', null, null, null, null, '', '數量：三筒共廿一片<br />\n總重量：約6,860g<br />\n年份：1992年<br />\n<br />\n本拍品連竹箬包裝，倉存狀況及品相極佳。此為92年青餅中的無飛版本，以勐海7542為配方，薄紙包裝，印刷顏色為硃砂紅，中央「茶」字為青草綠色。有關資料可參閱《深遂的七子世界》第254頁。', '100', null, null, null, null, null), ('205', '2 Tongs Of 7542 Pu-er Tea Cake, 1992', '92普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_268_1.jpg,/public/pic_content/img/a3/LOTNO_268_2.jpg', '268', '', '150,000', null, null, null, null, '', '數量：兩筒共十四片<br />\n總重量：約4,545g<br />\n年份：1992年<br />\n<br />\n本拍品為92年青餅中的無飛版本，以勐海7542為配方，薄紙包裝，印刷顏色為硃砂紅，中央「茶」字為青草綠色。拍品連竹箬包裝。<br />\n有關資料可參閱《深遂的七子世界》第254頁。', '100', null, null, null, null, null), ('206', 'A Tong Of Pu-er Tea Cake, 1994 (Misprinted Version)', '94業字普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_269_1.jpg,/public/pic_content/img/a3/LOTNO_269_2.jpg,/public/pic_content/img/a3/LOTNO_269_3.jpg', '269', '70,000 - 120,000', '', null, null, null, null, '', '數量：一筒共七片<br />\n總重量：約2,484g<br />\n年份：1994年<br />\n<br />\n本拍品是1994年勐海茶廠出產的7542普洱茶，近乎乾倉儲存；包裝紙上「中國土產畜產進出口公司雲南省茶業分公司」的「業」字並不是其他版本的「葉」字。自1994年出廠至今，已有已有二十多年陳期，是繼88青後的中期茶佳作。<br />\n有關資料可參閱《深邃的七子世界》第260頁。', '100', null, null, null, null, null), ('207', 'A Tong Of Pu-er Tea Cake, 1990&#039;s (Misprinted Version)', '九十年代 業字普洱青餅', '14', '/public/pic_content/img/a3/LOTNO_270_1.jpg,/public/pic_content/img/a3/LOTNO_270_2.jpg', '270', '45,000 - 60,000', '', null, null, null, null, '', '數量：一筒共七片<br />\n總重量：約2,461g<br />\n年份：90年代<br />\n<br />\n此筒7542是普洱茶市場的指標產品，也是勐海茶廠最受歡迎的產品。茶品包裝完整，品相上佳，見內票；特別之處為「茶葉分公司」上的「葉」字印成「業」字，市場較少。此茶滲透出傳統香港陳年普洱茶的香氣，其味溫醇，輕輕細心聞一下，會發現迷人的輕輕藥香，陳年普洱茶陳香味明顯。', '100', null, null, null, null, null), ('208', '7 Pieces Of Chungcha Brand Pu-er Tea Cake With Simplified Chinese Characters, 1970’s', '七十年代 下關簡體字中茶鐵餅', '14', '/public/pic_content/img/a3/LOTNO_271_1.jpg,/public/pic_content/img/a3/LOTNO_271_2.jpg', '271', '350,000 - 500,000', '', null, null, null, null, '', '數量：七片<br />\n總重量：約2,244g<br />\n年份：70年代<br />\n<br />\n此組中茶牌簡體字鐵餅有三款，分別是9字版，17字版和19字版；本拍品其中一片是19字版，其餘為17字的標準版。<br />\n中茶牌簡體字鐵餅是八十年代之前的產品，是下關茶廠中具代表性的茶品，餅型扁平堅實，展現濃厚質感及強勁氣韻，被譽為50年代藍印鐵餅的沿襲。<br />\n有關資料可參閱《深邃的七子世界》第369及371頁。', '100', null, null, null, null, null), ('209', '14 Pieces Of 8653 Pu-er Tea Cake With Traditional Chinese Charaters, 1980’s', '八十年代 下關8653繁體字中茶青餅', '14', '/public/pic_content/img/a3/LOTNO_272_1.jpg,/public/pic_content/img/a3/LOTNO_272_2.jpg', '272', '180,000 - 280,000', '', null, null, null, null, '', '數量：十四片<br />\n總重量：約4,684g<br />\n年份：80年代中<br />\n<br />\n一組十四片的茶餅均是八十年代中期薄絲紋紙版本的繁體字中茶牌鐵餅，倉儲良好，品相上佳。<br />\n茶面條索清晰分明，由於緊壓關係，故餅面也略見有布紋的痕跡。<br />\n有關資料可參閱《深邃的七子世界》第377頁。', '100', null, null, null, null, null);
INSERT INTO `curio_pic_content` VALUES ('210', '3 Pieces Of 8653 Pu-er Tea Cake, 1980’s', '八十年代 下關8653繁體字中茶青餅', '14', '/public/pic_content/img/a3/LOTNO_273_1.jpg,/public/pic_content/img/a3/LOTNO_273_2.jpg,/public/pic_content/img/a3/LOTNO_273_3.jpg,/public/pic_content/img/a3/LOTNO_273_4.jpg', '273', '', '70,000', null, null, null, null, '', '數量：共三片<br />\n總重量：約1,047g<br />\n年份：80年代初<br />\n工序：生茶<br />\n<br />\n本拍品是八十年代繁體字中茶牌鐵餅，品相上佳，氣韻強勁。由於8653陳期已有三十多年，達至中期陳茶的年份，故在今日陳茶需要殷切的情況下，其價格也大幅攀升。', '100', null, null, null, null, null), ('211', 'A Pack Of 73 Jujude Tea Bricks, 1960&#039;s', '六十年代 73棗香磚', '14', '/public/pic_content/img/a3/LOTNO_274_1.jpg,/public/pic_content/img/a3/LOTNO_274_2.jpg,/public/pic_content/img/a3/LOTNO_274_3.jpg', '274', '', '180,000', null, null, null, null, '', '數量：一包四磚<br />\n總重量：約996g<br />\n年份：60年代中期<br />\n<br />\n本藏品為市面罕見連同原包裝紙的73棗香磚。當年五行圖書在編輯世界權威普洱茶書《深邃的七子世界》時，遍尋連包裝的73棗香磚不果，可見本拍品之珍貴。此藏品部分茶磚見內飛，品相頗為完整，十分難得。<br />\n由於使用了大葉芽為材料，故其轉化過程較佳，帶顯著陳香。在發酵過程中更產生接近紅棗的香甜味道，故稱為棗香普洱茶。<br />\n同款茶磚曾刊登於《深邃的七子世界》第409頁中。<br />', '100', null, null, null, null, null), ('212', '5 Pieces Of Wide 73 Camphor Pu-er Tea Bricks, 1970’s', '七十年代 73寬版樟香磚', '14', '/public/pic_content/img/a3/LOTNO_275_1.jpg,/public/pic_content/img/a3/LOTNO_275_2.jpg,/public/pic_content/img/a3/LOTNO_275_3.jpg', '275', '', '50,000', null, null, null, null, '', '數量：五塊<br />\n總重量：1,200g<br />\n年份：70年代末<br />\n<br />\n一組五件的73樟香磚均為寬版，包裝及品相俱良好。此茶品樟香味醇厚，口感順滑，茶氣強烈，為一眾茶人所追捧。<br />\n有關資料可參閱《深遂的七子世界》第411頁。', '100', null, null, null, null, null), ('213', '6 Pieces Of Tea Bricks With Thick Yellow Paper, late 1980’s', '八十年代純乾倉油光厚黃紙熟磚', '14', '/public/pic_content/img/a3/LOTNO_276_1.jpg,/public/pic_content/img/a3/LOTNO_276_2.jpg', '276', '', '50,000', null, null, null, null, '', '數量：共六塊<br />\n總重量：1,410g<br />\n年份：80年代末<br />\n<br />\n一組六磚的厚黃紙熟茶磚以純乾倉儲存，產於八十年代末，見其包裝紙上的「洱」字與其他版本有輕微差別，有關資料可參閱《深邃的七子世界》第415頁。', '100', null, null, null, null, null), ('214', '4 Pieces Of  &#039;Xiaguan&#039; Tuocha, 1970&#039;s', '七十年代 下關沱茶', '14', '/public/pic_content/img/a3/LOTNO_277_1.jpg,/public/pic_content/img/a3/LOTNO_277_2.jpg,/public/pic_content/img/a3/LOTNO_277_3.jpg', '277', '', '25,000', null, null, null, null, '', '數量：四粒<br />\n總重量：約386g<br />\n年份：70年代初<br />\n<br />\n遠近馳名的下關沱茶，包裝完整，品相上佳。沱茶是雲南茶中相當古老的製品，早就暢銷國內外，現市場較少見。<br />\n有關資料可參閱《深邃的七子世界》第418頁。', '100', null, null, null, null, null), ('215', '3 Pack And A Piece Of ‘Xia Guan’ Tuacha, 1990’s', '九十年代 下關寶焰牌緊茶', '14', '/public/pic_content/img/a3/LOTNO_278_1.jpg,/public/pic_content/img/a3/LOTNO_278_2.jpg,/public/pic_content/img/a3/LOTNO_278_3.jpg,/public/pic_content/img/a3/LOTNO_278_4.jpg', '278', '40,000 - 60,000', '', null, null, null, null, '', '數量：三條九粒加一粒<br />\n總重量：約2,495g<br />\n年份：90年代中<br />\n<br />\n一批共十顆的寶焰牌緊茶，包括三條未曾開封的原廠紙袋包裝，品相極佳。其中單獨一顆見寶焰牌商標的內飛：內飛由紅黑黃三色組成，黃色香爐中的火焰象徵佛光，火焰燃燒正旺，代表佛光普照吉祥如意，四顆元寶則有貢茶的意思。下關沱茶名揚天下，如此品相倍添收藏價值。', '100', null, null, null, null, null), ('216', '5 Pack Of Pu-er Tuocha, 1980’s', '八十年代 班禪熟沱', '14', '/public/pic_content/img/a3/LOTNO_279_1.jpg,/public/pic_content/img/a3/LOTNO_279_2.jpg', '279', '', '48,000', null, null, null, null, '', '數量：五條共二十粒<br />\n總重量：約4,620g<br />\n年份：80年代末<br />\n<br />\n拍品沱茶竹箬包裝保存，品相出色。<br />\n此批班禪熟沱於1988年，由西藏十世班禪以私人名義向下關茶廠特別訂製，選用布朗山上等原料精心製作，內飛為「扎西德勒」四字吉祥語，以供西藏佛寺使用。外包白色棉紙，歷經歲月現在每個約重230克。至90年代末由林姓茶商將此茶帶往台灣，才有今日之流通。<br />\n在特別的宗教意義下，加上其轉化優異，口感順滑厚重，香氣融於茶湯而不浮，實為熟茶之上品。', '100', null, null, null, null, null), ('217', 'A Pack Of Aged White Tea From ‘Chen Mao Tai’,1950s', '五十年代 貞茂泰出口莊老白茶', '14', '/public/pic_content/img/a3/LOTNO_280_1.JPG,/public/pic_content/img/a3/LOTNO_280_2.JPG', '280', '', '42,000', null, null, null, null, '', '數量：散茶一袋<br />\n總重量：約600g<br />\n年份：50年代<br />\n<br />\n近年市場興起白茶熱潮，惜多為近期產品，而古董老白茶幾乎絕跡，所以此次拍品藏期近60年，是極為罕見。清代名人周亮工的《閩小記》中載白茶之功效：「白毫銀針，產太姥山鴻雪洞，其性寒涼，功同犀角，是治麻疹之聖藥。」', '100', null, null, null, null, null), ('218', 'A Box Of Old White Tea, 1970’s', '七十年代 野生古董老白茶', '14', '/public/pic_content/img/a3/LOTNO_281_1.jpg,/public/pic_content/img/a3/LOTNO_281_2.jpg', '281', '', '125,000', null, null, null, null, '', '數量：散茶一箱<br />\n總重量：約4,084g（連同原箱重量）<br />\n年份：70年代中<br />\n<br />\n白茶素有「一年茶、三年藥、七年寶」之說，一般五﹑六年的白茶就可算老白茶，超過二十年的老白茶已經非常難得，本拍品更有四十多年之久，難能可貴。<br />\n明朝李時珍《本草綱木》描述：「茶生於崖林之間，味苦，性寒涼，具有解毒、利尿、少寢、解暑」等功效，可見古人們早就發現白茶的藥用價值，是為養生佳品。', '100', null, null, null, null, null), ('219', 'A Pack of Liu Bao Tea Cake, 1960&#039;s - 1970&#039;s', '陳春蘭茶莊六堡茶', '14', '/public/pic_content/img/a3/LOTNO_282_1.jpg', '282', '100,000 - 160,000', '', null, null, null, null, '', '總重量：約9,600g<br />\n年份： 1960至70年代<br />\n<br />\n已有一千多年歷史的六堡茶屬黑茶種類，因出產於梧州市六堡鄉而得名。此茶特色在於蒸製，即將烘乾的茶葉，分等級投入木桶中蒸軟，然後攤入方底圓身的竹簍中，進倉讓其自然乾燥，最後再存放一兩個月進行陳化始成。其香醇陳，滋味濃醇甘和，為品飲及養生佳品。<br />\n六堡茶有消暑去濕的功效，在港澳、東南亞地區都深受歡迎，所以從清朝開始，就有廣東及香港的茶行到六堡買入農制的散茶，再運到香港及馬來西亞包裝。本茶出自總部設在香港的陳春蘭煙茶莊，是當時六堡茶在南洋地區主要經銷茶行，見證華人於南洋生活的歷史。', '100', null, null, null, null, null), ('220', 'A Pack Of Liu Bao Tea Cake, 1970’s', '七十年代 六堡茶', '14', '/public/pic_content/img/a3/LOTNO_283_1.jpg,/public/pic_content/img/a3/LOTNO_283_2.jpg,/public/pic_content/img/a3/LOTNO_283_3.jpg', '283', '', '140,000', null, null, null, null, '', '總重量：約9,100g<br />\n年份：1970年代<br />\n<br />\n此六堡茶至今已接近四十年陳期，保存於原裝之竹簍內，完好難得，為品飲及養生佳品。<br />\n已有一千五百多年歷史的六堡茶屬黑茶種類，因出產於梧州市蒼梧縣六堡而得名。過去大馬錫礦業發達，六堡茶大量出口當地，幫助礦工消暑去濕。又因為六堡茶在港澳、東南亞地區都深受歡迎，所以從清朝開始，就有廣東及香港的茶行到六堡收茶農制的散茶，再運到香港或馬來西亞包裝。其特色在於蒸製，即將烘乾的茶葉，分等級投入木桶中蒸軟，然後攤入方底圓身的竹簍中，進倉讓其自然乾燥，最後再存放一兩個月進行陳化始成。其香醇陳，滋味濃醇甘和。', '100', null, null, null, null, null), ('221', 'Liu Bao Tea With A Pair Of Tin Tea Caddies, 1980’s', '八十年代 珍藏六堡 連錫製一片冰心茶葉罐一對', '14', '/public/pic_content/img/a3/LOTNO_284_1.jpg,/public/pic_content/img/a3/LOTNO_284_2.jpg', '284', '15,000 - 28,000', '', null, null, null, null, '', '總重量：約750g<br />\n年份：80年代<br />\n<br />\n此茶條索緊結黑褐，湯色紅濃明亮，滋味醇厚帶陳香，十泡有餘甘。<br />\n絶版錫罐用料厚重，口蓋嚴密，是藏茶利器。茶與罐猶如牡丹綠葉，相得益彰。', '100', null, null, null, null, null), ('222', 'Liu Bao Tea With A Pair Of Faience Tea Caddies, 1980’s', '八十年代 珍藏六堡 連嵌絲琺瑯茶葉罐一對', '14', '/public/pic_content/img/a3/LOTNO_285_1.jpg,/public/pic_content/img/a3/LOTNO_285_2.jpg,/public/pic_content/img/a3/LOTNO_285_3.jpg,/public/pic_content/img/a3/LOTNO_285_4.jpg', '285', '', '38,000', null, null, null, null, '', '總重量：約1,500g<br />\n年份：80年代<br />\n<br />\n此六堡茶條索緊結黑褐，湯色紅濃明亮，滋味醇厚帶陳香。名茶配美器，以成對精湛獅鈕琺瑯茶葉罐盛載，從色濃莊重至清淡素雅，這洋味十足的彩料在瓷器上得到淋漓盡致的發揮。', '100', null, null, null, null, null), ('223', '12 Pieces Of Liu An Tea, 1980’s', '八十年代 六安茶', '14', '/public/pic_content/img/a3/LOTNO_286_1.jpg', '286', '', '50,000', null, null, null, null, '', '數量：兩條共十二粒<br />\n總重量：約5,586g<br />\n年份：80年代<br />\n<br />\n兩條共十二粒的六安茶以竹簍與粽葉組合包裝，品相上好。明代聞龍在《茶箋》中稱，六安茶入藥最有功效，不僅可消暑生津解渴，而且還有可幫忙消化，保健作用不容置疑，被視為養生品飲的珍品。', '100', null, null, null, null, null), ('224', '6 Pieces Of Liu An Tea From Ying Kee Tea House, 2001 – 2002', '英記家藏舊六安', '14', '/public/pic_content/img/a3/LOTNO_287_1.jpg,/public/pic_content/img/a3/LOTNO_287_2.jpg', '287', '', '18,000', null, null, null, null, '', '數量：共六粒<br />\n總重量：約2,788g<br />\n年份：2001年至2002年<br />\n<br />\n產自安徽省六安縣的舊六安茶，甘醇濃和，具暖脾胃的保健功效；而且由始創於1881年的英記茶莊經嚴格挑選及加工配製而成，品質有所保證，標劣立判。', '100', null, null, null, null, null), ('225', '2 Pieces Of Blue Mark Pu-er Tea Cake, 1970’s', '七十年代 大藍印圓茶', '14', '/public/pic_content/img/a3/LOTNO_288_1.jpg,/public/pic_content/img/a3/LOTNO_288_2.jpg', '288', '80,000 - 120,000', '', null, null, null, null, '', '數量：兩片<br />\n總重量：約683g<br />\n年份：70年代中<br />\n<br />\n此組大藍印圓茶品相上佳，餅身比一般七子餅要大要厚，而得名「大藍印」，其使用的茶葉亦較粗大，撥開餅身甚至可以在葉底中找到完整的大葉，這在其它的七子餅中難得一見。此拍品在茶韻上樟香與甜香交錯，為品飲及收藏者帶來驚喜。<br />\n有關資料可參閱《深邃的七子世界》第180及181頁。', '100', null, null, null, null, null), ('226', '7 Pieces Of Blue Mark Pu-er Tea Cake, 1970’s', '七十年代 大藍印圓茶', '14', '/public/pic_content/img/a3/LOTNO_289_1.jpg,/public/pic_content/img/a3/LOTNO_289_2.jpg', '289', '300,000 - 450,000', '', null, null, null, null, '', '數量：七片<br />\n總重量： 約2,400g<br />\n年份：70年代中期<br />\n<br />\n此拍品包裝較完整。七子大藍印以熱蒸發酵，俗稱四分熟六分生，口感較醇滑，甘甜而富老味，就品飲及收藏而言，相信是別具雅趣的選擇。<br />\n有關資料可參閱《深邃的七子世界》第180頁。', '100', null, null, null, null, null), ('227', '4 Tongs And A Piece Of 8592 Pu-er Tea Cake In Thick Wrapping, 1980&#039;s', '八十年代 8592厚紙普洱圓茶', '14', '/public/pic_content/img/a3/LOTNO_290_1.jpg,/public/pic_content/img/a3/LOTNO_290_2.jpg', '290', '200,000 - 350,000', '', null, null, null, null, '', '數量：四筒二十八片加一片<br />\n總重量：9,750g<br />\n年份：80年代<br />\n工序：熟茶<br />\n<br />\n本拍品是與8582同期的8592普洱茶餅，被稱為八十年代中最乾倉的熟餅，拍品包裝品相上佳，倉儲良好，是香港南天公司於八十年代向勐海茶廠訂製的茶品，所以其包裝都用上與8582同期的厚紙，竹箬上同樣貼有「商檢」的貼紙，此等高質茶品為一眾茶客之佳音。', '100', null, null, null, null, null), ('228', '4 Tongs And A Piece Of 7572 Pu-er Tea Cake, 1980&#039;s', '八十年代 7572普洱圓茶', '14', '/public/pic_content/img/a3/LOTNO_291_1.jpg,/public/pic_content/img/a3/LOTNO_291_2.jpg', '291', '150,000 - 220,000', '', null, null, null, null, '', '數量：四筒二十八片加一片<br />\n總重量：10,100g<br />\n年份：80年代末<br />\n工序：熟茶<br />\n<br />\n此批以手工薄棉紙包裝的7572普洱茶倉儲良好且品相佳，包裝及竹箬完整。此茶產於八十年代末，與88青餅採用同一款包裝紙，均以三至八級料為茶菁，五至六級青壯葉為主，發酵度較高，湯水細甜，在品飲上頗有老熟茶的風韻。', '100', null, null, null, null, null), ('229', '4 Tongs And A Piece Of Rose ‘Dayi’ Pu-er Tea Cake, 1996', '96玫瑰大益圓茶', '14', '/public/pic_content/img/a3/LOTNO_292_1.jpg,/public/pic_content/img/a3/LOTNO_292_2.jpg', '292', '', '110,000', null, null, null, null, '', '數量：四筒二十八片加一片<br />\n總重量：10,900g<br />\n年份：1996年<br />\n工序：熟茶<br />\n<br />\n96玫瑰大益圓茶是勐海茶廠第一款使用「大益」商標出品的普洱熟茶，倉儲良好，附內票，竹箬品相佳。由於其外包裝為紫色，內飛為紅字，以7572為配方，大家稱為玫瑰大益。<br />\n經近二十年轉化，此茶已漸轉有傳統熟茶的香味，茶香豐富，質厚帶甜，清爽而不俗氣。拍品並不包括圖片內的大票。<br />\n有關資料可參閱《深邃的七子世界》第295頁。', '100', null, null, null, null, null), ('230', 'Six Tongs Of Aquatic', '九十年代 97水藍印圓茶', '14', '/public/pic_content/img/a3/LOTNO_293_1.jpg,/public/pic_content/img/a3/LOTNO_293_2.jpg', '293', '50,000 - 80,000', '', null, null, null, null, '', '數量：共六筒<br />\n總重量：約16,900g<br />\n年份：1997年 <br />\n<br />\n水藍印是勐海系九十年代比較出名的一款茶，屬於七子餅的一種。隨著時間的流逝，愈見難尋，也因此變得彌足珍貴起來。', '100', null, null, null, null, null), ('231', '3 Pieces Of &#039;Guang Yun&#039;  Pu-er Tea Cake, 1960&#039;s - 1970&#039;s', '廣雲貢餅', '14', '/public/pic_content/img/a3/LOTNO_294_1.jpg,/public/pic_content/img/a3/LOTNO_294_2.jpg', '294', '', '28,000', null, null, null, null, '', '數量：三片<br />\n總重量：約1,034g<br />\n倉儲：乾倉<br />\n年份：60/70年代<br />\n<br />\n本藏品一套三片，餅型完整緊實，皆有內飛；其中兩片更為乾倉存放，包裝完好保存，品相上佳。<br />\n廣雲貢餅和中茶公司印級茶為同期茶，也被視為印級茶的一員。其製作方式沿用中茶印級茶，包括方形八中綠色茶字內票，但票型較細小。雖然製程相近，但由於氣候、茶菁來源不同，廣雲貢餅比其它印級茶更為細膩油潤。<br />\n有關資料可參閱《深邃的七子世界》第426頁。', '100', null, null, null, null, null), ('232', '7 Pieces Of &#039;Guang Yun&#039;  Pu-er Tea Cake, 1970&#039;s', '七十年代 廣雲貢餅', '14', '/public/pic_content/img/a3/LOTNO_295_1.jpg,/public/pic_content/img/a3/LOTNO_295_2.jpg,/public/pic_content/img/a3/LOTNO_295_3.jpg', '295', '60,000 - 100,000', '', null, null, null, null, '', '數量：七片<br />\n總重量：約2,428g<br />\n年份：70年代<br />\n<br />\n樟香濃郁的老茶，餅形完整，品相良好。茶餅均附內票，票形為方形八中綠色茶字，規格較細小。廣雲貢餅以大葉種為茶菁，茶質細膩油潤，使用鐵模壓製，餅形緊實。有關資料可參閱《深邃的七子世界》第428及429頁。', '100', null, null, null, null, null), ('233', '2 Pieces Of Kunming Raw Pu-er Tea Cake, 1990&#039;s', '九十年代 昆明鐵餅', '14', '/public/pic_content/img/a3/LOTNO_296_1.jpg,/public/pic_content/img/a3/LOTNO_296_2.jpg', '296', '', '22,000', null, null, null, null, '', '數量：兩片<br />\n總重量：約784g<br />\n年份：90年代<br />\n工序：生茶<br />\n<br />\n此藏品為九十年代末昆明茶廠製造的青茶，以金屬模具壓製而成，餅型緊實，包裝及品相俱佳。', '100', null, null, null, null, null), ('234', '2 Pieces Of ‘The Reunification Of Hong Kong’ Pu-er Tea Cake, 1997', '田茶牌 香港回歸紀念鐵餅', '14', '/public/pic_content/img/a3/LOTNO_297_1.jpg,/public/pic_content/img/a3/LOTNO_297_2.jpg', '297', '', '5,000', null, null, null, null, '', '數量：兩片<br />\n總重量：666g<br />\n年份：1997年<br />\n<br />\n為紀念香港回歸祖國，此拍品為田茶牌訂制生產的鐵餅。茶餅壓制緊實，餅面見凸起的「香港回歸 紀念餅」字樣。隨著香港回歸二十年，茶餅陳化愈見氣度與質韻，實乃投資自飲之佳品。', '100', null, null, null, null, null), ('235', 'A Tong of‘Zhen Chun Ya Hao’Pu-er Tea Cake, 1996', '96 真淳雅號', '14', '/public/pic_content/img/a3/LOTNO_298_1.jpg,/public/pic_content/img/a3/LOTNO_298_2.jpg,/public/pic_content/img/a3/LOTNO_298_3.jpg', '298', '', '120,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,500g<br />\n年份：1996年<br />\n工序：生茶<br />\n<br />\n藏品葉片立體完整，茶餅面的茶芽，已轉金黃之色，茶葉也明顯有陳茶的泛栗紅色澤，油亮清晰，竹箬包裝完整。<br />\n真淳雅號由易武鄉鄉長張毅老師傅，細選易武百年老樹，春芽一口料，精心培育，以「宋聘號」為藍圖定製。茶源採自雲南易武，摘自戰後第一批種植的喬木，該批喬木於1996年才首次被採摘；以石模手工古法手工壓製，茶餅顯厚，窩底略大，茶餅既厚且鬆。', '100', null, null, null, null, null), ('236', 'A Tong Of 99 Pu-er Tea Cake With Blue Mark In Dry Storage, 1999', '99綠大樹藍黑標厚紙青餅', '14', '/public/pic_content/img/a3/LOTNO_299_1.jpg,/public/pic_content/img/a3/LOTNO_299_2.jpg,/public/pic_content/img/a3/LOTNO_299_3.jpg', '299', '', '230,000', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,546g<br />\n倉儲：極乾倉<br />\n年份：1999年<br />\n<br />\n此藏品為藍黑標99年綠大樹以極乾倉存放，品相佳，餅身緊實，其品質卓越，是新生代普洱茶中評價很好的一批。當中99綠大樹有藍黑標和紅標之分，以藍黑標為優，市價亦為紅標之二倍，市場上甚至認為其口感不輸印級茶，頗值投資收藏。', '100', null, null, null, null, null), ('237', '6 Pieces Of 99 Pu-er Tea Cake With Red Marks, 1999', '99綠大樹紅標厚紙青餅', '14', '/public/pic_content/img/a3/LOTNO_300_1.JPG,/public/pic_content/img/a3/LOTNO_300_2.JPG', '300', '', '75,000', null, null, null, null, '', '數量：共六片<br />\n總重量：約2,320g<br />\n年份：1999<br />\n<br />\n此茶味韻勻長，呈現出一股原野高香，包裝品相良好，值得收藏。99綠大樹為廣州茶商於1999年深入易武深山，精選三百年以上的野放喬木為料，並委任國營勐海茶廠精工壓制而成，味韻出眾。', '100', null, null, null, null, null), ('238', '15 Piece Of 99 Pu-er Tea Cakes, 1999', '99綠大樹厚紙青餅', '14', '/public/pic_content/img/a3/LOTNO_301_1.jpg', '301', '', '160,000', null, null, null, null, '', '數量：共十五片<br />\n總重量：約5,042g<br />\n倉儲：乾倉<br />\n年份：約1999年<br />\n<br />\n此拍品一組十五片，乾倉，包裝品相良好；以茶餅緊壓度適中，配茶中等級，面茶及底茶均配有茶芽，這些茶芽也轉了顏色，金黃顯露。甜甜的陳茶香開始變得明顯，轉化出傳統勐海海老茶的年輕陳香，受到市場追捧。', '100', null, null, null, null, null), ('239', 'A Pack Of Fengching Pu-er Tea Cake, 2000 - 2001', '首批鳳慶青餅', '14', '/public/pic_content/img/a3/LOTNO_302_1.jpg,/public/pic_content/img/a3/LOTNO_302_2.jpg,/public/pic_content/img/a3/LOTNO_302_3.jpg', '302', '60,000 - 100,000', '', null, null, null, null, '', '數量：一支<br />\r\n總重量：約34,400g <br />\r\n年份：90年代後期<br />\r\n<br />\r\n此鳳慶茶整支為一組，每筒均有竹箬包裝，品相完整；餅面上見內票，票上並未如後期版本一樣，在「雲南七子餅茶」上加入「鳳牌」二字。<br />\r\n2015年，雲南鳳慶縣被中國茶葉學會授予第五屆「中國名茶之鄉」稱號，其鳳慶小葉種茶亦早於1984年已被國家認定為國家級優質茶樹種，能以此為料，其獨特的香氣韻味，散發清新淡雅的新感受。<br />\r\n有關資料可參閱《新生普洱年鑑 1998 - 2003》第378頁。', '100', null, null, null, null, null), ('240', 'A Tong Of‘Chang Tai Hao’Bu Lang Tea Cake, 2003-2004', '昌泰號 版納雲霧圓茶', '14', '/public/pic_content/img/a3/LOTNO_303_1.jpg,/public/pic_content/img/a3/LOTNO_303_2.jpg', '303', '18,000 - 30,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,660g<br />\n年份：2003-2004年<br />\n工序：生茶<br />\n<br />\n此拍品特選於西雙版納茶區喬木春芽菁拼配而成，以细嫩芽頭及青壯茶菁為料，鬆緊適中，條索清晰，陳香明顯。原裝竹箬包裝，附內飛。', '100', null, null, null, null, null), ('241', 'A Tong Of &#039;Douji&#039; Pu-er Tea Cake, 2006', '斗記精品普洱', '14', '/public/pic_content/img/a3/LOTNO_304_1.jpg,/public/pic_content/img/a3/LOTNO_304_2.jpg,/public/pic_content/img/a3/LOTNO_304_3.jpg', '304', '10,000 - 15,000', '', null, null, null, null, '', '數量：一筒七片<br />\n總重量：約2,706g<br />\n倉儲：乾倉<br />\n年份：2006年<br />\n<br />\n此品以巴達山喬木王為料，乾倉儲存，品相出色。', '100', null, null, null, null, null), ('242', '8 Pieces Of Pumpkin Tribute Tea, 1980’s', '八十年代 金瓜貢茶', '14', '/public/pic_content/img/a3/LOTNO_305_1.jpg,/public/pic_content/img/a3/LOTNO_305_2.jpg,/public/pic_content/img/a3/LOTNO_305_3.jpg', '305', '30,000 - 50,000', '', null, null, null, null, '', '數量：八粒<br />\n總重量：約3,833g<br />\n年份：80年代<br />\n<br />\n此金瓜貢茶是普洱茶獨有的一種特殊緊壓茶形式，又稱團茶，配料以芽頭為主，自然倉存放。因其形似南瓜，茶芽長年陳放後色澤金黃，得名金瓜；早年的金瓜茶是專為上貢朝廷而制，故名「金瓜貢茶」。本拍品市面較少。', '100', null, null, null, null, null), ('243', '4 Pieces Of Pomelo Pith Tea, 1980’s', '八十年代 柚皮茶', '14', '/public/pic_content/img/a3/LOTNO_306_1.jpg,/public/pic_content/img/a3/LOTNO_306_2.jpg', '306', '15,000 - 22,000', '', null, null, null, null, '', '數量：四粒<br />\n總重量：約385g<br />\n年份：80年代<br />\n<br />\n將柚子瓤取出後的柚皮，經晒乾後裝入普洱茶作儲茶的方式不僅通風，亦可隔絕外界氣味污染，使其中的普洱茶熏染上怡人的柚香氣。', '100', null, null, null, null, null), ('244', '7 Cans Of Yunnan Pu-er Tea, 1980&#039;s', '雲南舊普洱茶', '14', '/public/pic_content/img/a3/LOTNO_307_1.jpg,/public/pic_content/img/a3/LOTNO_307_2.jpg,/public/pic_content/img/a3/LOTNO_307_3.jpg', '307', '', '30,000', null, null, null, null, '', '數量：七罐<br />\n總重量：約3,482g<br />\n年份：約80年代<br />\n<br />\n一組七罐的雲南舊普洱茶，為當時美隆洋行代理分銷，以出口海外的雲南普洱，常飲生津解渴，有益健康。', '100', null, null, null, null, null), ('245', '2 Cans Of Oriental Beauty Tea, 2017', '2017年 全國東方美人茶 特等獎', '14', '/public/pic_content/img/a3/LOTNO_308_1.jpg', '308', '45,000 - 65,000', '', null, null, null, null, '', '數量：兩罐<br />\n總重量：約300g<br />\n年份：2017年<br />\n<br />\n本拍品榮獲2017年全台灣東方美人茶比賽的最高榮譽特等獎，是台灣最知名的茶品之一，它具有採收量少(一斤茶有四萬芽頭)、有機天然的特色；而且帶甘甜蜜香、花香、果香，深受世界各地茶友所愛戴，為收藏品飲的最佳選擇。', '100', null, null, null, null, null), ('246', '2 Cans Of Oriental Beauty Tea, 2015', '2015年 新竹東方美人茶 特等獎', '14', '/public/pic_content/img/a3/LOTNO_309_1.jpg', '309', '38,000 - 60,000', '', null, null, null, null, '', '數量：兩罐<br />\n總重量：約300g<br />\n年份：2015年<br />\n<br />\n這一組兩罐的拍品曾獲得2015年新竹東方美人茶比賽最高等級特等獎，質素之佳不容置疑。東方美人茶是台灣特有的茶品，這種茶最特別之處在於必須經由小綠葉蟬刺吸而生成，侵害愈嚴重茶品質愈好。除了東方美人茶外，它還叫白毫烏龍茶或椪風茶，為收藏佳品。', '100', null, null, null, null, null), ('247', '3 Packs And A Piece Of ‘Xia Guan’ Tuacha, 1990’s', '九十年代 下關寶焰牌緊茶', '14', '/public/pic_content/img/a3/LOTNO_310_1.jpg,/public/pic_content/img/a3/LOTNO_310_2.jpg', '310', '30,000 - 50,000', '', null, null, null, null, '', '數量：四條十二粒加一粒<br />\n總重量：約3,098g<br />\n年份：90年代<br />\n<br />\n下關緊茶名揚天下，一批共十三顆的寶焰牌緊茶，其中三條為原廠紙袋包裝，倉儲良好，品相極佳。寶焰緊茶的裡料採用較為粗壯的8至10級茶菁，轉化後甘甜的程度較高，因此三十多年陳期的緊茶，已屬品飲佳品。下關沱茶名揚天下，如此品相倍添收藏價值。', '100', null, null, null, null, null), ('248', '18 Pieces Of Pu-er Tea Bricks, 1980&#039;s', '八十年代 普洱熟磚', '14', '/public/pic_content/img/a3/LOTNO_311_1.jpg,/public/pic_content/img/a3/LOTNO_311_2.jpg', '311', '', '90,000', null, null, null, null, '', '數量：共十八塊<br />\n總重量：約4,383g<br />\n年份：80年代<br />\n<br />\n此拍品與70年代文革磚一樣，同為景谷茶廠之出品；一組十八件的油光紙包裝茶磚倉存良好，兩者口感相約，故被茶友視為文革磚的延續產品，收藏自用皆宜。<br />\n有關資料可參閱《深邃的七子世界》第415頁。', '100', null, null, null, null, null), ('249', '20 Pieces Of Pu-er Tea Bricks, 1990’s', '九十年代 普洱熟磚', '14', '/public/pic_content/img/a3/LOTNO_312_1.jpg,/public/pic_content/img/a3/LOTNO_312_2.jpg', '312', '', '25,000', null, null, null, null, '', '數量：共二十塊<br />\n總重量：約4,886g<br />\n年份：90年代', '100', null, null, null, null, null), ('250', '16 Pieces Of Pu-er Tea Bricks, 1990&#039;s', '九十年代 普洱熟磚', '14', '/public/pic_content/img/a3/LOTNO_313_1.jpg,/public/pic_content/img/a3/LOTNO_313_2.jpg', '313', '40,000 - 60,000', '', null, null, null, null, '', '數量：共十六塊<br />\n總重量：約3,945g<br />\n年份：90年代<br />\n<br />\n一組十六件熟茶磚以土黃紙包裝，熟茶口感醇厚，茶性溫和，為品飲佳品。<br />\n有關資料可參閱《深邃的七子世界》第418頁。', '100', null, null, null, null, null), ('251', '5 Pieces Of‘Yi Chang Hao’Tea Bricks, 2004', '易昌號方磚', '14', '/public/pic_content/img/a3/LOTNO_314_1.jpg,/public/pic_content/img/a3/LOTNO_314_2.jpg', '314', '', '3,000', null, null, null, null, '', '數量：共五片<br />\n總重量：約1,742g<br />\n年份：2004年<br />\n<br />\n易昌號選用西雙版納易武茶區野生野放茶為主，按傳統方法石磨加工而成。餅面見「易昌」二字。', '100', null, null, null, null, null), ('252', '2 Tongs Of Silver Dayi Pu-er Tea Cake, 2003', '03 銀大益青餅', '14', '/public/pic_content/img/a3/LOTNO_315_1.jpg,/public/pic_content/img/a3/LOTNO_315_2.jpg,/public/pic_content/img/a3/LOTNO_315_3.jpg,/public/pic_content/img/a3/LOTNO_315_4.jpg', '315', '', '45,000', null, null, null, null, '', '數量：兩筒共十四片<br />\n總重量：約5,150g<br />\n倉儲：純乾倉<br />\n年份：2003年<br />\n<br />\n兩筒純乾倉儲存的銀大益於2003年生產，經過十多年轉化，茶葉已泛油光，茶香輕微帶甜清爽。藏品竹箬包裝品相良好，作為漸進佳境而有年份的大益知名茶品，收藏及品飲價值與日俱增。<br />\n有關資料可參閱《大益普洱茶大事典 1994-2007》第115頁。', '100', null, null, null, null, null), ('253', 'A Tong Of Gold Dayi Pu-er Tea Cake, 2003', '03 金大益青餅', '14', '/public/pic_content/img/a3/LOTNO_316_1.jpg,/public/pic_content/img/a3/LOTNO_316_2.jpg,/public/pic_content/img/a3/LOTNO_316_3.jpg', '316', '', '40,000', null, null, null, null, '', '數量：一筒共七片<br />\r\n總重量：約2,599g<br />\r\n倉儲：乾倉<br />\r\n年份：2003年<br />\r\n<br />\r\n本拍品於乾倉陳放，加上用料上乘及陳化表現佳，故廣受茶界肯定，成為普洱茶餅的經典作品，深具收藏價值。<br />\r\n此茶為大益茶廠於2003年初製作，因包裝字體呈金色，故被稱為「金大益」。<br />\r\n有關資料可參閱《大益普洱茶大事典 1994-2007》第114頁。', '100', null, null, null, null, null), ('254', '9 Pieces of Sterling Silver Sencha Nodate Set with Signature', '宗文翁造 純銀槌目紋煎茶器套裝 一套九件', '15', '/public/pic_content/img/a4/LOTNO_101_1.jpg,/public/pic_content/img/a4/LOTNO_101_2.jpg,/public/pic_content/img/a4/LOTNO_101_3.jpg', '101', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：純銀、宗文翁<br />\n共箱 <br />\n茶壺 | H15.5cm，D15cm，重399g<br />\n急須 | H6cm，D13cm，重203g<br />\n茶罐 | H5cm，D14cm，重201g<br />\n建水 | H5cm，D10cm，重180g<br />\n茶托 | D10.5cm，重70g（共五隻）<br />', '100', null, null, null, null, null), ('255', 'A Sterling Silver Kettle With Signature', '長二齋造 花蝶圖銀壺', '15', '/public/pic_content/img/a4/LOTNO_102_1.jpg,/public/pic_content/img/a4/LOTNO_102_2.jpg,/public/pic_content/img/a4/LOTNO_102_3.jpg', '102', '150,000 - 220,000', '', null, null, null, null, 'Please refer to Chinese website', '蓋內款：長二齋<br />\nH17cm，D13cm，316g<br />\n<br />\n此藏品造型挺拔清秀，精美雅致，寶珠形壺身配流暢的三彎嘴，高身提把令整體比例更顯修長雋逸，配以鏤空靈活摘的更見匠心。器身的花蝶圖繁荗盛放，彩蝶翩翩，整體佈局疏密有致，生機盎然。', '100', null, null, null, null, null), ('256', 'A Carved And Signed Iron Tetsubin by Kibundo', '龜文堂 波多野正平造 風景人物圖鐵壺', '15', '/public/pic_content/img/a4/LOTNO_103_1.jpg,/public/pic_content/img/a4/LOTNO_103_2.jpg,/public/pic_content/img/a4/LOTNO_103_3.jpg', '103', '150,000 - 280,000', '', null, null, null, null, 'Please refer to Chinese website', '器身款：日本龜文<br />\n器底有款<br />\nH21cm，D16cm<br />\n<br />\n此藏品乃龜文堂創始人波多野正平精心鍛造，以寶珠形製，器身浮雕眾人於石蒼松下偷得浮生，綴點點銀花平凡中見巧思。銅蓋質地厚實，壺把仿竹節形製，相互呼應。<br />\n明治時期波多野正平（1812至1892）師從于龍文堂堂主四方安之助，學藝大成，尤以脫蠟製造而出名。出徒後離開龍文堂，他自立門戶自詡為龜文，創建了自己的堂號「龜文堂」。<br />', '100', null, null, null, null, null), ('257', 'A Carved Iron Tetsubin', '大丸好竹葉紋鐵壺', '15', '/public/pic_content/img/a4/LOTNO_104_1.jpg,/public/pic_content/img/a4/LOTNO_104_2.jpg', '104', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '器身圓款<br />\nH19cm，D15.5cm', '100', null, null, null, null, null), ('258', 'A Carved Gold-Inlaid Iron Tetsubin by Kinryudo', '金龍堂造 嵌金銀茶道具紋鐵壺', '15', '/public/pic_content/img/a4/LOTNO_105_1.jpg,/public/pic_content/img/a4/LOTNO_105_2.jpg,/public/pic_content/img/a4/LOTNO_105_3.jpg,/public/pic_content/img/a4/LOTNO_105_4.jpg', '105', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '蓋內款：金龍堂造<br />\n器身有款<br />\n共箱<br />\nH21cm，D16cm<br />', '100', null, null, null, null, null), ('259', 'A Carved Gold-Inlaid Iron Tetsubin', '嵌金山水家屋圖鐵壺', '15', '/public/pic_content/img/a4/LOTNO_106_1.jpg,/public/pic_content/img/a4/LOTNO_106_2.jpg', '106', '48,000 - 68,000', '', null, null, null, null, 'Please refer to Chinese website', 'H20cm，D16.5cm', '100', null, null, null, null, null), ('260', 'A Sterling Silver Kettle with Heart Sutra Inscription', '般若心經 純銀茶壺', '15', '/public/pic_content/img/a4/LOTNO_107_1.jpg,/public/pic_content/img/a4/LOTNO_107_2.jpg', '107', '68,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '壺身銘刻：《般若波羅蜜多心經》<br />\n底款：純銀<br />\n共箱<br />\nH16.5cm，D15cm，重352g<br />', '100', null, null, null, null, null), ('261', 'A Tin Tea Caddy', '古錫茶罐', '15', '/public/pic_content/img/a4/LOTNO_108_1.jpg', '108', '12,000 - 20,000', '', null, null, null, null, 'Please refer to Chinese website', 'H22cm，D17cm', '100', null, null, null, null, null), ('262', 'A Yixing Teapot in Lotus Shape', '朱泥蓮子壺', '15', '/public/pic_content/img/a4/LOTNO_109_1.jpg,/public/pic_content/img/a4/LOTNO_109_2.jpg,/public/pic_content/img/a4/LOTNO_109_3.jpg', '109', '8,000 - 12,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：清遠留香<br />\n壺把及蓋內有款<br />\nH7.5cm，D11cm<br />\n<br />\n此壺為小煤窯朱泥製，泥料純正，外型討好。<br />', '100', null, null, null, null, null), ('263', 'A Yixing Teapot  in Lotus Shape With Signature', '惠孟臣款 朱泥蓮子壺', '15', '/public/pic_content/img/a4/LOTNO_110_1.jpg,/public/pic_content/img/a4/LOTNO_110_2.jpg,/public/pic_content/img/a4/LOTNO_110_3.jpg', '110', '8,000 - 12,000', '', null, null, null, null, 'Please refer to Chinese website', '壺底銘刻：丁亥慶日名人法古<br />\n落款：惠、孟臣<br />\nH8cm，D12cm<br />', '100', null, null, null, null, null), ('264', 'A Carved Yixing Teapot, Late Qing', '晚清 金士恆刻 紫泥茶壺', '15', '/public/pic_content/img/a4/LOTNO_111_1.jpg,/public/pic_content/img/a4/LOTNO_111_2.jpg,/public/pic_content/img/a4/LOTNO_111_3.jpg', '111', '16,000 - 28,000', '', null, null, null, null, 'Please refer to Chinese website', '壺身銘刻：...光緒十二年 中秋夜 大清 金士恆 篆<br />\n刻款：金士恆 篆<br />\n共箱<br />\nH5.5cm，D9.5cm<br />', '100', null, null, null, null, null), ('265', 'A Signed Yixing Teapot', '澹然齋 紫泥甕式壺', '15', '/public/pic_content/img/a4/LOTNO_112_1.jpg,/public/pic_content/img/a4/LOTNO_112_2.jpg,/public/pic_content/img/a4/LOTNO_112_3.jpg', '112', '40,000 - 60,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：澹然齋<br />\nH9.5cm，D14cm<br />\n<br />\n此壺以甕式形製，咀成三彎，圈把鼓的。紫泥調砂，梨皮隱現，古意盎然。底款澹然齋見於清代，亦頗有名氣。', '100', null, null, null, null, null), ('266', 'A Carved Bronze Gold And Silver-Inlaid Box And Cover By Kuroda', '京都黑田造 靜明刻 四方嵌金銀花鳥圖茶罐', '15', '/public/pic_content/img/a4/LOTNO_113_1.jpg,/public/pic_content/img/a4/LOTNO_113_2.jpg,/public/pic_content/img/a4/LOTNO_113_3.jpg,/public/pic_content/img/a4/LOTNO_113_4.jpg,/public/pic_content/img/a4/LOTNO_113_5.jpg,/public/pic_content/img/a4/LOTNO_113_6.jpg,/public/pic_content/img/a4/LOTNO_113_7.jpg,/public/pic_content/img/a4/LOTNO_113_8.jpg', '113', '180,000 - 280,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：京都黑田造<br />\n器身款：靜明刻<br />\n共箱<br />\nH12cm，D6cm<br />\n<br />\n四方盒身色澤沉穩柔潤，造形正氣，下承四足。器身四面精工鑲嵌不同圖樣，分別有象徵長壽的仙鶴、有擺出各種仙姿神態自若的靈鳥、又見一只小舟於富士山下緩緩駛航，意景非凡。銀蓋上佈錦繡繁花，滲人的香韻飄飄裊裊，四周見彩蝶翩翩，意趣非凡。', '100', null, null, null, null, null), ('267', 'A Sterling Silver Kettle With Ratten Handle', '純銀 大槌目紋藤把茶壺', '15', '/public/pic_content/img/a4/LOTNO_114_1.jpg,/public/pic_content/img/a4/LOTNO_114_2.jpg,/public/pic_content/img/a4/LOTNO_114_3.jpg', '114', '35,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：純銀<br />\nH15cm，D12.5cm，重341g<br />\n<br />\n大槌目紋銀壺與藤把帶出粗獷的味道，配三環玉摘頓時增添一絲柔美之感，形成強烈對比，平凡中見拙趣。', '100', null, null, null, null, null), ('268', 'A Sterling Silver Kettle with a Phoenix Spout by Masaharu Nagoshi', '名越彌五郎 昌晴造 槌目寶珠形鳳口銀壺', '15', '/public/pic_content/img/a4/LOTNO_115_1.jpg,/public/pic_content/img/a4/LOTNO_115_2.jpg,/public/pic_content/img/a4/LOTNO_115_3.jpg,/public/pic_content/img/a4/LOTNO_115_4.jpg,/public/pic_content/img/a4/LOTNO_115_5.jpg,/public/pic_content/img/a4/LOTNO_115_6.jpg', '115', '68,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：昌晴造<br />\n箱蓋款：名越彌五郎<br />\n蓋內題：銀瓶 明治三十年酉春造之<br />\nH19.5cm，D16cm，重481g<br />', '100', null, null, null, null, null), ('269', 'A Carved Gold-and-Silver-Inlaid Iron Tetsubin by Ryubundo', '龍文堂 安之介造 嵌金銀鳳首鐵壺', '15', '/public/pic_content/img/a4/LOTNO_116_1.jpg,/public/pic_content/img/a4/LOTNO_116_2.jpg,/public/pic_content/img/a4/LOTNO_116_3.jpg,/public/pic_content/img/a4/LOTNO_116_4.jpg', '116', '100,000 - 160,000', '', null, null, null, null, 'Please refer to Chinese website', '器身橫款：龍文堂造<br />\n蓋內款：龍文堂造<br />\nH19.5cm，D16.6cm<br />\n<br />\n此拍品為龍文堂安之介所造，在樸拙的荒地肌理上巧用金銀嵌技藝，一面描繪花卉爭妍，吐豔欲滴的瑰麗；另一邊巧嵌靈蟲，靈氣逼人。流口巧妙依形打造成引頸長鳴的鳳凰，柳葉細目，出神入化，為此壺增添一抹靈動的神韻。', '100', null, null, null, null, null), ('270', 'A Carved Iron Tetsubin by Kinjyudo', '金壽堂造 漢詩文鐵壺', '15', '/public/pic_content/img/a4/LOTNO_117_1.jpg,/public/pic_content/img/a4/LOTNO_117_2.jpg,/public/pic_content/img/a4/LOTNO_117_3.jpg', '117', '120,000 - 180,000', '', null, null, null, null, 'Please refer to Chinese website', '蓋內款：金壽堂造<br />\nH22cm，D17cm<br />\n<br />\n此金壽堂鐵壺為明治丙申年間所製（1896），壺身銘刻「出議一縣經濟，入圖一村良計。明治丙申霖災，村田埋水閉。君為水利委員，克施設克宰製。堤障爰告竣成，君功不朽萬世」，是為感謝其治水之功以送贈，充份展現當時史實、民風、文化與工藝的結合，值得流傳。', '100', null, null, null, null, null), ('271', 'A Gold Kettle with Arare Embossment', '純金 鏤空花瓣鈕霧霰紋壺', '15', '/public/pic_content/img/a4/LOTNO_118_1.jpg,/public/pic_content/img/a4/LOTNO_118_2.jpg,/public/pic_content/img/a4/LOTNO_118_3.jpg', '118', '280,000 - 480,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：純金<br />\n箱蓋有款<br />\n共箱<br />\nH17cm，D16cm，重749g<br />\n<br />\n霰霧技法，是指匠師利用凹凸面的特殊釘子和自製工具，在壺身鑿出大小不一卻整齊有致的霰粒。此霧霰紋純金壺壺體飽滿圓潤，金色燦燦，霰紋滿鋪勻稱金質獨有的溫潤光芒與匠師的沉著耐心和純熟手藝相融，使擁有一把尊貴金壺已成為高級藏家的標誌。', '100', null, null, null, null, null), ('272', 'A Gold Kyusus with Arare Embossment with Signature', '純金 芳久作 霧霰紋牙把急須', '15', '/public/pic_content/img/a4/LOTNO_119_1.jpg,/public/pic_content/img/a4/LOTNO_119_2.jpg', '119', '180,000 - 280,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：純金、芳久作<br />\nH8.5cm，D13cm，331g<br />\n<br />\n此藏品純金鍛造，小巧壺周身飾玉霰珠粒，壺蓋上摘鈕為球形切面，華貴時尚。配素三彎流，出水順暢；乳白色牙側把則有助斷熱，尊貴中又添雅趣。金壺煮水，清心甘甜，入口順滑。', '100', null, null, null, null, null), ('273', 'A Copper-Coated Sterling Silver Kettle with Wave Design and Signature', '長島作 海波紋銅包銀壺', '15', '/public/pic_content/img/a4/LOTNO_120_1.jpg,/public/pic_content/img/a4/LOTNO_120_2.jpg', '120', '150,000 - 220,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：長島、純銀<br />\n共箱<br />\nH17cm，D16cm，554g<br />\n<br />\n此壼以銅為衣，銀為身，以含蓄厚實的銅色來表現翻騰的海浪拍打狀；整體呈現高雅的色肌，柔美的線條，兩種金屬的融合之美讓人愛不釋手。', '100', null, null, null, null, null), ('274', 'A Gold Kettle with Arare Embossment with Signature', '久進作 純金 鏤空花瓣鈕霧霰紋壺', '15', '/public/pic_content/img/a4/LOTNO_121_1.jpg,/public/pic_content/img/a4/LOTNO_121_2.jpg,/public/pic_content/img/a4/LOTNO_121_3.jpg', '121', '320,000 - 480,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：久進作、純金<br />\n蓋內款；純金<br />\n箱面題：純金製霰湯沸<br />\n箱蓋內款：久進作<br />\n共箱<br />\nH18cm，D13cm，754g<br />\n<br />\n此壺由純金鍛造，壺身飽滿大方，玉霧珠粒顆顆尖圓扎實，手感沉甸穩重，大氣而華貴。 三彎壺流飽滿流暢，壺蓋上花苞摘鈕靈動精巧，畫龍點睛；搭配光潔直立提把，尊貴清雅。金壺煮水，清心甘甜，入口順滑，令人陶醉於其中。', '100', null, null, null, null, null), ('275', 'A Carved Gold-and-Silver-Inlaid Iron Tetsubin by Kougyokudo', '光玉堂 嵌金銀山水家屋圖鐵壺', '15', '/public/pic_content/img/a4/LOTNO_122_1.jpg,/public/pic_content/img/a4/LOTNO_122_2.jpg,/public/pic_content/img/a4/LOTNO_122_3.jpg,/public/pic_content/img/a4/LOTNO_122_4.jpg', '122', '80,000 - 120,000', '', null, null, null, null, 'Please refer to Chinese website', '蓋內款：光玉堂造<br />\n共箱<br />\nH21cm，D17cm<br />\n<br />\n以金絲銀線嵌在飽滿的壺身上，山水家屋，林木蔥蘢，水波徐徐，一副禪意融融之境。荒肌紋理的肌底，敦厚的鐵壁，與刻畫的幽靜之境，對比鮮明，卻又剛柔並濟，營造出別具一格的氛圍。', '100', null, null, null, null, null), ('276', 'A Set Of Nine Pieces Carved Sterling Silver Tea Set With Floral Design', '純銀 四君子茶具套組 一套九件', '15', '/public/pic_content/img/a4/LOTNO_123_1.jpg,/public/pic_content/img/a4/LOTNO_123_2.jpg,/public/pic_content/img/a4/LOTNO_123_3.jpg', '123', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：純銀<br />\n共箱<br />\n茶 壺 | H19 cm，D16cm，重540g<br />\n急 須 | H7.5 cm，D12.5cm，重207g<br />\n建 水 | H8.5 cm，D12cm，重418g<br />\n茶 罐 | H9 cm，D11cm，重310g<br />\n茶 托 | 共5枚，D10.5 cm，重76g<br />\n<br />\n整套茶具套組線均以純銀所製，器形渾厚飽滿，簡潔大器。以刀代筆生動刻畫出四君子梅蘭菊竹的美態，其工精秀，佈局疏密有致，將梅花高潔、蘭花幽芳、竹子挺拔、菊花傲霜等自然屬性表露無遺，充滿生命力。', '100', null, null, null, null, null), ('277', 'A Carved Sterling Silver Kettle with Jade Knob', '玉環鈕摘銀壺', '15', '/public/pic_content/img/a4/LOTNO_124_1.jpg,/public/pic_content/img/a4/LOTNO_124_2.jpg,/public/pic_content/img/a4/LOTNO_124_3.jpg', '124', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', 'H19.5cm，D15.5cm，重534g<br />\n<br />\n閃亮圓渾的銀壺，以三環玉摘輕輕點綴，流光溢彩難掩高雅之氣；銀壺煮水自古為達官貴人所用，乃收藏不二之選。', '100', null, null, null, null, null), ('278', 'A Set Of Golden Wine Vessel And Two Wine Cups with Arare Embossment By Yoshihisa', '純金 芳久作 霧霰紋酒器連酒杯兩隻', '15', '/public/pic_content/img/a4/LOTNO_125_1.jpg,/public/pic_content/img/a4/LOTNO_125_2.jpg,/public/pic_content/img/a4/LOTNO_125_3.jpg,/public/pic_content/img/a4/LOTNO_125_4.jpg', '125', '220,000 - 380,000', '', null, null, null, null, 'Please refer to Chinese website', '酒器底款：純金、芳久作<br />\n酒杯底款：純金<br />\n酒器：H14cm，D7cm，348g<br />\n酒杯：H4.5cm，D5.5cm，共157g<br />\n<br />\n芳久本名大渡治兵衛，幕府末期人，是幕府時期著名金工釜師芳繼的入室弟子。其作品盡得真傳：一套三件的酒器皆以霧霰紋作，大小有序的霰粒均勻分佈於器物上，見精湛工藝。', '100', null, null, null, null, null), ('279', 'A Signed Yixing Teapot With Signature', '大彬款 朱泥蓮子壺', '15', '/public/pic_content/img/a4/LOTNO_126_1.jpg,/public/pic_content/img/a4/LOTNO_126_2.jpg,/public/pic_content/img/a4/LOTNO_126_3.jpg,/public/pic_content/img/a4/LOTNO_126_4.jpg', '126', '16,000 - 28,000', '', null, null, null, null, 'Please refer to Chinese website', '器底題：方吟李白成三夢<br />\n落款：大彬<br />\n共箱<br />\nH6.5cm，D10.5cm<br />', '100', null, null, null, null, null), ('280', 'A Pair Of Yixing Teapot', '紫泥具輪珠茶壺 一對', '15', '/public/pic_content/img/a4/LOTNO_127_1.jpg,/public/pic_content/img/a4/LOTNO_127_2.jpg,/public/pic_content/img/a4/LOTNO_127_3.jpg', '127', '12,000 - 20,000', '', null, null, null, null, 'Please refer to Chinese website', '共箱，蓋面題字<br />\nH6cm，D9cm<br />', '100', null, null, null, null, null), ('281', 'A Yixing Teapot in Early Qing Dynasty', '清 紫泥高梨壺', '15', '/public/pic_content/img/a4/LOTNO_128_1.jpg,/public/pic_content/img/a4/LOTNO_128_2.jpg,/public/pic_content/img/a4/LOTNO_128_3.jpg', '128', '8,000 - 15,000', '', null, null, null, null, 'Please refer to Chinese website', '<br />\n共箱<br />\nH9cm，D11.5cm<br />\n<br />\n此壺為清早期作品，泥料純正，壺咀造形靈巧生動。<br />', '100', null, null, null, null, null), ('282', 'A Carved Stoneware Teapot', '白泥詩文壺', '15', '/public/pic_content/img/a4/LOTNO_129_1.jpg,/public/pic_content/img/a4/LOTNO_129_2.jpg', '129', '8,000 - 12,000', '', null, null, null, null, 'Please refer to Chinese website', '器身銘刻：流水今日明月前身<br />\n器身有款<br />\nH11cm，D16cm<br />', '100', null, null, null, null, null), ('283', 'A Carved Bronze Tripod Censer With Signature', '英青造 銅製饕餮紋獸耳三足涼爐', '15', '/public/pic_content/img/a4/LOTNO_130_1.jpg,/public/pic_content/img/a4/LOTNO_130_2.jpg,/public/pic_content/img/a4/LOTNO_130_3.jpg', '130', '12,000 - 18,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：英青堂造<br />\n箱面題：涼爐 英青堂造<br />\n共箱<br />\nH15cm，W14.5cm<br />', '100', null, null, null, null, null), ('284', 'A Pair of Carved Wooden Chasokus with Floral Design', '木雕花草圖茶則一對', '15', '/public/pic_content/img/a4/LOTNO_131_1.jpg,/public/pic_content/img/a4/LOTNO_131_2.jpg', '131', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', '器身刻：風細心香<br />\n落款：哲齋刀<br />\n茶則一 | L17cm，D5cm<br />\n茶則二 | L20cm，D5cm<br />', '100', null, null, null, null, null), ('285', 'A Pair of Carved Wooden Chasokus with Heart Sutra Inscription', '般若心經 木雕茶則一對', '15', '/public/pic_content/img/a4/LOTNO_132_1.jpg,/public/pic_content/img/a4/LOTNO_132_2.jpg', '132', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', '器身銘刻：《般若波羅蜜多心經》<br />\n茶則一 | L16cm，D5cm<br />\n茶則二 | L19cm，D6.5cm<br />', '100', null, null, null, null, null), ('286', 'A Set of Five Carved Copper ChatakuWith Snail Design', '大島如雲作 斑紫銅蝸牛茶托 一套五件', '15', '/public/pic_content/img/a4/LOTNO_133_1.jpg,/public/pic_content/img/a4/LOTNO_133_2.jpg', '133', '8,000 - 15,000', '', null, null, null, null, 'Please refer to Chinese website', '器身有款<br />\n箱蓋款：如雲作<br />\nD11.5cm<br />', '100', null, null, null, null, null), ('287', 'A Set of Four Carved Mother-Of-Pearl-Inlaid Black Lacquer Plates, Ming Dynasty', '明 黑漆嵌螺鈿人物紋盤 一套四件', '15', '/public/pic_content/img/a4/LOTNO_134_1.jpg,/public/pic_content/img/a4/LOTNO_134_2.jpg', '134', '12,000 - 20,000', '', null, null, null, null, 'Please refer to Chinese website', 'D13cm<br />\n<br />\n此藏品以螺鈿工藝細嵌出四幅人物圖，人物在山內樓閣之間，甚具古韻，栩栩欲活。', '100', null, null, null, null, null), ('288', 'A Set of Six Famille Rose Tea Cups', '福壽粉彩桃杯六隻', '15', '/public/pic_content/img/a4/LOTNO_135_1.jpg,/public/pic_content/img/a4/LOTNO_135_2.jpg,/public/pic_content/img/a4/LOTNO_135_3.jpg', '135', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：「大清光緒年製」<br />\nH5.5cm，W8.5cm<br />\n<br />\n此拍品一套六隻，胎輕薄而釉質老潤，青花瓷杯構圖淡雅閑逸，寫意性強；胎質細膩密致，釉質肥潤，以老彩繪出福壽仙桃，線條流暢有力，有長壽吉慶的美意。', '100', null, null, null, null, null), ('289', 'A signed And Carved Yixing Teapot, Late Qing', '清 楊氏梅樁壺', '15', '/public/pic_content/img/a4/LOTNO_136_1.jpg,/public/pic_content/img/a4/LOTNO_136_2.jpg,/public/pic_content/img/a4/LOTNO_136_3.jpg,/public/pic_content/img/a4/LOTNO_136_4.jpg', '136', '40,000 - 60,000', '', null, null, null, null, 'Please refer to Chinese website', '器底有款：楊氏<br />\n共箱<br />\nH10cm，D19cm<br />\n<br />\n此造形獨特的紫砂壺產於清末民初，壺上浮雕梅花嬌艷欲滴，生香處處；品茗時兼具賞花之樂為人生一大快事。<br />\n楊氏鳳年，為清中期紫砂大家楊彭年之妹，字玉禽，世稱「楊氏」；為清嘉慶、道光間宜興制壺高手。<br />', '100', null, null, null, null, null), ('290', 'A Pair Of Signed Yixing Teapots in Qing Dynasty', '萬豐順記 紫砂古春壺一對', '15', '/public/pic_content/img/a4/LOTNO_137_1.jpg,/public/pic_content/img/a4/LOTNO_137_2.jpg,/public/pic_content/img/a4/LOTNO_137_3.jpg,/public/pic_content/img/a4/LOTNO_137_4.jpg,/public/pic_content/img/a4/LOTNO_137_5.jpg', '137', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：萬豐順記<br />\n箱蓋題：具輪珠式紫泥茶瓶一雙<br />\n共箱<br />\nH7cm，D10cm<br />\n<br />\n對壺產於晚清時期，以甕式形製，配上六方扭摘造形少見，為收藏佳品。<br />', '100', null, null, null, null, null), ('291', 'A Pair Of Carved Tin Tea Caddy With Signature', '乾茂號造 錫製茶罐一對', '15', '/public/pic_content/img/a4/LOTNO_138_1.jpg,/public/pic_content/img/a4/LOTNO_138_2.jpg', '138', '5,000 - 8,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：乾茂號造<br />\nH8.5cm，D7.5cm<br />\n<br />\n圓潤討喜的器身上分別刻有蒼勁詩文及山水家屋圖，彩蝶翩翩其中，盡顯閑逸。', '100', null, null, null, null, null), ('292', 'A Set of Five Carved Copper and Wood Chataku By Kiyoshieki Kyushui', '中川靜益造 木茶托一套五件', '15', '/public/pic_content/img/a4/LOTNO_139_1.jpg,/public/pic_content/img/a4/LOTNO_139_2.jpg,/public/pic_content/img/a4/LOTNO_139_3.jpg', '139', '20,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '器底有款<br />\n箱蓋款：中川靜益<br />\nD10cm<br />\n<br />\n一套五件的木製茶托光素無紋，不事雕刻，在隱約透出的木質光澤下，更能感受到寂侘之美。<br />', '100', null, null, null, null, null), ('293', 'A Blue and White  Porcelain Box and Cover With Figures Design', '青花人物蓋盒', '15', '/public/pic_content/img/a4/LOTNO_140_1.jpg,/public/pic_content/img/a4/LOTNO_140_2.jpg,/public/pic_content/img/a4/LOTNO_140_3.jpg,/public/pic_content/img/a4/LOTNO_140_4.jpg', '140', '12,000 - 18,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：大明宣德年製<br />\n共箱<br />\nH14.5cm，D12cm<br />', '100', null, null, null, null, null), ('294', 'A Finely Carved Huanghuali Brushpot With Inscription', '黃花梨刻　明代沈度詩文筆筒', '15', '/public/pic_content/img/a4/LOTNO_141_1.jpg,/public/pic_content/img/a4/LOTNO_141_2.jpg', '141', '120,000 - 180,000', '', null, null, null, null, 'Please refer to Chinese website', '銘刻：敬齋箴<br />\n詩文題識：永樂十六年仲冬至日　翰林學士雲門沈度書<br />\nH22cm，D24cm<br />\n<br />\n筆筒以黃花梨為材，木料厚實，包漿均潤自然，紋理細緻；器身圓腹敞口，足平，巧刻明代沈度之《敬齋箴》。通篇結字勻稱，行列齊整，古樸有雅韻。伏案著書得此典雅的文房清供相伴，實為人生一大快事。黃花梨稀少難尋，身價矜貴，自古以來皆被奉為上品，值得收藏。', '100', null, null, null, null, null), ('295', 'A Cannibar Lacquer  Box and Cover With Dragon Design', '六角龍紋剔紅飾盒', '15', '/public/pic_content/img/a4/LOTNO_142_1.jpg,/public/pic_content/img/a4/LOTNO_142_2.jpg,/public/pic_content/img/a4/LOTNO_142_3.jpg', '142', '22,000 - 38,000', '', null, null, null, null, 'Please refer to Chinese website', '共箱<br />\nL30.5cm，D26.5cm，H10cm<br />\n<br />\n六角形飾盒造形別緻，器身中央浮雕飛龍騰雲出波，氣勢威猛，龍鬚飄動，栩栩如生，仿若神光華普照大地。飛龍外則細膩如繡地巧刻朵朵雲紋，與器身四圍的回紋互相點綴，刻工細膩，匠心獨運。', '100', null, null, null, null, null), ('296', 'A Cannibar Lacquer Tray with Floral Design', '花草詩文剔紅飾皿', '15', '/public/pic_content/img/a4/LOTNO_143_1.jpg,/public/pic_content/img/a4/LOTNO_143_2.jpg,/public/pic_content/img/a4/LOTNO_143_3.jpg', '143', '8,000 - 12,000', '', null, null, null, null, 'Please refer to Chinese website', '器底銘刻：「乾隆丙申孟春月御題」及詩文<br />\n器底有款<br />\nH3.5cm，D35cm<br />', '100', null, null, null, null, null), ('297', 'A Cinnabar Lacquer Plate with Figures Design', '剔紅泛舟遊湖雕花飾盤', '15', '/public/pic_content/img/a4/LOTNO_144_1.jpg,/public/pic_content/img/a4/LOTNO_144_2.jpg', '144', '2,000 - 3,000', '', null, null, null, null, 'Please refer to Chinese website', '共箱<br />\nD27cm<br />', '100', null, null, null, null, null), ('298', 'A Carved Gold-and-Silver-Inlaid Iron Tetsubin with Lotus Pond Design by Kinryudo', '金龍堂造 金銀象嵌荷塘花鳥圖鐵壺', '15', '/public/pic_content/img/a4/LOTNO_145_1.jpg,/public/pic_content/img/a4/LOTNO_145_2.jpg,/public/pic_content/img/a4/LOTNO_145_3.jpg', '145', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '蓋內款：金龍堂造<br />\nH15cm，D8.5cm<br />', '100', null, null, null, null, null), ('299', 'An Iron Tstsubin with Jade Knob by Ryubundo', '龍文堂造 碧玉鈕摘鐵壺', '15', '/public/pic_content/img/a4/LOTNO_146_1.jpg,/public/pic_content/img/a4/LOTNO_146_2.jpg,/public/pic_content/img/a4/LOTNO_146_3.jpg,/public/pic_content/img/a4/LOTNO_146_5.jpg', '146', '5,000 - 8,000', '', null, null, null, null, 'Please refer to Chinese website', '壺腹及壺蓋款：龍文堂造<br />\nH23cm，D18cm<br />', '100', null, null, null, null, null), ('300', 'A Carved And Signed Yixing Teapot in Gourd Shape', '邵生祥款 詩文葫蘆形紫砂壺', '15', '/public/pic_content/img/a4/LOTNO_147_1.jpg,/public/pic_content/img/a4/LOTNO_147_2.jpg,/public/pic_content/img/a4/LOTNO_147_3.jpg,/public/pic_content/img/a4/LOTNO_147_4.jpg,/public/pic_content/img/a4/LOTNO_147_5.jpg,/public/pic_content/img/a4/LOTNO_147_6.jpg', '147', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：邵生祥<br />\n壺身款：石泉刻<br />\n蓋內有款<br />\n共箱，蓋面題：宜興窯山水文字大急須<br />\nH11cm，D14cm<br />', '100', null, null, null, null, null), ('301', 'A Signed Yixing Teapot in Qing Dynasty', '清 萬豐順記 具輪珠紫砂茶壺', '15', '/public/pic_content/img/a4/LOTNO_148_1.jpg,/public/pic_content/img/a4/LOTNO_148_2.jpg,/public/pic_content/img/a4/LOTNO_148_3.jpg,/public/pic_content/img/a4/LOTNO_148_4.jpg', '148', '6,000 - 10,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：萬豐順記<br />\n共箱<br />\nH6cm，D10cm<br />', '100', null, null, null, null, null), ('302', 'A Sterling Silver Insect Cage With Signature', '宗好作 純銀蟲籠', '15', '/public/pic_content/img/a4/LOTNO_149_1.jpg,/public/pic_content/img/a4/LOTNO_149_2.jpg,/public/pic_content/img/a4/LOTNO_149_3.jpg,/public/pic_content/img/a4/LOTNO_149_4.jpg,/public/pic_content/img/a4/LOTNO_149_5.jpg', '149', '16,000 - 28,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：宗好、純銀<br />\n箱蓋款：宗好<br />\n蓋面題：蟲籠<br />\nH10.5cm，D11.5cm，L13.5cm，重596g（連繩）<br />', '100', null, null, null, null, null), ('303', 'A Sterling Silver Box and Cover by Zouroku', '藏六居造 純銀槌目紋果子器', '15', '/public/pic_content/img/a4/LOTNO_150_1.jpg,/public/pic_content/img/a4/LOTNO_150_2.jpg,/public/pic_content/img/a4/LOTNO_150_3.jpg,/public/pic_content/img/a4/LOTNO_150_4.jpg', '150', '6,000 - 10,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：藏六居造<br />\n共箱<br />\nH7.5cm，D12.5cm，重222g<br />', '100', null, null, null, null, null), ('304', 'Bomboo Vase By Third -generation Tanabe Chiku&#039;unsai', '三代田邊竹雲齋造 竹編花器', '15', '/public/pic_content/img/a4/LOTNO_151_1.jpg,/public/pic_content/img/a4/LOTNO_151_2.jpg,/public/pic_content/img/a4/LOTNO_151_3.jpg', '151', '6,000 - 12,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：竹雲齋造<br />\n箱面題：巖 花籃<br />\n箱蓋款：竹雲齋造<br />\nH10.5cm，D15cm<br />\n<br />\n花器以細緻流暢的竹枝藤紋編織，充份將竹藤編織的柔美，配搭與生俱來的色澤，呈現出材質的變化，頗見匠心。', '100', null, null, null, null, null), ('305', 'Bamboo Vase By Tanabe Chiku&#039;unsai', '初代田邊竹雲齋制 竹編花器', '15', '/public/pic_content/img/a4/LOTNO_152_1.jpg,/public/pic_content/img/a4/LOTNO_152_2.jpg,/public/pic_content/img/a4/LOTNO_152_3.jpg', '152', '16,000 - 28,000', '', null, null, null, null, 'Please refer to Chinese website', '底款：竹雲齋<br />\n箱蓋款：竹雲齋造<br />\nH32cm，D34cm<br />\n<br />\n此花器由竹編製而成，造型渾厚圓渾討喜潤，象徵圓滿。籃身以簡約淡雅的編織技法，展現竹子的特性及色澤；隨心而至，洋洋灑灑，整器散發著賞心悅目的氣質。', '100', null, null, null, null, null), ('306', 'Bamboo Vase With Signature', '籃鳳齋制 竹編花器', '15', '/public/pic_content/img/a4/LOTNO_153_1.jpg,/public/pic_content/img/a4/LOTNO_153_2.jpg,/public/pic_content/img/a4/LOTNO_153_3.jpg,/public/pic_content/img/a4/LOTNO_153_4.jpg', '153', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', '器身款：籃鳳齋<br />\n箱面款：平安籃鳳齋作<br />\nL31cm，D15cm<br />', '100', null, null, null, null, null), ('307', 'A Pair of Carved Bronze Vases in Birds Design', '嵌花鳥紋銅花瓶一對', '15', '/public/pic_content/img/a4/LOTNO_154_1.jpg,/public/pic_content/img/a4/LOTNO_154_2.jpg', '154', '22,000 - 38,000', '', null, null, null, null, 'Please refer to Chinese website', 'H24cm，D13.5cm<br />\n<br />\n厚重大器的銅製花瓶以花鳥為題：正面浮雕牡丹絢麗燦爛，背面見楊柳隨風搖曳，燕子盤旋於其中，靈動生香，春意綿綿。', '100', null, null, null, null, null), ('308', 'A Bronze Censer with Gold-Inlaid and Lion-Head Shaped Ears, With Base', '鎏金丸形獸耳香爐', '15', '/public/pic_content/img/a4/LOTNO_155_1.jpg,/public/pic_content/img/a4/LOTNO_155_2.jpg,/public/pic_content/img/a4/LOTNO_155_3.jpg,/public/pic_content/img/a4/LOTNO_155_4.jpg', '155', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：大明宣德年製<br />\n共箱，共座<br />\nH9cm，W14cm<br />\n<br />\n此香爐皮殼圓潤靚麗，包漿古樸；器上鎏金隨性散落，輔以雙獅含環作耳，凝重含蓄，大氣不凡。整器為銅質精製，手感沉重，器型比例協調，造形流暢。', '100', null, null, null, null, null), ('309', 'A Carved Bronze Censer With Gold And Silver Inlay Landscapes', '銅製浮雕山水樓閣人物圖香爐', '15', '/public/pic_content/img/a4/LOTNO_156_1.jpg,/public/pic_content/img/a4/LOTNO_156_2.jpg,/public/pic_content/img/a4/LOTNO_156_3.jpg,/public/pic_content/img/a4/LOTNO_156_4.jpg,/public/pic_content/img/a4/LOTNO_156_5.jpg,/public/pic_content/img/a4/LOTNO_156_6.jpg', '156', '280,000 - 400,000', '', null, null, null, null, 'Please refer to Chinese website', '器底有款<br />\nH12.5cm，D10cm<br />\n<br />\n此香爐以銅料為胚，立體浮雕山水家屋圖，工藝繁複奇巧。一面見家屋樓閣佇立於遠近山巒之中，刻畫仔細入微；另一邊刻小橋流水，松柏林蔭拔地參天，樹木蔥蘢又見碧波蕩漾，描繪出傳說中的蓬萊仙景，氣勢恢宏。木地爐蓋以菊紋為摘座，嵌雲紋銀絲，華美精湛；整器設計拙雅中見奢華，寓意安定富貴，繁榮不衰。', '100', null, null, null, null, null), ('310', 'A Carved Silver-Inlaid Bronze Tripod Censer with Signature', '石叟款 古銅嵌銀絲饕餮三足香爐', '15', '/public/pic_content/img/a4/LOTNO_157_1.jpg,/public/pic_content/img/a4/LOTNO_157_2.jpg,/public/pic_content/img/a4/LOTNO_157_3.jpg,/public/pic_content/img/a4/LOTNO_157_4.jpg', '157', '12,000 - 20,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：石叟<br />\n共箱<br />\nH15.5cm，D12 cm<br />', '100', null, null, null, null, null), ('311', 'An Irregular Shaped Vietnamese Nha Trang Green Qinan Bracelet', '越南芽莊 綠油奇楠隨形手串', '15', '/public/pic_content/img/a4/LOTNO_158_1.jpg,/public/pic_content/img/a4/LOTNO_158_2.jpg', '158', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南芽莊<br />\n尺寸不一，重10.3g<br />', '100', null, null, null, null, null), ('312', 'A 14mm Vietnamese Qinan Bracelet', '奇楠十四亳米手串', '15', '/public/pic_content/img/a4/LOTNO_159_1.jpg,/public/pic_content/img/a4/LOTNO_159_2.jpg', '159', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '奇楠共8顆，珠徑14mm，總重約18g<br />\n<br />\n此手串顆粒圓潤，均勻的油線及虎斑紋非常亮眼，其香氣涼韻短暫花香明顯，隱約能感受到身在萬花從中，質感醇厚；配上珊瑚隔珠，別具匠心。<br />', '100', null, null, null, null, null), ('313', 'A Qinan Necklace With Jade', '奇楠翡翠項錬', '15', '/public/pic_content/img/a4/LOTNO_160_1.jpg,/public/pic_content/img/a4/LOTNO_160_2.jpg,/public/pic_content/img/a4/LOTNO_160_3.jpg', '160', '150,000 - 250,000', '', null, null, null, null, 'Please refer to Chinese website', '尺寸不一，總約重17.2g<br />\n<br />\n此項錬形制獨特少見，珠粒大小相互分隔，伴以晶瑩翡翠於其中，形成獨特規律，別出心裁。此奇楠項鍊香韻清雅涼甜，表面滿佈獨特的油花紋，油線清晰可見，色澤凝練光鮮，包漿亮麗，品相上佳。', '100', null, null, null, null, null), ('314', 'A 10mm Vietnamese Yellow Qinan Malas', '越南 黃奇楠十亳米佛珠', '15', '/public/pic_content/img/a4/LOTNO_161_1.jpg,/public/pic_content/img/a4/LOTNO_161_2.jpg', '161', '150,000 - 280,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n108顆，珠徑10mm，重49g<br />\n<br />\n奇楠為一等香中的極品，自沉香中長出，有「一片萬金」的美譽。本藏品以溫潤有體的黃奇楠設計，色澤金黃，深棕色香脂射腺密致，禪意清雅。<br />\n初香柔軟，本香甜膩，尾韻轉為乳香，氣息悠遠，富於變化，香型甜涼純淨，實為奇楠中的逸品。<br />', '100', null, null, null, null, null), ('315', 'A 12mm Bruneian Agarwood Malas in Almost Sinking Grade', '汶萊 十毫米沉香佛珠', '15', '/public/pic_content/img/a4/LOTNO_162_1.jpg,/public/pic_content/img/a4/LOTNO_162_2.jpg,/public/pic_content/img/a4/LOTNO_162_3.jpg,/public/pic_content/img/a4/LOTNO_162_4.jpg', '162', '150,000 - 220,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：汶萊<br />\n108顆，珠徑10mm，香材約重71g，絕大部分沉水<br />\n<br />\n常戴沉香佛珠可以穩定情感，聞香悟道，靜心寧神。其芬馥香氣之薰染，不僅可醒腦怡神，亦有一定的健身之功效，具有怡情和保健的雙重含義。<br />\n此佛珠絕大部分沉水，油脂十足。初聞甘涼略帶乳香，尾韻散發醇厚的香味，層次明顯，變化無窮。飾翡翠數枚輕輕點綴，盡見巧思。<br />\n拍品另附佛珠三粒。<br />', '100', null, null, null, null, null), ('316', 'A 12mm Yellow Qinan 17 Beads Bracelet', '黃奇楠十二毫米十七子手串', '15', '/public/pic_content/img/a4/LOTNO_163_1.jpg,/public/pic_content/img/a4/LOTNO_163_2.jpg', '163', '80,000 - 150,000', '', null, null, null, null, 'Please refer to Chinese website', '珠徑12mm，17顆，重14.9g<br />\n<br />\n此黃奇楠手串色澤金黃，深棕色香脂射腺密致，禪意清雅。戴於手上時甜涼純正的香韻久久縈繞沁人心脾，變化萬千，叫人暢快愉悅。<br />', '100', null, null, null, null, null), ('317', 'A 18mm Bruneian Agarwood 13 Beads Bracelet in Sinking Grade', '沉水 汶萊沉香十八亳米十三子手串', '15', '/public/pic_content/img/a4/LOTNO_164_1.jpg,/public/pic_content/img/a4/LOTNO_164_2.jpg', '164', '90,000 - 140,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：汶萊<br />\n珠徑18mm，13顆，重44.2g<br />\n<br />\n汶萊屬於一流沉香的產區，由於其地域較小，產量不高，如拍品般沉水料更為稀少。賞沉香之本在於賞其香味，此手串帶甘甜乳香，層次變化無窮，香韻醇厚一流。', '100', null, null, null, null, null), ('318', 'A 12mm Bruneian Agarwood 16 Beads Bracelet in Sinking Grade', '汶萊 沉水十二亳米十六子手串', '15', '/public/pic_content/img/a4/LOTNO_165_1.jpg,/public/pic_content/img/a4/LOTNO_165_2.jpg', '165', '25,000 - 38,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：汶萊<br />\n珠徑12mm，16顆，重16.6g<br />', '100', null, null, null, null, null), ('319', 'A Piece of Carved Bruneian Agarwood Plaque With Dragon Design', '汶萊 龍戲珠沉香46牌', '15', '/public/pic_content/img/a4/LOTNO_166_1.jpg,/public/pic_content/img/a4/LOTNO_166_2.jpg', '166', '22,000 - 40,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：汶萊 <br />\nH6cm，D4cm，L1cm，重23 g<br />\n<br />\n作者依據木紋天然分佈，在牌上雕龍戲珠圖：油脂線紋理細密之下半部為滔滔海水，龍身自海中騰空而起回環盤繞，追逐火焰圍繞的寶珠，仿若神光華普照大地。', '100', null, null, null, null, null), ('320', 'A Piece Of Carved Agarwood in Lotus Shape', '沉香荷花擺件', '15', '/public/pic_content/img/a4/LOTNO_167_1.jpg', '167', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', 'L10cm，D3.5cm，重22.9g<br />\n共座<br />\n<br />\n難得一見的沉香擺設，以整塊沉香雕刻成一片連著蓮蓬之荷葉，油色自然，油線更顯清晰。巧配獨特海浪紋木座，尤似荷花飄盪湖面上，是上佳之沉香工藝品。', '100', null, null, null, null, null), ('321', 'A 8mm Vietnamese Nha Trang Agarwood Malas', '越南芽莊 八亳米沉香佛珠', '15', '/public/pic_content/img/a4/LOTNO_168_1.jpg,/public/pic_content/img/a4/LOTNO_168_2.jpg', '168', '50,000 - 80,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南芽莊<br />\r\n108顆，珠徑8mm，重31.5g，接近沉水<br />\r\n<br />\r\n沉香不僅氣味優雅宜人，且有養生療疾之效，自古以來就是備受珍視的香中神品。由於芽莊沉香香材珍貴，稀少難尋，所以價格居高難下，是藏家夢寐以求的上品沉香。此香香氣清涼雅致，散發著清爽的熟果氣息，甘冽宜人，使人清新舒心。', '100', null, null, null, null, null), ('322', 'A Set of Kyara Agarwood Pieces With Wooden Box', '五星等級名油伽羅（奇楠）揃件套裝', '15', '/public/pic_content/img/a4/LOTNO_169_1.jpg,/public/pic_content/img/a4/LOTNO_169_2.jpg,/public/pic_content/img/a4/LOTNO_169_3.jpg,/public/pic_content/img/a4/LOTNO_169_4.jpg,/public/pic_content/img/a4/LOTNO_169_5.jpg,/public/pic_content/img/a4/LOTNO_169_6.jpg', '169', '280,000 - 400,000', '', null, null, null, null, 'Please refer to Chinese website', '尺寸不一，共重45.8g<br />\n附五星等級證書<br />\n蓋面題：極品伽羅<br />\n共箱<br />\n<br />\n本藏品一套五件綜合四種極品奇楠(綠奇楠、紫奇楠、黃奇楠及黑奇楠)，每一種皆有其獨特香氣，不需點燃已有聞之不盡的香氣沁出；清、雅、透、厚的獨特氣息帶給藏家幻化多端的嗅覺體驗。', '100', null, null, null, null, null), ('323', 'A Carved Box and Cover with Gold-Inlaid by Komai', '駒井製 金工亭台樓閣圖香盒', '15', '/public/pic_content/img/a4/LOTNO_170_1.jpg,/public/pic_content/img/a4/LOTNO_170_2.jpg,/public/pic_content/img/a4/LOTNO_170_3.jpg,/public/pic_content/img/a4/LOTNO_170_4.jpg', '170', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：日本國 京都住 駒井製<br />\nL12cm，D8cm，H4.5cm<br />\n<br />\n金絲銀線勾勒出的山水樓閣，流水小橋，在盒面鋪出一派靜謐之境。遠方的富士山，白雪茫茫，氣度恢弘，雖只是遠觀，但足以神怡。細緻之極，器身四周還雕琢了仙鶴和花葉，典雅瑰麗，使整幅構圖更加完美，可見匠師心思。', '100', null, null, null, null, null), ('324', 'Vietnamese Nha Trang Green Qinan Powder', '越南芽莊 綠油奇楠粉', '15', '/public/pic_content/img/a4/LOTNO_171_1.jpg,/public/pic_content/img/a4/LOTNO_171_2.jpg', '171', '22,000 - 38,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南芽莊<br />\n重18g<br />', '100', null, null, null, null, null), ('325', 'Qinan Powder', '奇楠粉', '15', '/public/pic_content/img/a4/LOTNO_172_1.jpg,/public/pic_content/img/a4/LOTNO_172_2.jpg', '172', '38,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '重30g', '100', null, null, null, null, null), ('326', 'A Bronze Censer With Lion-Head Shaped Ears', '雙獅耳銅香爐', '15', '/public/pic_content/img/a4/LOTNO_173_1.jpg,/public/pic_content/img/a4/LOTNO_173_2.jpg,/public/pic_content/img/a4/LOTNO_173_3.jpg', '173', '8,000 - 15,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：大明宣德年製<br />\nH15cm，D36cm<br />', '100', null, null, null, null, null), ('327', 'A filigree Censer in Owl Shape, Qing Dynasty', '清 銀掐絲香籠', '15', '/public/pic_content/img/a4/LOTNO_174_1.jpg,/public/pic_content/img/a4/LOTNO_174_2.jpg', '174', '22,000 - 35,000', '', null, null, null, null, 'Please refer to Chinese website', 'H 17.5cm，D8cm，157g', '100', null, null, null, null, null), ('328', 'A Carved Bronze Censer with Gold and Silver Inlay', '明治金工嵌金銀荷鷺圖筒形香爐', '15', '/public/pic_content/img/a4/LOTNO_175_1.jpg,/public/pic_content/img/a4/LOTNO_175_2.jpg,/public/pic_content/img/a4/LOTNO_175_3.jpg,/public/pic_content/img/a4/LOTNO_175_4.jpg', '175', '16,000 - 22,000', '', null, null, null, null, 'Please refer to Chinese website', '共座<br />\nH7.5cm，D6cm', '100', null, null, null, null, null), ('329', 'Vietnamese Qinan Powder', '越南 奇楠粉', '15', '/public/pic_content/img/a4/LOTNO_176_1.jpg,/public/pic_content/img/a4/LOTNO_176_2.jpg', '176', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n重42g<br />\n<br />\n此香粉選自上等奇楠，為上爐品香之選。香韻醇厚一流，其奶香、蜜甜氣味鑽鼻，直入鼻腔，久久不散。<br />', '100', null, null, null, null, null), ('330', 'A Set of Vietnamese Qinan Pieces', '越南 奇楠碎料', '15', '/public/pic_content/img/a4/LOTNO_177_1.jpg,/public/pic_content/img/a4/LOTNO_177_2.jpg', '177', '80,000 - 120,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n尺寸不一，重61g<br />\n<br />\n奇楠為沉香中最高等級，其魅力在於熏燒時散發出的獨特香氣，把奇楠邊角直接上爐熏香，可延長熏香時間，使奇楠香氣完美散發。', '100', null, null, null, null, null), ('331', 'A Piece of Vietnamese Qinan', '越南 奇楠擺件', '15', '/public/pic_content/img/a4/LOTNO_178_1.jpg,/public/pic_content/img/a4/LOTNO_178_2.jpg,/public/pic_content/img/a4/LOTNO_178_3.jpg', '178', '180,000 - 300,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\nL11cm，D3.5cm，重61g<br />\n<br />\n此香凝練光鮮，形態自然；結油飽滿，包漿瑩潤富光澤，通體散發沈穩的氣質；大氣古樸的香材作上香或擺件皆宜。<br />\n<br />\n奇楠亦有君子品德以香喻人之智慧，其形成是來自罕有的真菌隨著水份進入樹心，木後受到傷害而慢慢滋長繁衍，成為奇楠；人生即使受盡苦疾，只要堅強面對，卻能化苦為奇香。', '100', null, null, null, null, null), ('332', 'A Piece of Qinan', '奇楠擺件', '15', '/public/pic_content/img/a4/LOTNO_179_1.jpg,/public/pic_content/img/a4/LOTNO_179_2.jpg,/public/pic_content/img/a4/LOTNO_179_3.jpg', '179', '120,000 - 200,000', '', null, null, null, null, 'Please refer to Chinese website', 'L14.5cm，D4.5cm，重117.5g<br />\n<br />\n此藏品表面泛出黑油，皮色深棕，如蒼山覆苔，豐澤映入。香味清新具穿透力，奇楠所蘊含的氣場能量，無形中更有安定人心、改變磁場之效益，實為上等賞玩之物。<br />', '100', null, null, null, null, null), ('333', 'A Piece of Yellow Qinan', '黃奇楠擺件', '15', '/public/pic_content/img/a4/LOTNO_180_1.jpg,/public/pic_content/img/a4/LOTNO_180_2.jpg', '180', '80,000 - 120,000', '', null, null, null, null, 'Please refer to Chinese website', 'H15.5cm，D8cm，重141g<br />\n<br />\n此山形擺件紋理細膩，質地密致，手感沉甸，外形挺直沉實。細聞味道使人心曠神怡，實心料燒之甘甜芳香，廣受香道愛好者歡迎。', '100', null, null, null, null, null), ('334', 'A Set of Vietnamese Qinan Pieces', '越南 奇楠塊', '15', '/public/pic_content/img/a4/LOTNO_181_1.jpg,/public/pic_content/img/a4/LOTNO_181_2.jpg,/public/pic_content/img/a4/LOTNO_181_3.jpg,/public/pic_content/img/a4/LOTNO_181_4.jpg', '181', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n尺寸不一，重55g<br />\n<br />\n自古以來奇楠便是高雅之物，被列為香中極品，到了清代，只有皇帝才有資格品香，外國進貢的上等沉香鎖在庫房裡，每天使用的數量會由專人負責記錄，皇帝高興時才會賞賜一些予近臣，彌足珍貴。此拍品味道醇厚而富有內涵，香氣清涼鮮靈，引人入勝。', '100', null, null, null, null, null), ('335', 'A Yellow Qinan Necklace', '黃奇楠飾錬', '15', '/public/pic_content/img/a4/LOTNO_182_1.jpg,/public/pic_content/img/a4/LOTNO_182_2.jpg', '182', '80,000 - 120,000', '', null, null, null, null, 'Please refer to Chinese website', '尺寸不一，重19.4g<br />\n<br />\n此品為同料所裁，出自上等奇楠，顆粒大小相隔，頗見匠心。細觀其肌理，見顆粒色澤分佈平均，油脂於毛孔滲出，聞之馥郁自然，穿透力十足，直入鼻腔良久不散，暢快愉悅。', '100', null, null, null, null, null), ('336', 'A 10mm Indonesia Agarwood Malas', '印尼 十毫米沉香佛珠', '15', '/public/pic_content/img/a4/LOTNO_183_1.jpg,/public/pic_content/img/a4/LOTNO_183_2.jpg', '183', '22,000 - 40,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：印尼<br />\n108顆，珠徑10mm，重42.9g<br />\n<br />\n產自印尼的佛珠油脂密度較高，獨特油花香線清晰鮮活，古淡大雅，渾然天成；適合輕撫手中或佩戴身上，氣味通透聞後使人放鬆平靜。', '100', null, null, null, null, null), ('337', 'A 7mm Indonesia Agarwood Malas', '印尼 七毫米沉香佛珠', '15', '/public/pic_content/img/a4/LOTNO_184_1.jpg,/public/pic_content/img/a4/LOTNO_184_2.jpg', '184', '4,000 - 6,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：印尼<br />\n108顆，珠徑7mm，重20.6g<br />', '100', null, null, null, null, null), ('338', 'A Set of Vietnamese Qinan Pieces', '越南 奇楠材料', '15', '/public/pic_content/img/a4/LOTNO_185_1.jpg,/public/pic_content/img/a4/LOTNO_185_2.jpg', '185', '40,000 - 60,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n尺寸不一，重30g<br />\n<br />\n此香材紋理清晰，表面可見深色油線，質地柔中帶剛；集天地靈氣，匯日月精華，渾然天成，愛香人士必愛不釋手。', '100', null, null, null, null, null), ('339', 'A Set of Vietnamese Qinan Pieces', '越南 奇楠材料', '15', '/public/pic_content/img/a4/LOTNO_186_1.jpg,/public/pic_content/img/a4/LOTNO_186_2.jpg', '186', '60,000 - 100,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\n尺寸不一，重40g<br />\n<br />\n奇楠為沉香之中的品質最上者，據說「一萬棵沉香中只有一棵奇楠」，存世不多，極為稀罕。本香材即使在不燃燒時，仍能從內而外散發香氣。香味帶有豐富層次感，細細品鑒，回韻甘醇奶香，藥癒性比沉香更加温潤。', '100', null, null, null, null, null), ('340', 'A Set of Kyara Agarwood Pieces', '伽羅塊', '15', '/public/pic_content/img/a4/LOTNO_187_1.jpg,/public/pic_content/img/a4/LOTNO_187_2.jpg', '187', '20,000 - 35,000', '', null, null, null, null, 'Please refer to Chinese website', '尺寸不一，共重31g', '100', null, null, null, null, null), ('341', 'A Piece of Vietnamese Agarwood', '越南老沉香擺件', '15', '/public/pic_content/img/a4/LOTNO_188_1.jpg,/public/pic_content/img/a4/LOTNO_188_2.jpg,/public/pic_content/img/a4/LOTNO_188_3.jpg', '188', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\nL18cm，D6.5cm，重234g<br />', '100', null, null, null, null, null), ('342', 'A Piece of Sinking Grade Vietnamese Agarwood', '沉水 越南沉香包頭', '15', '/public/pic_content/img/a4/LOTNO_189_1.jpg,/public/pic_content/img/a4/LOTNO_189_2.jpg,/public/pic_content/img/a4/LOTNO_189_3.jpg,/public/pic_content/img/a4/LOTNO_189_4.jpg,/public/pic_content/img/a4/LOTNO_189_5.jpg', '189', '30,000 - 50,000', '', null, null, null, null, 'Please refer to Chinese website', '產地：越南<br />\nH15cm，D16.5cm，重273g，沉水<br />\n<br />\n此香材產於越南，手感沉甸，入水能沉，是為難得。', '100', null, null, null, null, null), ('343', 'An Artemisia Glaze Censer with Base', '蒿麥釉香爐', '15', '/public/pic_content/img/a4/LOTNO_190_1.jpg,/public/pic_content/img/a4/LOTNO_190_2.jpg', '190', '18,000 - 30,000', '', null, null, null, null, 'Please refer to Chinese website', '共箱、共座<br />\nH6cm，D7.5cm<br />', '100', null, null, null, null, null), ('344', 'A Carved Sterling Silver Censer with Signature', '宗益造 浮雕花草圖銀製香爐', '15', '/public/pic_content/img/a4/LOTNO_191_1.jpg,/public/pic_content/img/a4/LOTNO_191_2.jpg,/public/pic_content/img/a4/LOTNO_191_3.jpg,/public/pic_content/img/a4/LOTNO_191_4.jpg', '191', '16,000 - 25,000', '', null, null, null, null, 'Please refer to Chinese website', '器底款：宗益<br />\n箱蓋內款：宗益造<br />\nH6cm，D9cm，重137g<br />\n<br />\n圓渾器身中央飾花卉紋樣，爐蓋上同飾花草紋樣，造就層次多變的景色，春暖花開的美景一一呈現於香爐之上。', '100', null, null, null, null, null), ('345', 'A Carved Iron Censer With Signature', '水野源六作 鐵地嵌金祥瑞香爐', '15', '/public/pic_content/img/a4/LOTNO_192_1.jpg,/public/pic_content/img/a4/LOTNO_192_2.jpg,/public/pic_content/img/a4/LOTNO_192_3.jpg', '192', '30,000 - 48,000', '', null, null, null, null, 'Please refer to Chinese website', '器底有款：源六<br />\n箱蓋款：水野源六<br />\n共箱<br />\nH9cm，D7.5cm<br />\n<br />\n釜師匠心獨運，在鐵質的荒地肌紋上以金絲鑲嵌「祥瑞」二字，工藝精湛，寓意吉祥。', '100', null, null, null, null, null), ('346', 'A Carved Bronze Vase With Bird Design', '銅製飛鳥花瓶', '15', '/public/pic_content/img/a4/LOTNO_193_1.jpg,/public/pic_content/img/a4/LOTNO_193_2.jpg', '193', '6,000 - 10,000', '', null, null, null, null, 'Please refer to Chinese website', 'H12cm，D7.5cm', '100', null, null, null, null, null), ('347', 'A Carved Bronze Vase With Fruits Design', '嵌金果子圖銅製花瓶', '15', '/public/pic_content/img/a4/LOTNO_194_1.jpg,/public/pic_content/img/a4/LOTNO_194_2.jpg', '194', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', 'H21cm，D10cm<br />\n<br />\n此銅瓶嵌果子圖，碩果豐收富含吉祥的美意。', '100', null, null, null, null, null), ('348', 'A Carved Wooden Tray', '銀絲如意蝙蝠木托盤', '15', '/public/pic_content/img/a4/LOTNO_195_1.jpg,/public/pic_content/img/a4/LOTNO_195_2.jpg', '195', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', 'L29cm，H19.5cm，H3.5cm', '100', null, null, null, null, null), ('349', 'A Carved Wooden Armrest', '木雕山水松柏圖手托', '15', '/public/pic_content/img/a4/LOTNO_196_1.jpg,/public/pic_content/img/a4/LOTNO_196_2.jpg,/public/pic_content/img/a4/LOTNO_196_3.jpg', '196', '1,000 - 2,000', '', null, null, null, null, 'Please refer to Chinese website', '器身刻：瑞雪江南<br />\n落款：小松<br />\nL34.5cm，D10cm<br />', '100', null, null, null, null, null), ('350', 'A Piece of Bronze Ornament in Elephant Shape', '銅製大象擺件', '15', '/public/pic_content/img/a4/LOTNO_197_1.jpg,/public/pic_content/img/a4/LOTNO_197_2.jpg', '197', '18,000 - 28,000', '', null, null, null, null, 'Please refer to Chinese website', 'H19cm，D47cm<br />\n<br />\n「象」與「祥」諧音，有吉祥喜慶的象征意義，加上象鼻吸水，水即財，有吸財、招財的寓意，故「象」乃招財吉祥之物。', '100', null, null, null, null, null), ('351', 'A Bronze Censer in Lion Shape', '古銅瑞獅戲球香爐', '15', '/public/pic_content/img/a4/LOTNO_198_1.jpg', '198', '3,000 - 5,000', '', null, null, null, null, 'Please refer to Chinese website', 'H21cm，D24cm', '100', null, null, null, null, null), ('352', 'A Sterling Silver Teapot with Heart Sutra Inscription by Masaharu Nagoshi', '名越昌晴 般若心經純銀茶壺', '16', '/public/pic_content/img/a5/LOTNO_101_1.jpg,/public/pic_content/img/a5/LOTNO_101_2.jpg,/public/pic_content/img/a5/LOTNO_101_3.jpg', '101', '68,000 - 118,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：《般若波羅蜜多心經》<br />\r\n箱蓋款：名越彌五郎及昌晴鈴印<br />\r\nH14.5cm，D14cm，重313g', '100', null, null, null, null, null), ('353', 'A Carved  Silver Teapot  with Imperial Seal', '皇室三葉葵家運昌隆銀壺', '16', '/public/pic_content/img/a5/LOTNO_102_1.jpg,/public/pic_content/img/a5/LOTNO_102_2.jpg', '102', '12,000 - 20,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', 'H6cm，D10.5cm，重132.5g', '100', null, null, null, null, null), ('354', 'A Carved Sterling Silver Kyusu with a Phoenix Spout', '純銀 鳳口龍紋小急須', '16', '/public/pic_content/img/a5/LOTNO_103_1.jpg,/public/pic_content/img/a5/LOTNO_103_2.jpg,/public/pic_content/img/a5/LOTNO_103_3.jpg,/public/pic_content/img/a5/LOTNO_103_4.jpg', '103', '18,000 - 28,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：純銀<br />\nH8.5cm，D11cm，重190g', '100', null, null, null, null, null), ('355', 'A Hirahojyu-Gata Iron Tetsubin by Masamitsu Kikuchi', '菊地政光作寶珠形鐵壺', '16', '/public/pic_content/img/a5/LOTNO_104_1.jpg,/public/pic_content/img/a5/LOTNO_104_2.jpg,/public/pic_content/img/a5/LOTNO_104_3.jpg,/public/pic_content/img/a5/LOTNO_104_4.jpg,/public/pic_content/img/a5/LOTNO_104_5.jpg', '104', '5,000 - 8,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器款：政光<br />\n箱蓋款：政光<br />\nH22.5cm，W17cm', '100', null, null, null, null, null), ('356', 'An Iron Tetsubin with a Silver Knob', '銀環鈕摘鐵壺', '16', '/public/pic_content/img/a5/LOTNO_105_1.jpg,/public/pic_content/img/a5/LOTNO_105_2.jpg,/public/pic_content/img/a5/LOTNO_105_3.jpg', '105', '30,000 - 50,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：般若造之<br />\nH15cm，D14.5cm', '100', null, null, null, null, null), ('357', 'A Carved Gold-inlaid Iron Tetsubin', '鎏金山水圖鐵壺', '16', '/public/pic_content/img/a5/LOTNO_106_1.jpg,/public/pic_content/img/a5/LOTNO_106_2.jpg,/public/pic_content/img/a5/LOTNO_106_3.jpg', '106', '18,000 - 28,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '共箱<br />\nH18cm，D17cm', '100', null, null, null, null, null), ('358', 'An Iron Tstsubin by Ryubundo', '龍文堂造 荒地肌紋鐵壺', '16', '/public/pic_content/img/a5/LOTNO_107_2.jpg,/public/pic_content/img/a5/LOTNO_107_3.jpg,/public/pic_content/img/a5/LOTNO_107_4.jpg', '107', '40,000 - 60,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：龍文堂造<br />\n蓋款：龍文堂造。<br />\nH22.5cm，D21cm', '100', null, null, null, null, null), ('359', 'A Carved Iron Tetsubin with an Iron Plated Silver Handle by Anami of Kinjyudo', '金壽堂 雨宮造 嵌銀點鐵包銀把茶壺', '16', '/public/pic_content/img/a5/LOTNO_108_1.jpg,/public/pic_content/img/a5/LOTNO_108_2.jpg,/public/pic_content/img/a5/LOTNO_108_3.jpg,/public/pic_content/img/a5/LOTNO_108_4.jpg,/public/pic_content/img/a5/LOTNO_108_5.jpg,/public/pic_content/img/a5/LOTNO_108_6.jpg', '108', '28,000 - 40,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：金壽堂造<br />\n器款：「雨宮」小圓印<br />\n蓋款：「金壽堂」、「雨宮造」鈐印。<br />\nH21cm，W16.5cm', '100', null, null, null, null, null), ('360', 'A Carved Gold-and-Silver-Inlaid Iron Tetsubin by Kougyokudo', '光玉堂造 嵌金銀家屋彩蝶紋鐵壺', '16', '/public/pic_content/img/a5/LOTNO_109_1.jpg,/public/pic_content/img/a5/LOTNO_109_2.jpg,/public/pic_content/img/a5/LOTNO_109_3.jpg,/public/pic_content/img/a5/LOTNO_109_4.jpg', '109', '68,000 - 100,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：光玉堂造<br />\nH22cm，D18cm<br />\n此壺圓潤細膩，兩面開光以金銀嵌出絕妙美景；一面飾彩蝶翩翩，家屋佇立於林間寧靜雅緻；另一面見水波徐徐，小舟數只飄揚其中，愜意瀟灑。', '100', null, null, null, null, null);
INSERT INTO `curio_pic_content` VALUES ('361', 'A Carved Gold-inlaid Iron Tetsubin by Kinjyudo', '金壽堂造 嵌金銀瓜瓞綿綿鐵壺', '16', '/public/pic_content/img/a5/LOTNO_110_1.jpg,/public/pic_content/img/a5/LOTNO_110_2.jpg,/public/pic_content/img/a5/LOTNO_110_3.jpg', '110', '30,000 - 50,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：金壽堂造<br />\n器款(橫)：金壽堂造<br />\nH21cm，D19cm', '100', null, null, null, null, null), ('362', 'A Carved Iron Tetsubin By Ryubundo', '龍文堂造 嵌金山水家屋圖鐵壺', '16', '/public/pic_content/img/a5/LOTNO_111_1.jpg,/public/pic_content/img/a5/LOTNO_111_2.jpg,/public/pic_content/img/a5/LOTNO_111_3.jpg,/public/pic_content/img/a5/LOTNO_111_4.jpg', '111', '40,000 - 68,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：龍文堂造<br />\nH24.5cm，D15cm', '100', null, null, null, null, null), ('363', 'A Carved Archaic Tin Tea Caddy, Qing Dynasty', '清 老錫鎏金猴趣茶葉罐', '16', '/public/pic_content/img/a5/LOTNO_112_1.jpg,/public/pic_content/img/a5/LOTNO_112_2.jpg,/public/pic_content/img/a5/LOTNO_112_3.jpg,/public/pic_content/img/a5/LOTNO_112_4.jpg', '112', '12,000 - 18,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：光大市金玉天順制<br />\n共箱<br />\nH6.5cm，D6.5cm', '100', null, null, null, null, null), ('364', 'A Carved Tin Tea Caddy with Shen Cunzhuo Mark', '沈存周 古錫漢詩雕茶罐', '16', '/public/pic_content/img/a5/LOTNO_113_1.jpg,/public/pic_content/img/a5/LOTNO_113_2.jpg,/public/pic_content/img/a5/LOTNO_113_3.jpg,/public/pic_content/img/a5/LOTNO_113_4.jpg,/public/pic_content/img/a5/LOTNO_113_5.jpg', '113', '22,000 - 38,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：沈存周<br />\n底款：落蒼風靜煎茶香<br />\n共箱<br />\nH8cm，D6cm', '100', null, null, null, null, null), ('365', 'A Pair of Carved Tin Tea Caddies', '慶鐘堂造 花草紋三耳錫茶罐一對', '16', '/public/pic_content/img/a5/LOTNO_114_1.jpg,/public/pic_content/img/a5/LOTNO_114_2.jpg,/public/pic_content/img/a5/LOTNO_114_3.jpg', '114', '12,000 - 18,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：慶鐘堂造<br />\n共箱<br />\nH11cm，D7cm', '100', null, null, null, null, null), ('366', 'A Carved Tin Tea Caddy', '蓉祥堂造 壽紋錫茶罐', '16', '/public/pic_content/img/a5/LOTNO_115_1.jpg,/public/pic_content/img/a5/LOTNO_115_2.jpg', '115', '20,000 - 38,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：壽<br />\n底款：蓉祥堂造<br />\n共箱<br />\nH11cm，D6cm<br />\n直口錫茶罐造形內斂樸素，錫皮色澤古雅。蓋上淺刻壽字紋，整器簡約大氣。', '100', null, null, null, null, null), ('367', 'A Carved Copper Kensui by Honma Takusa', '琢齋造 斑紫銅山水圖建水', '16', '/public/pic_content/img/a5/LOTNO_116_1.jpg,/public/pic_content/img/a5/LOTNO_116_2.jpg,/public/pic_content/img/a5/LOTNO_116_3.jpg,/public/pic_content/img/a5/LOTNO_116_4.jpg', '116', '30,000 - 50,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：琢齋造、「琢齋」方章<br />\n共箱<br />\nH11.5cm，D15.5cm', '100', null, null, null, null, null), ('368', 'A Pair of Yixing Teapots, Late Qing Dynasty', '晚清 紫泥舖沙古春壺一對', '16', '/public/pic_content/img/a5/LOTNO_117_1.jpg,/public/pic_content/img/a5/LOTNO_117_2.jpg', '117', '20,000 - 30,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '共箱<br />\nH7.5cm，D10.5cm<br />\n古春壺又稱甕式壺；對壺造形別緻，舖沙效果特別，壺咀及整體造形俱佳。', '100', null, null, null, null, null), ('369', 'A Carved Yixing Teapot with Signature, Late Qing Dynasty', '清末 孟臣款詩文青竹朱泥扁壺', '16', '/public/pic_content/img/a5/LOTNO_118_1.jpg,/public/pic_content/img/a5/LOTNO_118_2.jpg', '118', '8,000 - 15,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '壺身銘刻：月字中無色<br />\n印：為孟臣製<br />\n蓋款：水平<br />\nH6 cm，D13 cm', '100', null, null, null, null, null), ('370', 'A Carved Yixing Teapot with Signature', '王石耕 紫泥玉壁夔龍蝶舞壺', '16', '/public/pic_content/img/a5/LOTNO_119_1.jpg,/public/pic_content/img/a5/LOTNO_119_2.jpg,/public/pic_content/img/a5/LOTNO_119_3.jpg,/public/pic_content/img/a5/LOTNO_119_4.jpg', '119', '12,000 - 18,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：王、石耕<br />\n底款：王石耕<br />\nH5.5cm，D16cm<br />\n王石耕是紫砂大師王寅春長子，系出名門，技藝獨當一面；此壺是其早期作品，甚為難得。', '100', null, null, null, null, null), ('371', 'A Pair of Carved Yixing Teapots with Signature, Qing Dynasty', '清 紫泥具輪珠茶壺一對', '16', '/public/pic_content/img/a5/LOTNO_120_1.jpg,/public/pic_content/img/a5/LOTNO_120_2.jpg,/public/pic_content/img/a5/LOTNO_120_3.jpg', '120', '40,000 - 68,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '共箱<br />\r\nH6cm，D10cm<br />\r\n此對紫泥小壺造形古雅，小嘴粗短、環把、圈足，是具輪珠的典型造形。此器泥料純正，為紫泥鋪砂，點點段砂隨性灑落，均勻有致，恰到好處。壺內乾淨細緻，單孔出水，整器製作不事雕琢，貴在醇雅，是壺中上品。', '100', null, null, null, null, null), ('372', 'A Carved Yixing Teapot with Signature', '邵景南款 詩文紫泥茶壺', '16', '/public/pic_content/img/a5/LOTNO_121_1.jpg,/public/pic_content/img/a5/LOTNO_121_2.jpg,/public/pic_content/img/a5/LOTNO_121_3.jpg,/public/pic_content/img/a5/LOTNO_121_4.jpg,/public/pic_content/img/a5/LOTNO_121_5.jpg,/public/pic_content/img/a5/LOTNO_121_6.jpg,/public/pic_content/img/a5/LOTNO_121_7.jpg,/public/pic_content/img/a5/LOTNO_121_8.jpg', '121', '15,000 - 28,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：霜凋兩岸柳，水浸一天星；留佩<br />\n蓋款：景南<br />\n底款：邵景南記<br />\n共箱<br />\nH9.5cm，D14.5cm<br />\n壺人刻南宋詞人陸游之《城西晚眺》：「霜凋兩岸柳，水浸一天星」，落款「留佩」。<br />\n蓋內印款「景南」，壺身雙印款，底款「邵景南記」。', '100', null, null, null, null, null), ('373', 'A Sterling Silver Teapot with Heart Sutra Inscription', '般若心經銀壺', '16', '/public/pic_content/img/a5/LOTNO_122_1.jpg,/public/pic_content/img/a5/LOTNO_122_2.jpg,/public/pic_content/img/a5/LOTNO_122_3.jpg,/public/pic_content/img/a5/LOTNO_122_4.jpg', '122', '68,000 - 100,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：《般若波羅蜜多心經》<br />\r\n箱蓋款：寶珠般若心經銀瓶及釜師花押<br />\r\nH16cm，D13cm，重254g', '100', null, null, null, null, null), ('374', 'A Sterling Silver Teapot with Arare Embossment by Kounan', '光南造 純銀霧霰紋茶壺', '16', '/public/pic_content/img/a5/LOTNO_123_1.jpg,/public/pic_content/img/a5/LOTNO_123_2.jpg,/public/pic_content/img/a5/LOTNO_123_3.jpg,/public/pic_content/img/a5/LOTNO_123_4.jpg', '123', '38,000 - 60,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：純銀<br />\n蓋款：光南<br />\nH21.5cm，D16.5cm，重161g', '100', null, null, null, null, null), ('375', 'A Carved Iron Tetsubin with Gold and Silver Inlay by Seiryudo', '青龍堂造 金銀嵌仙人飛龍圖鐵壺', '16', '/public/pic_content/img/a5/LOTNO_124_1.jpg,/public/pic_content/img/a5/LOTNO_124_2.jpg,/public/pic_content/img/a5/LOTNO_124_3.jpg,/public/pic_content/img/a5/LOTNO_124_4.jpg,/public/pic_content/img/a5/LOTNO_124_5.jpg', '124', '50,000 - 80,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：龍文堂造<br />\n器身款：青龍堂造<br />\n共箱<br />\nH17cm，D16.5cm', '100', null, null, null, null, null), ('376', 'A Carved Gold-and-Silver-Inlaid Iron Tetsubin', '金銀象嵌花蝶山水圖鐵壺', '16', '/public/pic_content/img/a5/LOTNO_125_1.jpg,/public/pic_content/img/a5/LOTNO_125_2.jpg', '125', '20,000 - 30,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', 'H18.5cm，W17cm', '100', null, null, null, null, null), ('377', 'A Gold Teapot with Arare Embossment', '純金 鏤空花瓣鈕霧霰紋壺', '16', '/public/pic_content/img/a5/LOTNO_126_1.jpg,/public/pic_content/img/a5/LOTNO_126_2.jpg,/public/pic_content/img/a5/LOTNO_126_3.jpg,/public/pic_content/img/a5/LOTNO_126_4.jpg', '126', '180,000 - 300,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：純金<br />\r\n共箱<br />\r\nH15.5cm，D15cm，重589g<br />\r\n此霧霰形金壺，壺體飽滿圓潤，金色璀璨，霰粒佈局均勻，三彎壺流線條優美，實為臻品。', '100', null, null, null, null, null), ('378', 'A Carved Silver-Inlaid Iron Tetsubin with an Iron Knob By Seikodo', '精金堂造 銀嵌仙鶴銅蓋鐵壺', '16', '/public/pic_content/img/a5/LOTNO_127_2.jpg,/public/pic_content/img/a5/LOTNO_127_3.jpg,/public/pic_content/img/a5/LOTNO_127_4.jpg', '127', '30,000 - 50,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：精金堂造<br />\n箱籤：精金堂<br />\nH22cm，D16.5cm', '100', null, null, null, null, null), ('379', 'A Carved Jujube-shaped Silver Tea Caddy with a Wooden Handle', '荷花詩文棗式檀木把銀壺', '16', '/public/pic_content/img/a5/LOTNO_128_1.jpg,/public/pic_content/img/a5/LOTNO_128_2.jpg,/public/pic_content/img/a5/LOTNO_128_3.jpg,/public/pic_content/img/a5/LOTNO_128_4.jpg', '128', '50,000 - 80,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：清風曉月<br />\n箱蓋款：棗式銀罐<br />\nH19.5cm，D16cm，重363g', '100', null, null, null, null, null), ('380', 'A Carved Double-signed Gold-Inlaid Iron Tetsubin by Kinjyudo', '金壽堂造 雨宮宗兵衛雙款 嵌金仙鶴圖鐵壺', '16', '/public/pic_content/img/a5/LOTNO_129_1.jpg,/public/pic_content/img/a5/LOTNO_129_2.jpg,/public/pic_content/img/a5/LOTNO_129_3.jpg,/public/pic_content/img/a5/LOTNO_129_4.jpg,/public/pic_content/img/a5/LOTNO_129_5.jpg,/public/pic_content/img/a5/LOTNO_129_6.jpg,/public/pic_content/img/a5/LOTNO_129_7.jpg', '129', '150,000 - 280,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '蓋款：金壽堂<br />\r\n器款：雨宮<br />\r\n箱款：金壽堂<br />\r\nH20.5cm，D17.5cm<br />\r\n壺身兩面開窗，一面嵌仙鶴於蘆葦叢上振翅高飛巍峨俯瞰；壺身另一面有仙鶴一雙佇立蘆葦叢中，靈動生香。<br />\r\n此壺罕有地備有雙款畫押，不但蓋內刻有「金壽堂」款，壺流下亦帶雨宮宗小圓款。共箱，箱面題「長閑形鐵瓶」，箱蓋內款「金壽堂」。', '100', null, null, null, null, null), ('381', 'A Gold Teapot with Arare Embossment by Ishiguro Konan', '石黑光南 純金霧霰紋茶壺', '16', '/public/pic_content/img/a5/LOTNO_130_1.jpg,/public/pic_content/img/a5/LOTNO_130_2.jpg,/public/pic_content/img/a5/LOTNO_130_3.jpg,/public/pic_content/img/a5/LOTNO_130_4.jpg,/public/pic_content/img/a5/LOTNO_130_5.jpg', '130', '280,000 - 400,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '底款：純金<br />\r\n箱款：光南<br />\r\nH16.5cm，D18cm，重755g<br />\r\n著名金工師石黑光南除傳承了江戶時代金銀器製作的技法外，更獨創玉霰技藝，青出於藍。整器滿佈均勻而漸變的霧霰紋，工法嚴謹，繁複奇巧；寶珠形摘線條優美，需要極大耐心與工藝方能成就，把黃金的高雅氣質發揮得淋漓盡致。', '100', null, null, null, null, null), ('382', 'A Gold Chashaku', '純金茶杓', '16', '/public/pic_content/img/a5/LOTNO_131_1.jpg,/public/pic_content/img/a5/LOTNO_131_2.jpg,/public/pic_content/img/a5/LOTNO_131_3.jpg', '131', '28,000 - 40,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器款：純金<br />\n箱款；雕鉼純金製 茶杓<br />\nL17.5cm，重52g', '100', null, null, null, null, null), ('383', 'A Pair of Carved Silver Kyusus with Signature', '延壽款 銀製漢詩竹梅紋雕急須一對', '16', '/public/pic_content/img/a5/LOTNO_132_1.jpg,/public/pic_content/img/a5/LOTNO_132_2.jpg,/public/pic_content/img/a5/LOTNO_132_3.jpg,/public/pic_content/img/a5/LOTNO_132_4.jpg', '132', '180,000 - 280,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器身銘刻：老山<br />\n底款：延壽<br />\n共箱<br />\nH7cm，D11cm，重121g<br />\n此對銀壺配紫檀手把，壺身圓潤如珠，袖珍討喜。一隻淺刻竹梅紋樣，隨風搖曳；另一隻配虔刻詩文，蒼勁有力。', '100', null, null, null, null, null), ('384', 'An Iron Plated Sterling Silver Teapot By Somi Yamada', '宗美造 鐵包銀寶珠形茶壺', '16', '/public/pic_content/img/a5/LOTNO_133_1.jpg,/public/pic_content/img/a5/LOTNO_133_2.jpg,/public/pic_content/img/a5/LOTNO_133_3.jpg,/public/pic_content/img/a5/LOTNO_133_4.jpg,/public/pic_content/img/a5/LOTNO_133_5.jpg', '133', '60,000 - 100,000', '', null, null, null, null, 'For detailed description, please refer to the Chinese site', '器款：宗美<br />\nH20cm，D16.5cm，重668.5g<br />\n此壺為日本金工名家山田宗美所製，作者繼承家族的傳統打出技藝後，發展出「一塊造」的技術。由於鐵的延展性在所有金屬中是最低，其難度幾為金工之絕。加上此壺結合鐵包銀的技術：先將銀器成型，再用「鐵打出」錘出與銀器大小吻合的鐵片，最後進行緊密接合，相當耗力費時，從工藝角度上來說就極為難得。', '100', null, null, null, null, null), ('385', 'A Sterling Silver Teapot with Arare Embossment', '純銀 霧霰紋茶壺', '16', '/public/pic_content/img/a5/LOTNO_134_1.jpg,/public/pic_content/img/a5/LOTNO_134_2.jpg,/public/pic_content/img/a5/LOTNO_134_3.jpg,/public/pic_content/img/a5/L