INSERT INTO `curio_static` VALUES ('1', '今屆拍賣', 'UPCOMING AUCTION', 'http://abc.shegurz.com/public/static_page/img/15288629218uz6ejsm.jpg', '<h1 style=\"font-family: 微軟正黑體, 新細明體, arial, helvetica, sans-serif; color: rgb(0, 0, 0);\"><p class=\"MsoNormal\" style=\"font-family: Arial, Verdana, sans-serif; font-size: 12px;\"><span style=\"font-weight: 700;\"><span style=\"color: rgb(142, 26, 29);\"><span style=\"font-family: Arial;\"><span style=\"font-size: xx-large;\">L&amp;H</span></span></span></span><span style=\"font-weight: 700;\"><span style=\"font-family: Arial;\"><span style=\"font-size: xx-large;\">&nbsp;2018 Spring Auction</span></span></span><span style=\"font-family: Arial;\"><span style=\"font-size: xx-large;\"><br></span></span></p><p style=\"font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><font face=\"Arial, Verdana, sans-serif\" size=\"3\">L &amp; H Auction Spring auction has another successful sale at the Grand Hyatt Hong Kong on the 27 May 2018. In celebration of L &amp; H Auction’s 5th anniversary, the Spring 2018 sales encompass a well curated mix of an exclusive collection of rare offerings from private collections including the categories of Teawares, Vintage Pu’er, Chinese Delicacies and aged Maotai of excellent condition and impeccable provenance. Please click here for the result information.&nbsp;</font></p><p style=\"font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><span style=\"font-family: Arial, Verdana, sans-serif; font-size: medium;\">L&amp;H will provide more unique and rare items in the future. Please stay tune to our website, Facebook and Wechat for any update in the future auction.</span><br></p><p style=\"font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><font face=\"Arial, Verdana, sans-serif\" size=\"3\"><br></font></p></h1><h3